import { useState, useCallback, useEffect, useRef } from 'react';
import { useVoiceContext } from '@/context/VoiceContext';

// Type definitions for Web Speech API
interface SpeechRecognitionEvent extends Event {
  results: SpeechRecognitionResultList;
  resultIndex: number;
}

interface SpeechRecognitionErrorEvent extends Event {
  error: string;
  message?: string;
}

interface SpeechRecognitionResultList {
  length: number;
  item(index: number): SpeechRecognitionResult;
  [index: number]: SpeechRecognitionResult;
}

interface SpeechRecognitionResult {
  isFinal: boolean;
  length: number;
  item(index: number): SpeechRecognitionAlternative;
  [index: number]: SpeechRecognitionAlternative;
}

interface SpeechRecognitionAlternative {
  transcript: string;
  confidence: number;
}

interface CustomSpeechRecognition extends EventTarget {
  continuous: boolean;
  interimResults: boolean;
  lang: string;
  start(): void;
  stop(): void;
  abort(): void;
  onstart: ((event: Event) => void) | null;
  onend: ((event: Event) => void) | null;
  onerror: ((event: SpeechRecognitionErrorEvent) => void) | null;
  onresult: ((event: SpeechRecognitionEvent) => void) | null;
}

// Use any for the constructor to avoid type conflicts with built-in declarations
// eslint-disable-next-line @typescript-eslint/no-explicit-any
type SpeechRecognitionConstructor = any;

const VOICE_IDLE_TIMEOUT = 10000; // 10s idle timeout for mic monitoring

interface UseVoiceInterfaceProps {
  onStateChange?: (state: 'listening' | 'speaking' | 'idle') => void;
}

export const useVoiceInterface = ({ onStateChange }: UseVoiceInterfaceProps = {}) => {
  const [isListening, setIsListening] = useState(false);
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [transcript, setTranscript] = useState('');
  const [interimTranscript, setInterimTranscript] = useState('');
  const [error, setError] = useState<string | null>(null);
  const [micLevel, setMicLevel] = useState(0);
  
  // eslint-disable-next-line @typescript-eslint/no-explicit-any
  const recognitionRef = useRef<any>(null);
  const audioContextRef = useRef<AudioContext | null>(null);
  const analyserRef = useRef<AnalyserNode | null>(null);
  const gainNodeRef = useRef<GainNode | null>(null);
  const streamRef = useRef<MediaStream | null>(null);
  const animationFrameRef = useRef<number | null>(null);

  useEffect(() => {
    // Initialize Speech Recognition
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (SpeechRecognition) {
      const recognition = new SpeechRecognition();
      recognition.continuous = true;
      recognition.interimResults = true;
      recognition.lang = 'en-US';

      recognition.onstart = () => {
        setIsListening(true);
        onStateChange?.('listening');
        setError(null);
      };

      recognition.onend = () => {
        setIsListening(false);
        onStateChange?.('idle');
      };

      recognition.onerror = (event: SpeechRecognitionErrorEvent) => {
        console.error('Speech recognition error', event.error);
        setError(event.error);
        setIsListening(false);
        onStateChange?.('idle');
      };

      recognition.onresult = (event: SpeechRecognitionEvent) => {
        let final = '';
        let interim = '';

        for (let i = 0; i < event.results.length; ++i) {
          if (event.results[i].isFinal) {
            final += event.results[i][0].transcript;
          } else {
            interim += event.results[i][0].transcript;
          }
        }
        
        setTranscript(final);
        setInterimTranscript(interim);
      };

      recognitionRef.current = recognition;
    } else {
      setError('Speech recognition not supported in this browser.');
    }

    return () => {
      if (recognitionRef.current) {
        recognitionRef.current.abort();
      }
    };
  }, [onStateChange]);

  const { speakerVolume, isSpeakerMuted, micGain, isMicMuted } = useVoiceContext();

  const startAudioMonitoring = useCallback(async () => {
    try {
      if (isMicMuted) return;

      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      streamRef.current = stream;

      const audioContext = new AudioContext();
      audioContextRef.current = audioContext;

      const source = audioContext.createMediaStreamSource(stream);
      const gainNode = audioContext.createGain();
      gainNode.gain.value = micGain; // Apply global gain
      gainNodeRef.current = gainNode;

      const analyser = audioContext.createAnalyser();
      analyser.fftSize = 256;
      analyserRef.current = analyser;

      source.connect(gainNode);
      gainNode.connect(analyser);

      const bufferLength = analyser.frequencyBinCount;
      const dataArray = new Uint8Array(bufferLength);

      const updateLevel = () => {
        if (!analyserRef.current) return;
        analyserRef.current.getByteFrequencyData(dataArray);
        
        let sum = 0;
        for (let i = 0; i < bufferLength; i++) {
          sum += dataArray[i];
        }
        const average = sum / bufferLength;
        setMicLevel(average / 128); // Normalize to 0-1 (roughly)

        animationFrameRef.current = requestAnimationFrame(updateLevel);
      };

      updateLevel();
    } catch (err) {
      console.error('Error accessing microphone:', err);
      setError('Could not access microphone.');
    }
  }, [micGain, isMicMuted]);

  const stopAudioMonitoring = useCallback(() => {
    if (animationFrameRef.current) {
      cancelAnimationFrame(animationFrameRef.current);
    }
    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => track.stop());
    }
    if (audioContextRef.current) {
      audioContextRef.current.close();
    }
    setMicLevel(0);
  }, []);

  useEffect(() => {
    if (isListening && !isMicMuted) {
      startAudioMonitoring();
    } else {
      stopAudioMonitoring();
    }
    return () => stopAudioMonitoring();
  }, [isListening, isMicMuted, startAudioMonitoring, stopAudioMonitoring]);

  // Update gain node if value changes while listening
  useEffect(() => {
    if (gainNodeRef.current) {
      gainNodeRef.current.gain.value = micGain;
    }
  }, [micGain]);

  const startListening = useCallback(() => {
    if (isMicMuted) {
      console.log('[useVoiceInterface] Mic is muted, ignoring start request');
      return;
    }
    if (recognitionRef.current && !isListening) {
      setTranscript('');
      setInterimTranscript('');
      try {
        recognitionRef.current.start();
      } catch (e) {
        console.error("Failed to start speech recognition:", e);
      }
    }
  }, [isListening, isMicMuted]);

  const stopListening = useCallback(() => {
    if (recognitionRef.current && isListening) {
      recognitionRef.current.stop();
    }
  }, [isListening]);

  const toggleListening = useCallback(() => {
    if (isListening) {
      stopListening();
    } else {
      startListening();
    }
  }, [isListening, startListening, stopListening]);

  const speak = useCallback((text: string) => {
    if ('speechSynthesis' in window) {
      if (isSpeakerMuted) {
        console.log('[useVoiceInterface] Speaker is muted, skipping speech');
        return;
      }

      window.speechSynthesis.cancel();
      const utterance = new SpeechSynthesisUtterance(text);
      utterance.volume = speakerVolume; // Apply global volume

      utterance.onstart = () => {
        setIsSpeaking(true);
        onStateChange?.('speaking');
      };
      utterance.onend = () => {
        setIsSpeaking(false);
        onStateChange?.('idle');
      };
      utterance.onerror = (e) => {
         console.error("Speech synthesis error", e);
         setIsSpeaking(false);
      };
      
      const voices = window.speechSynthesis.getVoices();
      const preferredVoice = voices.find(v => v.name.includes('Google US English') || v.name.includes('Samantha'));
      if (preferredVoice) utterance.voice = preferredVoice;

      window.speechSynthesis.speak(utterance);
    } else {
      setError('Speech synthesis not supported.');
    }
  }, [onStateChange, isSpeakerMuted, speakerVolume]);

  const stopSpeaking = useCallback(() => {
      if ('speechSynthesis' in window) {
          window.speechSynthesis.cancel();
          setIsSpeaking(false);
      }
  }, []);

  return {
    isListening,
    isSpeaking,
    transcript: transcript + interimTranscript,
    error,
    micLevel,
    startListening,
    stopListening,
    toggleListening,
    speak,
    stopSpeaking
  };
};

