  );

  return <VoiceStateContext.Provider value={value}>{children}</VoiceStateContext.Provider>;
};

export const useVoiceState = (): VoiceStateContextValue => {
  const context = useContext(VoiceStateContext);
  if (!context) {
    throw new Error('useVoiceState must be used within a VoiceStateProvider');
