import React, { createContext, useCallback, useContext, useEffect, useMemo, useState } from 'react';
import type { ReactNode } from 'react';
import type { VoiceOrbState } from '../components/voice/VoiceOrb';
import { voiceStartListening, voiceStop, voiceSynthesize, type VoiceSynthesisResponse } from '../bridges/VoiceBridge';
import { listen } from '@tauri-apps/api/event';
import type { UnlistenFn } from '@tauri-apps/api/event';

export type BiometricReading = {
  type?: string;
  timestamp: number;
  heartRate: number;
  hrv?: number | null;
  stressEstimate?: number | null;
  source: string;
  audioLevel?: number;
};

type RawBiometricPayload = {
  type?: string;
  timestamp: number;
  heart_rate: number;
  hrv?: number | null;
  stress_estimate?: number | null;
  source: string;
  audio_level?: number;
};

interface VoiceStateContextValue {
  voiceState: VoiceOrbState;
  setVoiceState: (state: VoiceOrbState) => void;
  isOrbVisible: boolean;
  setOrbVisible: (visible: boolean) => void;
  startListening: () => Promise<void>;
  stopListening: () => Promise<void>;
  synthesize: (text: string, voiceId?: string) => Promise<VoiceSynthesisResponse>;
  error: string | null;
  audioLevel: number;
  biometricReading: BiometricReading | null;
}

const VoiceStateContext = createContext<VoiceStateContextValue | undefined>(undefined);

const normalizeBiometricReading = (raw: RawBiometricPayload): BiometricReading => ({
  type: raw.type,
  timestamp: raw.timestamp,
  heartRate: raw.heart_rate,
  hrv: raw.hrv ?? null,
  stressEstimate: raw.stress_estimate ?? null,
  source: raw.source,
  audioLevel: raw.audio_level,
});

export const VoiceStateProvider: React.FC<{ children: ReactNode; userId: string }> = ({ children, userId }) => {
  const [voiceState, setVoiceState] = useState<VoiceOrbState>('idle');
  const [isOrbVisible, setOrbVisible] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [audioLevel, setAudioLevel] = useState(0);
  const [biometricReading, setBiometricReading] = useState<BiometricReading | null>(null);

  const startListening = useCallback(async () => {
    setError(null);
    setVoiceState('listening');
    try {
      await voiceStartListening({ user_id: userId });
    } catch (err) {
      console.error('[VoiceStateContext] Failed to start listening:', err);
      setError(err instanceof Error ? err.message : 'Failed to start listening');
      setVoiceState('error');
    }
  }, [userId]);

  const stopListening = useCallback(async () => {
    setError(null);
    setVoiceState('processing');
    try {
      await voiceStop({ user_id: userId });
      setVoiceState('idle');
    } catch (err) {
      console.error('[VoiceStateContext] Failed to stop listening:', err);
      setError(err instanceof Error ? err.message : 'Failed to stop listening');
      setVoiceState('error');
    }
  }, [userId]);

  const synthesize = useCallback(
    async (text: string, voiceId?: string) => {
      setError(null);
      setVoiceState('speaking');
      try {
        const response = await voiceSynthesize({ user_id: userId, text, voice_id: voiceId });
        if (!response.success) {
          setError(response.error ?? 'Voice synthesis failed');
          setVoiceState('error');
        } else {
          setVoiceState('idle');
        }
        return response;
      } catch (err) {
        console.error('[VoiceStateContext] Failed to synthesize voice:', err);
        setError(err instanceof Error ? err.message : 'Failed to synthesize voice');
        setVoiceState('error');
        throw err;
      }
    },
    [userId]
  );

  useEffect(() => {
    let unlistenAudio: UnlistenFn | null = null;
    let unlistenReady: UnlistenFn | null = null;
    let unlistenTranscript: UnlistenFn | null = null;
    let unlistenBiometric: UnlistenFn | null = null;

    const register = async () => {
      try {
        unlistenAudio = await listen('voice://audio', (event) => {
          const payload = event.payload as { audio_level?: number; volume?: number };
          const rawLevel = payload.audio_level ?? payload.volume ?? 0;
          setAudioLevel(Math.max(0, Math.min(1, rawLevel)));
        });
      } catch (err) {
        console.warn('[VoiceStateContext] voice://audio listener failed', err);
      }

      try {
        unlistenReady = await listen('voice://ready', () => {
          setVoiceState((prev) => (prev === 'idle' || prev === 'processing' ? 'listening' : prev));
        });
      } catch (err) {
        console.warn('[VoiceStateContext] voice://ready listener failed', err);
      }

      try {
        unlistenTranscript = await listen('voice://transcript', () => {
          setVoiceState('processing');
        });
      } catch (err) {
        console.warn('[VoiceStateContext] voice://transcript listener failed', err);
      }

      try {
        unlistenBiometric = await listen('alice://biometric-update', (event) => {
          const payload = event.payload as RawBiometricPayload;
          setBiometricReading(normalizeBiometricReading(payload));
        });
      } catch (err) {
        console.warn('[VoiceStateContext] alice://biometric-update listener failed', err);
      }
    };

    register();

    return () => {
      unlistenAudio?.();
      unlistenReady?.();
      unlistenTranscript?.();
      unlistenBiometric?.();
    };
  }, []);

  const value = useMemo(
    () => ({
      voiceState,
      setVoiceState,
      isOrbVisible,
      setOrbVisible,
      startListening,
      stopListening,
      synthesize,
      error,
      audioLevel,
      biometricReading,
    }),
    [voiceState, isOrbVisible, startListening, stopListening, synthesize, error, audioLevel, biometricReading]
  );

  return <VoiceStateContext.Provider value={value}>{children}</VoiceStateContext.Provider>;
};

export const useVoiceState = (): VoiceStateContextValue => {
  const context = useContext(VoiceStateContext);
  if (!context) {
    throw new Error('useVoiceState must be used within a VoiceStateProvider');
  }
  return context;
};
