import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { motion, AnimatePresence, useReducedMotion } from 'framer-motion';
import { Mic, Loader2 } from '../ui/HearthlinkIcons';
import { cn } from '../../lib/utils';
// import { useVoiceBridge } from '../../hooks/useVoiceBridge'; // Future hook

interface VoiceOrbProps {
  onActivate?: () => void;
  onDeactivate?: () => void;
  visible?: boolean; // Controlled by parent (e.g. window minimizing)
  initialState?: VoiceOrbState;
  state?: VoiceOrbState;
  onStateChange?: (state: VoiceOrbState) => void;
  // Privacy controls
  privacyMode?: boolean; // When true, disables voice recording
  showPrivacyIndicator?: boolean; // Show local-only processing indicator
  onPrivacyToggle?: (enabled: boolean) => void;
}

export type VoiceOrbState = 'idle' | 'listening' | 'processing' | 'speaking' | 'error';

/**
 * Voice Orb - Minimized Interaction Surface
 * 
 * A reduced-UI component for always-on voice interaction.
 * Designed to float or dock when the main window is minimized.
 */
export const VoiceOrb: React.FC<VoiceOrbProps> = ({
  onActivate,
  onDeactivate,
  visible = true,
  initialState = 'idle',
  state: controlledState,
  onStateChange,
  privacyMode = false,
  showPrivacyIndicator = true,
  onPrivacyToggle,
}) => {
  const [uncontrolledState, setUncontrolledState] = useState<VoiceOrbState>(initialState);
  const [isHovered, setIsHovered] = useState(false);
  const [volume, setVolume] = useState(0); // Audio amplitude (0-1)
  const state = controlledState ?? uncontrolledState;
  const prefersReducedMotion = useReducedMotion();

  const setState = useCallback((next: VoiceOrbState) => {
    if (controlledState === undefined) {
      setUncontrolledState(next);
    }
    onStateChange?.(next);
  }, [controlledState, onStateChange]);

  // Keyboard accessibility handler
  const handleKeyDown = useCallback((e: React.KeyboardEvent) => {
    if (e.key === 'Enter' || e.key === ' ') {
      e.preventDefault();
      handleInteraction();
    }
    if (e.key === 'Escape' && state !== 'idle') {
      setState('idle');
      onDeactivate?.();
    }
  }, [state, onDeactivate]);

  // ARIA label based on state
  const ariaLabel = useMemo(() => {
    const labels: Record<VoiceOrbState, string> = {
      idle: privacyMode ? 'Voice assistant disabled. Enable in settings.' : 'Voice assistant. Press to start listening.',
      listening: privacyMode ? 'Voice recording disabled. Enable in settings.' : 'Listening for voice input. Press to stop.',
      processing: 'Processing your request.',
      speaking: 'Speaking response. Press to stop.',
      error: 'An error occurred. Press to retry.',
    };
    return labels[state];
  }, [state, privacyMode]);

  // Smooth volume animation using sine wave instead of random jumps
  useEffect(() => {
    let interval: NodeJS.Timeout;
    let phase = 0;

    if (state === 'listening' || state === 'speaking') {
      interval = setInterval(() => {
        phase += 0.15;
        // Smooth sine-based volume with slight variation
        const baseVolume = 0.4 + Math.sin(phase) * 0.25;
        const variation = Math.sin(phase * 2.3) * 0.1;
        setVolume(Math.max(0.2, Math.min(0.9, baseVolume + variation)));
      }, 50);
    } else {
      setVolume(0);
    }

    return () => clearInterval(interval);
  }, [state]);

  const handleInteraction = useCallback(() => {
    if (state === 'idle' || state === 'error') {
      setState('listening');
      onActivate?.();
    } else if (state === 'listening') {
      setState('processing');
      // Simulate processing completion
      setTimeout(() => setState('speaking'), 1500);
    } else if (state === 'speaking') {
      setState('idle');
      onDeactivate?.();
    }
  }, [state, setState, onActivate, onDeactivate]);

  if (!visible) return null;

  // Animation variants for reduced motion support
  const orbVariants: Record<VoiceOrbState, { scale: number | number[]; transition: { duration: number; repeat: number; ease: string; } | { idle: { scale: number; transition: { duration: number; repeat: number; ease: string; }; listening: { scale: number }; processing: { scale: number }; speaking: { scale: number }; error: { scale: number; transition: { duration: number; x: number[]; }; } = {
    idle: {
      scale: prefersReducedMotion ? 1 : [1, 1.02, 1],
      transition: { duration: 2, repeat: Infinity, ease: 'easeInOut' },
    },
    listening: { scale: 1.15 },
    processing: { scale: 1.05 },
    speaking: { scale: 1.1 },
    error: prefersReducedMotion
      ? { scale: 1 }
      : { x: [0, -4, 4, -4, 4, 0], transition: { duration: 0.4 } },
  };

  // Dynamic glow effect based on state and volume
  const getBoxShadow = () => {
    if (prefersReducedMotion) return '0 0 15px rgba(0, 229, 255, 0.3)';

    switch (state) {
      case 'listening':
        return `0 0 ${20 + volume * 25}px rgba(0, 229, 255, ${0.4 + volume * 0.4})`;
      case 'speaking':
        return `0 0 ${20 + volume * 30}px rgba(16, 185, 129, ${0.4 + volume * 0.3})`;
      case 'processing':
        return '0 0 25px rgba(99, 102, 241, 0.5)';
      case 'error':
        return '0 0 20px rgba(239, 68, 68, 0.6)';
      default:
        return '0 0 15px rgba(0, 229, 255, 0.2)';
    }
  };

  return (
    <AnimatePresence>
      <motion.div
        drag
        dragMomentum={false}
        initial={{ opacity: 0, scale: 0.8, y: 50 }}
        animate={{ opacity: 1, scale: 1, y: 0 }}
        exit={{ opacity: 0, scale: 0.8, y: 50 }}
        transition={{ type: 'spring', stiffness: 260, damping: 20 }}
        className="fixed bottom-8 right-8 z-[9999]"
      >
        <div
          className="relative group"
          onMouseEnter={() => setIsHovered(true)}
          onMouseLeave={() => setIsHovered(false)}
        >
          {/* Idle Pulse Ring */}
          {state === 'idle' && !prefersReducedMotion && (
            <motion.div
              animate={{ scale: [1, 1.3, 1.3], opacity: [0.5, 0, 0] }}
              transition={{ duration: 2, repeat: Infinity, ease: 'easeOut' }}
              className="absolute inset-0 rounded-full border-2 border-cyan-400/40"
            />
          )}

          {/* Main Orb Sphere */}
          <motion.button
            type="button"
            role="button"
            aria-label={ariaLabel}
            aria-live="polite"
            tabIndex={0}
            onClick={handleInteraction}
            onKeyDown={handleKeyDown}
            variants={orbVariants}
            animate={state}
            style={{ boxShadow: getBoxShadow() }}
            transition={{ type: 'spring', stiffness: 300, damping: 20 }}
            className={cn(
              "w-16 h-16 rounded-full cursor-pointer flex items-center justify-center backdrop-blur-md border border-white/10 transition-colors duration-300",
              "focus:outline-none focus-visible:ring-2 focus-visible:ring-cyan-400 focus-visible:ring-offset-2 focus-visible:ring-offset-black",
              state === 'idle' && "bg-black/60 hover:bg-black/80 border-cyan-500/30",
              state === 'listening' && "bg-cyan-900/40 border-cyan-400/80",
              state === 'processing' && "bg-indigo-900/40 border-indigo-400/80",
              state === 'speaking' && "bg-emerald-900/40 border-emerald-400/80",
              state === 'error' && "bg-red-900/40 border-red-400/80"
            )}
          >
            {/* Inner Icon / Visualization */}
            <div className="relative z-10 text-white/90">
              {state === 'idle' && (
                <Mic className={cn("w-6 h-6 transition-colors", isHovered && "text-cyan-400")} />
              )}
              {state === 'listening' && (
                <VoiceVisualizer active volume={volume} color="#00e5ff" reducedMotion={prefersReducedMotion} />
              )}
              {state === 'processing' && (
                <Loader2 className={cn("w-8 h-8 text-indigo-400", !prefersReducedMotion && "animate-spin")} />
              )}
              {state === 'speaking' && (
                <VoiceVisualizer active volume={volume} color="#10b981" reducedMotion={prefersReducedMotion} />
              )}
              {state === 'error' && (
                <Mic className="w-6 h-6 text-red-400" />
              )}
            </div>

            {/* Orbiting Ring (Processing) */}
            {state === 'processing' && !prefersReducedMotion && (
              <motion.div
                animate={{ rotate: 360 }}
                transition={{ repeat: Infinity, duration: 2, ease: 'linear' }}
                className="absolute inset-0 rounded-full border-t-2 border-indigo-400 opacity-50"
              />
            )}
          </motion.button>

          {/* Status Label (Hover Only) */}
          <AnimatePresence>
            {isHovered && (
                <motion.div
                    initial={{ opacity: 0, x: -10 }}
                    animate={{ opacity: 1, x: -20 }}
                    exit={{ opacity: 0, x: -10 }}
                    className="absolute right-full top-1/2 -translate-y-1/2 px-2 py-1 bg-black/80 border border-white/10 rounded text-xs text-white whitespace-nowrap backdrop-blur"
                >
                    {state.charAt(0).toUpperCase() + state.slice(1)}
                  </motion.div>
            )}
          </AnimatePresence>

          {/* Privacy Indicator */}
          {showPrivacyIndicator && privacyMode && (
            <motion.div
              initial={{ opacity: 0, scale: 0.8 }}
              animate={{ opacity: 1, scale: 1 }}
              className="absolute -top-1 -right-1 w-3 h-3 rounded-full bg-amber-500/20 border-2 border-amber-400/50"
            >
              <div className="w-1.5 h-1.5 rounded-full bg-amber-400" />
            </motion.div>
          )}
        </div>
      </motion.div>
    </AnimatePresence>
  );
};

/**
 * VoiceVisualizer - Smooth audio visualization bars
 * Uses deterministic sine-wave based animations for smooth movement
 */
interface VoiceVisualizerProps {
  active: boolean;
  volume: number;
  color: string;
  reducedMotion?: boolean | null;
}

const VoiceVisualizer: React.FC<VoiceVisualizerProps> = ({
  active,
  volume,
  color,
  reducedMotion = false,
}) => {
  // Deterministic height multipliers for each bar (creates wave effect)
  const barMultipliers = [0.6, 0.9, 1.0, 0.85, 0.5];

  if (reducedMotion) {
    // Static visualization for reduced motion
    return (
      <div className="flex items-center gap-0.5 h-8">
        {barMultipliers.map((mult, i) => (
          <div
            key={i}
            style={{ backgroundColor: color, height: active ? 16 * mult : 4 }}
            className="w-1 rounded-full opacity-80 transition-all duration-300"
          />
        ))}
      </div>
    );
  }

  return (
    <div className="flex items-center gap-0.5 h-8">
      {barMultipliers.map((mult, i) => (
        <motion.div
          key={i}
          animate={{
            height: active ? Math.max(4, volume * 32 * mult) : 4,
          }}
          transition={{
            type: 'spring',
            stiffness: 400,
            damping: 25,
            mass: 0.5,
          }}
          style={{ backgroundColor: color }}
          className="w-1 rounded-full opacity-80"
        />
      ))}
    </div>
  );
};

