import { McpServer } from '@modelcontextprotocol/sdk/server/mcp.js';
import { StdioServerTransport } from '@modelcontextprotocol/sdk/server/stdio.js';
import { z } from 'zod';
import type { ExtensionContext } from 'vscode';

import { SentinelDaemon } from '../../extension/src/sentinel/SentinelDaemon';
import { LedgerManager } from '../../extension/src/qorelogic/ledger/LedgerManager';
import { IntentService } from '../../extension/src/governance/IntentService';
import type { Intent } from '../../extension/src/governance/types/IntentTypes';
import type { LedgerEventType, SentinelVerdict } from '../../extension/src/shared/types';

type ToolContentChunk = {
    type: 'text';
    text: string;
};

type ToolResult = {
    content: ToolContentChunk[];
    outputs?: Record<string, unknown>;
};

type IntegritySnapshot = {
    ledgerChain: {
        valid: boolean;
        totalEntries: number;
        verifiedAt: string;
    };
};

const LEDGER_EVENT_TYPES: LedgerEventType[] = [
    'PROPOSAL',
    'AUDIT_PASS',
    'AUDIT_FAIL',
    'L3_QUEUED',
    'L3_APPROVED',
    'L3_REJECTED',
    'TRUST_UPDATE',
    'PENALTY_APPLIED',
    'QUARANTINE_START',
    'QUARANTINE_END',
    'DIVERGENCE_DECLARED',
    'DIVERGENCE_RESOLVED',
    'SYSTEM_EVENT'
];

const SYSTEM_AGENT_DID = 'did:myth:system:mcp';

const sentinelAuditSchema = z.object({
    path: z.string().min(1)
});

const ledgerLogSchema = z.object({
    content: z.string().min(1),
    type: z.string().min(1)
});

const qoreLogicStatusSchema = z.object({}).strict();

export class FailSafeMCPServer {
    private readonly server: McpServer;
    private readonly transport: StdioServerTransport;
    private running = false;

    constructor(
        private readonly context: ExtensionContext,
        private readonly sentinel: SentinelDaemon,
        private readonly ledgerManager: LedgerManager,
        private readonly intentService: IntentService
    ) {
        this.server = new McpServer({
            name: 'failsafe-mcp',
            version: '1.0.0',
            description: 'Model Context Protocol bridge into FailSafe governance and sentinel systems'
        });
        this.transport = new StdioServerTransport();
        this.registerTools();
    }

    async start(): Promise<void> {
        if (this.running) {
            return;
        }

        await this.server.connect(this.transport);
        this.running = true;
        await this.context.workspaceState.update('failsafeMcp.lastConnectedAt', new Date().toISOString());
    }

    private registerTools(): void {
        this.server.registerTool(
            'sentinel_audit_file',
            {
                title: 'Sentinel Audit File',
                description: 'Requests the sentinel daemon to audit the provided file path',
                inputSchema: sentinelAuditSchema
            },
            async ({ path }) => this.handleSentinelAudit(path)
        );

        this.server.registerTool(
            'ledger_log_entry',
            {
                title: 'Ledger Log Entry',
                description: 'Appends an audit entry into the QoreLogic ledger',
                inputSchema: ledgerLogSchema
            },
            async ({ content, type }) => this.handleLedgerLogEntry(content, type)
        );

        this.server.registerTool(
            'qorelogic_status',
            {
                title: 'QoreLogic Status',
                description: 'Returns the active intent and ledger integrity posture',
                inputSchema: qoreLogicStatusSchema
            },
            async () => this.handleQoreLogicStatus()
        );
    }

    private async handleSentinelAudit(path: string): Promise<ToolResult> {
        try {
            const verdict: SentinelVerdict = await this.sentinel.auditFile(path);
            const confidence = Number.isFinite(verdict.confidence)
                ? `${Math.round(verdict.confidence * 100)}%`
                : 'n/a';

            return {
                content: [
                    {
                        type: 'text',
                        text: `Sentinel verdict for ${path}: ${verdict.decision} (${verdict.riskGrade}) Ã¢â‚¬â€ ${verdict.summary} [confidence ${confidence}]`
                    }
                ],
                outputs: { verdict }
            };
        } catch (error) {
            FailSafeMCPServer.raiseToolError('sentinel_audit_file', error);
        }
    }

    private async handleLedgerLogEntry(content: string, type: string): Promise<ToolResult> {
        try {
            const eventType = this.normalizeLedgerEventType(type);
            const entry = await this.ledgerManager.appendEntry({
                eventType,
                agentDid: SYSTEM_AGENT_DID,
                agentTrustAtAction: 0,
                payload: {
                    message: content,
                    recordedBy: 'mcp',
                    recordedAt: new Date().toISOString()
                }
            });

            return {
                content: [
                    {
                        type: 'text',
                        text: `Ledger entry ${entry.id} recorded (${entry.eventType}).`
                    }
                ],
                outputs: { entry }
            };
        } catch (error) {
            FailSafeMCPServer.raiseToolError('ledger_log_entry', error);
        }
    }

    private async handleQoreLogicStatus(): Promise<ToolResult> {
        try {
            const intent: Intent | null = await this.intentService.getActiveIntent();
            const integrity = this.buildIntegritySnapshot();
            const intentSummary = intent
                ? `${intent.id} [${intent.status}]`
                : 'no active intent';

            return {
                content: [
                    {
                        type: 'text',
                        text: `Active intent: ${intentSummary}. Ledger chain intact: ${integrity.ledgerChain.valid ? 'yes' : 'no'} (${integrity.ledgerChain.totalEntries} entries).`
                    }
                ],
                outputs: {
                    activeIntent: intent,
                    integrityStatus: integrity
                }
            };
        } catch (error) {
            FailSafeMCPServer.raiseToolError('qorelogic_status', error);
        }
    }

    private buildIntegritySnapshot(): IntegritySnapshot {
        const verified = this.ledgerManager.verifyChain();
        return {
            ledgerChain: {
                valid: verified,
                totalEntries: this.ledgerManager.getEntryCount(),
                verifiedAt: new Date().toISOString()
            }
        };
    }

    private normalizeLedgerEventType(value: string): LedgerEventType {
        if (!value) {
            return 'SYSTEM_EVENT';
        }

        const normalized = value.trim().toUpperCase() as LedgerEventType;
        if (LEDGER_EVENT_TYPES.includes(normalized)) {
            return normalized;
        }

        return 'SYSTEM_EVENT';
    }

    private static raiseToolError(tool: string, error: unknown): never {
        const message = error instanceof Error ? error.message : String(error);
        throw new Error(`${tool} failed: ${message}`);
    }
}

