#[tauri::command]
pub async fn voice_interaction_create(
    auth: State<'_, Arc<SecureAuthenticator>>,
    request: AuthenticatedRequest<CreateVoiceInteractionRequest>,
    db_state: State<'_, Arc<RwLock<Option<DatabaseManager>>>>,
    key_manager: State<'_, Arc<SecureKeyManager>>,
) -> Result<VoiceInteraction, String> {
    let authenticated_user = require_auth!(auth, &request.auth_token);
    require_ownership!(authenticated_user, &request.payload.user_id);
    let manager =
        VoiceInteractionManager::new(db_state.inner().clone(), key_manager.inner().clone());
    manager.create_interaction(request.payload).await
}

