#[cfg(test)]
mod tests {
    use super::*;
    use crate::db_manager::DatabaseManager;
    use sqlx::sqlite::SqlitePoolOptions;

    async fn create_test_db() -> Arc<RwLock<Option<DatabaseManager>>> {
        let pool = SqlitePoolOptions::new()
            .max_connections(5)
            .connect("sqlite::memory:")
            .await
            .expect("Failed to create test database");

        let db_manager = DatabaseManager {
            write_pool: pool.clone(),
            read_pool: pool,
        };
        Arc::new(RwLock::new(Some(db_manager)))
    }

    fn create_test_key_manager() -> Arc<SecureKeyManager> {
        Arc::new(SecureKeyManager::new())
    }

    #[tokio::test]
    async fn test_voice_interaction_manager_creation() {
        let db = create_test_db().await;
        let key_manager = create_test_key_manager();
        let _manager = VoiceInteractionManager::new(db, key_manager);
        assert!(true);
    }

    #[tokio::test]
    async fn test_initialize_schema() {
        let db = create_test_db().await;
        let key_manager = create_test_key_manager();
        let manager = VoiceInteractionManager::new(db, key_manager);
        let result = manager.initialize_schema().await;
        assert!(result.is_ok());
    }

    #[tokio::test]
    async fn test_create_and_get_interaction() {
        let db = create_test_db().await;
        let key_manager = create_test_key_manager();
        let manager = VoiceInteractionManager::new(db, key_manager);
        manager.initialize_schema().await.unwrap();

        let request = CreateVoiceInteractionRequest {
            user_id: "test_user".to_string(),
            persona_id: Some("alice".to_string()),
            interaction_type: VoiceInteractionType::Transcript,
            transcript: "Hello world".to_string(),
            audio_metadata: Some(VoiceBiometrics {
                stress_level: Some(0.3),
                speech_rate: Some(150.0),
                volume_level: Some(0.7),
                pitch_variation: Some(50.0),
                confidence: Some(0.95),
                additional_metrics: HashMap::new(),
            }),
            engine: Some("whisper".to_string()),
            session_id: Some("session_123".to_string()),
            duration_ms: Some(2000),
        };

        let created = manager.create_interaction(request).await.unwrap();
        assert_eq!(created.user_id, "test_user");
        assert_eq!(created.transcript, "Hello world");
        assert!(created.audio_metadata.is_some());

        let get_request = GetVoiceInteractionsRequest {
            user_id: "test_user".to_string(),
            persona_id: None,
            interaction_type: None,
            session_id: None,
            limit: None,
        };

        let interactions = manager.get_interactions(get_request).await.unwrap();
        assert_eq!(interactions.len(), 1);
        assert_eq!(interactions[0].transcript, "Hello world");
    }

    #[tokio::test]
    async fn test_delete_interaction() {
        let db = create_test_db().await;
        let key_manager = create_test_key_manager();
        let manager = VoiceInteractionManager::new(db, key_manager);
        manager.initialize_schema().await.unwrap();

        let request = CreateVoiceInteractionRequest {
            user_id: "test_user".to_string(),
            persona_id: None,
            interaction_type: VoiceInteractionType::Transcript,
            transcript: "To be deleted".to_string(),
            audio_metadata: None,
            engine: None,
            session_id: None,
            duration_ms: None,
        };

        let created = manager.create_interaction(request).await.unwrap();

        let delete_request = DeleteVoiceInteractionRequest {
            interaction_id: created.id,
            user_id: "test_user".to_string(),
        };

        manager.delete_interaction(delete_request).await.unwrap();

        let get_request = GetVoiceInteractionsRequest {
            user_id: "test_user".to_string(),
            persona_id: None,
            interaction_type: None,
            session_id: None,
            limit: None,
        };

        let interactions = manager.get_interactions(get_request).await.unwrap();
        assert!(interactions.is_empty());
    }

    #[tokio::test]
    async fn test_get_stats() {
        let db = create_test_db().await;
        let key_manager = create_test_key_manager();
        let manager = VoiceInteractionManager::new(db, key_manager);
        manager.initialize_schema().await.unwrap();

        // Create multiple interactions
        for i in 0..3 {
            let request = CreateVoiceInteractionRequest {
                user_id: "test_user".to_string(),
                persona_id: None,
                interaction_type: VoiceInteractionType::Transcript,
                transcript: format!("Interaction {}", i),
                audio_metadata: Some(VoiceBiometrics {
                    stress_level: Some(0.2 + i as f64 * 0.1),
                    speech_rate: None,
                    volume_level: None,
                    pitch_variation: None,
                    confidence: Some(0.9 - i as f64 * 0.1),
                    additional_metrics: HashMap::new(),
                }),
                engine: None,
                session_id: None,
                duration_ms: Some(1000 + i * 500),
            };
            manager.create_interaction(request).await.unwrap();
        }

        let stats = manager.get_stats("test_user").await.unwrap();
        assert_eq!(stats.total_interactions, 3);
        assert_eq!(stats.total_duration_ms, 4500);
        assert!(stats.average_confidence.is_some());
        assert!(stats.average_stress_level.is_some());
    }
}

