  const [error, setError] = useState<string | null>(null);
  const [filterType, setFilterType] = useState<VoiceInteractionType | 'all'>('all');

  useEffect(() => {
    loadHistory();
  }, [userId]);

  const loadHistory = async () => {
    try {
      setLoading(true);
      const data = await getVoiceInteractions({
        user_id: userId,
        limit: 50 // Fetch last 50
      });
      setInteractions(data);
    } catch (err: any) {
      console.error("Failed to load voice history:", err);
      setError("Unable to retrieve voice memory securely.");
    } finally {
      setLoading(false);
    }
  };

  // Helper to format timestamp
