#[cfg(test)]
mod tests {
    use super::*;
    use crate::db_manager::DatabaseManager;
    use sqlx::sqlite::SqlitePoolOptions;

    async fn create_test_db() -> Arc<RwLock<Option<DatabaseManager>>> {
        let pool = SqlitePoolOptions::new()
            .max_connections(5)
            .connect("sqlite::memory:")
            .await
            .expect("Failed to create test database");

        let db_manager = DatabaseManager {
            write_pool: pool.clone(),
            read_pool: pool,
        };
        Arc::new(RwLock::new(Some(db_manager)))
    }

    fn create_test_key_manager() -> Arc<SecureKeyManager> {
        Arc::new(SecureKeyManager::new())
    }

    #[tokio::test]
    async fn test_voice_interaction_manager_creation() {
        let db = create_test_db().await;
        let key_manager = create_test_key_manager();
        let _manager = VoiceInteractionManager::new(db, key_manager);
        assert!(true);
    }

    #[tokio::test]
    async fn test_initialize_schema() {
        let db = create_test_db().await;
        let key_manager = create_test_key_manager();
        let manager = VoiceInteractionManager::new(db, key_manager);
        let result = manager.initialize_schema().await;
