  user_id: string;
  persona_id?: string;
  interaction_type?: VoiceInteractionType;
  session_id?: string;
  limit?: number;
};

export const getVoiceInteractions = async (
  request: GetVoiceInteractionsRequest,
): Promise<VoiceInteraction[]> => {
  // The strict-auth wrapper in main.rs expects { auth_token, payload: { ... } }
  // but the `buildAuth` helper above flattens it: { auth_token, ...payload }.
  // We need to match the backend expectation.
  // Rust: AuthenticatedRequest<GetVoiceInteractionsRequest>

  // Check if other bridges use a specific wrapper.
  // Usually AuthenticatedRequest on Rust side implies: invoke('cmd', { request: { auth_token, payload } })
  // OR invoke('cmd', { auth_token, payload }) if flat.
  // Based on `BaseBridge.ts` patterns in this project, it's likely wrapped.
  // However, existing voice commands use `buildAuth` which is flat.
  // Let's look at `voice_interaction_get` signature in `voice_interactions.rs`:
  // It takes `request: AuthenticatedRequest<GetVoiceInteractionsRequest>`.

  // Correct structure for AuthenticatedRequest macros in this project is usually:
  // { request: { auth_token: "...", payload: { ... } } }

  return invoke("voice_interaction_get", {
    request: {
      auth_token: `demo_token_${request.user_id}`,
      payload: request,
    },
  });
};

