    month: "short",
    day: "numeric",
    hour: "numeric",
    minute: "2-digit",
  });
};

const getStressTone = (stress?: number) => {
  if (stress === undefined) {
    return "bg-slate-800/70 text-slate-200 border border-slate-500/50";
  }
  if (stress > 0.75) {
    return "bg-rose-500/15 text-rose-200 border border-rose-400/60";
  }
  if (stress > 0.45) {
    return "bg-amber-500/15 text-amber-200 border border-amber-400/60";
  }
  return "bg-emerald-500/10 text-emerald-200 border border-emerald-400/60";
};

export const VoiceSessionHistory: React.FC<VoiceSessionHistoryProps> = ({
  userId,
  personaId,
}) => {
  const [history, setHistory] = useState<VoiceInteraction[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [expandedId, setExpandedId] = useState<string | null>(null);
  const [filterType, setFilterType] = useState<VoiceInteractionType | "all">(
    "all",
  );
  const [offset, setOffset] = useState(0);
  const [hasMore, setHasMore] = useState(false);

  const fetchHistory = useCallback(
    async (nextOffset = 0, append = false) => {
      setLoading(true);
      try {
        const response = await getVoiceHistory({
          user_id: userId,
          persona_id: personaId,
          limit: PAGE_SIZE,
          offset: nextOffset,
        });
        setHasMore(response.length === PAGE_SIZE);
        setHistory((previous) =>
          append ? [...previous, ...response] : response,
        );
        setOffset(nextOffset + response.length);
        setError(null);
      } catch (err) {
        console.error("Voice history fetch failed", err);
        setError("Unable to read your encrypted voice memory right now.");
      } finally {
        setLoading(false);
      }
    },
    [userId, personaId],
  );

  useEffect(() => {
    setHistory([]);
    setOffset(0);
    setHasMore(false);
    setExpandedId(null);
    fetchHistory(0, false);
  }, [userId, fetchHistory]);

  const filteredHistory = useMemo(() => {
    if (filterType === "all") return history;
    return history.filter(
      (interaction) => interaction.interaction_type === filterType,
    );
  }, [history, filterType]);

  const renderBiometrics = (interaction: VoiceInteraction) => {
    const data = interaction.audio_metadata;
