    const badges: JSX.Element[] = [];

    if (data.stress_level !== undefined) {
      badges.push(
        <span
          key="stress"
          className={`flex items-center gap-1 rounded-full px-3 py-0.5 text-[0.65rem] font-semibold tracking-wide uppercase ${getStressTone(
            data.stress_level,
          )}`}
        >
          <Activity className="h-3 w-3" />
          {data.stress_level > 0.75
            ? "High stress"
            : data.stress_level > 0.45
              ? "Elevated stress"
              : "Calm voice"}
        </span>,
      );
    }

    if (data.speech_rate !== undefined) {
      badges.push(
        <span
          key="speech_rate"
          className="flex items-center gap-1 rounded-full border border-slate-600/60 bg-slate-900/60 px-3 py-0.5 text-[0.65rem] font-semibold text-slate-200"
        >
          <LineChart className="h-3 w-3 text-slate-300" />
          {Math.round(data.speech_rate)} WPM
        </span>,
      );
    }

    if (data.confidence !== undefined) {
      const confidenceValue = Math.round(data.confidence * 100);
      badges.push(
        <span
          key="confidence"
          className="flex items-center gap-1 rounded-full border border-slate-600/60 bg-slate-900/40 px-3 py-0.5 text-[0.65rem] font-semibold text-slate-200"
        >
          <Clock className="h-3 w-3 text-slate-500" />
          {confidenceValue}% confidence
        </span>,
      );
    }

    if (badges.length === 0) {
      return null;
    }

    return <div className="mt-3 flex flex-wrap gap-2">{badges}</div>;
  };

  const hasContent = filteredHistory.length > 0 || loading;

  return (
    <div className="flex h-full flex-col overflow-hidden rounded-2xl bg-slate-950/60 p-6 shadow-inner shadow-teal-500/10">
      <div className="flex flex-col gap-4 pb-4 sm:flex-row sm:items-end sm:justify-between">
        <div>
          <h2 className="flex items-center gap-2 text-2xl font-light text-slate-100">
            <Mic className="h-6 w-6 text-teal-300" />
            Voice Memory
          </h2>
          <p className="text-sm text-slate-400">
            Secure, decrypted transcripts and biometrics streamed from your
            private vault.
          </p>
        </div>

        <div className="flex flex-wrap gap-2">
          {filterOptions.map((option) => (
            <button
              key={option.value}
              onClick={() => setFilterType(option.value)}
              className={`rounded-full border px-3 py-1 text-xs font-semibold uppercase tracking-wide transition ${
                filterType === option.value
                  ? "border-teal-400/70 bg-teal-500/20 text-teal-200 shadow-inner shadow-teal-500/30"
                  : "border-white/10 bg-white/5 text-white/70 hover:border-teal-300/70 hover:text-teal-200"
              }`}
              aria-pressed={filterType === option.value}
            >
              {option.label}
            </button>
          ))}
        </div>
      </div>

      <div className="relative flex-1 overflow-hidden rounded-2xl border border-white/5 bg-slate-900/40">
