/// Voice audit event types for GDPR compliance
pub const VOICE_EVENT_START_LISTENING: &str = "VOICE_START_LISTENING";
pub const VOICE_EVENT_STOP_LISTENING: &str = "VOICE_STOP_LISTENING";
#[allow(dead_code)]
pub const VOICE_EVENT_TRANSCRIPT_RECEIVED: &str = "VOICE_TRANSCRIPT_RECEIVED";
pub const VOICE_EVENT_SYNTHESIS_REQUEST: &str = "VOICE_SYNTHESIS_REQUEST";
#[allow(dead_code)]
pub const VOICE_EVENT_PRIVACY_BLOCK: &str = "VOICE_PRIVACY_BLOCK";

#[derive(Debug, Default)]
pub struct VoiceRuntimeState {
    process: Mutex<Option<VoiceProcess>>,
