#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct VoiceSynthesisResponse {
    pub success: bool,
    pub audio_base64: Option<String>,
    pub format: Option<String>,
    pub sample_rate: Option<i32>,
    pub engine: Option<String>,
    pub voice_id: Option<String>,
    pub error: Option<String>,
}

fn ensure_local_only(endpoint: &Option<String>) -> Result<(), String> {
    let Some(endpoint) = endpoint else {
        return Ok(());
    };
    let lower = endpoint.trim().to_lowercase();

    // 1. Scheme Check
    let is_http = lower.starts_with("http://");
    let is_https = lower.starts_with("https://");
    if !is_http && !is_https {
        return Err("Voice privacy policy: Only HTTP/HTTPS allowed".to_string());
    }

    let remainder = if is_http { &lower[7..] } else { &lower[8..] };

    // 2. Strict Host Check (Prevent DNS Rebinding / Subdomain attacks)
    // Valid hosts must be exactly "localhost" or "127.0.0.1", optionally followed by port or path.
    let valid_hosts = ["localhost", "127.0.0.1"];
    let mut is_local = false;

    for host in valid_hosts {
        if remainder.starts_with(host) {
            let after_host = &remainder[host.len()..];
            // Check boundary conditions: End of string, Port separator, or Path separator
            if after_host.is_empty() || after_host.starts_with(':') || after_host.starts_with('/') {
                is_local = true;
                break;
            }
        }
    }

    if !is_local {
        return Err("Voice privacy policy: Remote endpoints are STRICTLY FORBIDDEN".to_string());
    }

    Ok(())
}

/// Helper function to log voice audit events for GDPR compliance
async fn log_voice_audit_event(
    db: Arc<tokio::sync::RwLock<Option<DatabaseManager>>>,
    user_id: &str,
    event_type: &str,
    description: &str,
    details: std::collections::HashMap<String, serde_json::Value>,
) {
    if let Some(db_ref) = db.read().await.as_ref() {
        let event_id = format!("voice_event_{}", chrono::Utc::now().timestamp_millis());
        let timestamp = chrono::Utc::now().to_rfc3339();
        let details_json = serde_json::to_string(&details).unwrap_or_default();

        let query = r#"
            INSERT INTO sentry_security_events (
                event_id, user_id, agent_id, event_type, severity, description, details, timestamp, resolved
            ) VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?)
        "#;

        let _ = sqlx::query(query)
            .bind(&event_id)
            .bind(user_id)
            .bind("voice")
            .bind(event_type)
            .bind("info")
            .bind(description)
            .bind(&details_json)
            .bind(&timestamp)
            .bind(false)
            .execute(&db_ref.write_pool)
            .await;
    }
}

fn resolve_voice_bridge(app_handle: &AppHandle) -> std::path::PathBuf {
    app_handle
        .path_resolver()
        .resolve_resource("python/voice_bridge.py")
        .unwrap_or_else(|| std::path::PathBuf::from("src-tauri/python/voice_bridge.py"))
}

/// Start the Python voice bridge as a subprocess.
/// Emits stdout JSON lines as Tauri events for the frontend.
#[tauri::command]
pub async fn voice_start_listening(
    app_handle: tauri::AppHandle,
    auth: State<'_, Arc<SecureAuthenticator>>,
    request: AuthenticatedRequest<VoiceStartListeningRequest>,
    voice_state: State<'_, VoiceRuntimeState>,
    db: State<'_, Arc<tokio::sync::RwLock<Option<DatabaseManager>>>>,
) -> Result<String, String> {
    let authenticated_user = require_auth!(auth, &request.auth_token);
    require_ownership!(authenticated_user, &request.payload.user_id);

    ensure_local_only(&request.payload.endpoint)?;

    let mut state = voice_state.process.lock().await;
    if state.is_some() {
        return Err("Voice listening already active".to_string());
    }

    let python_exe = if cfg!(windows) { "python" } else { "python3" };
    let script_path = resolve_voice_bridge(&app_handle);
    let engine = request
        .payload
        .engine
        .clone()
        .unwrap_or_else(|| "auto".to_string());
    let mode = request
        .payload
        .mode
        .clone()
        .unwrap_or_else(|| "listen".to_string());

    // Log voice start event for GDPR compliance
    let user_id = request.payload.user_id.clone();
    let mut details = std::collections::HashMap::new();
    details.insert(
        "engine".to_string(),
        serde_json::Value::String(engine.clone()),
    );
    details.insert("mode".to_string(), serde_json::Value::String(mode.clone()));
    if let Some(endpoint) = &request.payload.endpoint {
        details.insert(
            "endpoint".to_string(),
            serde_json::Value::String(endpoint.clone()),
        );
    }

    log_voice_audit_event(
        db.inner().clone(),
        &user_id,
        VOICE_EVENT_START_LISTENING,
        &format!(
            "Voice listening started (engine: {}, mode: {})",
            engine, mode
        ),
        details,
    )
    .await;

    let mut child = Command::new(python_exe)
        .arg(script_path.to_string_lossy().to_string())
        .arg("listen")
        .arg("--engine")
        .arg(&engine)
        .arg("--mode")
        .arg(&mode)
        .stdout(Stdio::piped())
        .stderr(Stdio::piped())
        .spawn()
        .map_err(|e| format!("Failed to start voice bridge: {}", e))?;

    let stdout = child
        .stdout
        .take()
        .ok_or("Failed to capture voice bridge stdout")?;
    let app_handle_clone = app_handle.clone();
    std::thread::spawn(move || {
        let reader = BufReader::new(stdout);
        for line in reader.lines().flatten() {
            if let Ok(payload) = serde_json::from_str::<serde_json::Value>(&line) {
                let event_name = match payload.get("event").and_then(|v| v.as_str()) {
                    Some("transcript") => "voice://transcript",
                    Some("ready") => "voice://ready",
                    Some("audio") => "voice://audio",
                    _ => "voice://bridge",
                };
                let _ = app_handle_clone.emit_all(event_name, payload);
            }
        }
    });

    *state = Some(VoiceProcess { child });

    Ok(format!(
        "Voice listening started (engine: {}, mode: {})",
        engine, mode
    ))
}

/// Stop the voice bridge process if running.
#[tauri::command]
pub async fn voice_stop(
    auth: State<'_, Arc<SecureAuthenticator>>,
    request: AuthenticatedRequest<VoiceStopRequest>,
    voice_state: State<'_, VoiceRuntimeState>,
    db: State<'_, Arc<tokio::sync::RwLock<Option<DatabaseManager>>>>,
) -> Result<String, String> {
    let authenticated_user = require_auth!(auth, &request.auth_token);
    require_ownership!(authenticated_user, &request.payload.user_id);

    let mut state = voice_state.process.lock().await;

    let user_id = request.payload.user_id.clone();

    if let Some(mut process) = state.take() {
        let _ = process.child.kill();
        let _ = process.child.wait();

        // Log voice stop event for GDPR compliance
        let mut details = std::collections::HashMap::new();
        details.insert(
            "reason".to_string(),
            serde_json::Value::String("user_request".to_string()),
        );

        log_voice_audit_event(
            db.inner().clone(),
            &user_id,
            VOICE_EVENT_STOP_LISTENING,
            "Voice listening stopped by user",
            details,
        )
        .await;

        return Ok("Voice listening stopped".to_string());
    }
    Ok("Voice listening was not active".to_string())
}

/// Synthesize text to audio via the Python voice bridge (Piper).
#[tauri::command]
pub async fn voice_synthesize(
    app_handle: tauri::AppHandle,
    auth: State<'_, Arc<SecureAuthenticator>>,
    request: AuthenticatedRequest<VoiceSynthesisRequest>,
    db: State<'_, Arc<tokio::sync::RwLock<Option<DatabaseManager>>>>,
) -> Result<VoiceSynthesisResponse, String> {
    let authenticated_user = require_auth!(auth, &request.auth_token);
    require_ownership!(authenticated_user, &request.payload.user_id);

    let python_exe = if cfg!(windows) { "python" } else { "python3" };
    let script_path = resolve_voice_bridge(&app_handle);
    let voice_id = request
        .payload
        .voice_id
        .clone()
        .unwrap_or_else(|| "alice".to_string());

    // Log voice synthesis event for GDPR compliance
    let user_id = request.payload.user_id.clone();
    let text_preview = request.payload.text.chars().take(50).collect::<String>();
    let mut details = std::collections::HashMap::new();
    details.insert(
        "voice_id".to_string(),
        serde_json::Value::String(voice_id.clone()),
    );
    details.insert(
        "text_preview".to_string(),
        serde_json::Value::String(text_preview),
    );
    details.insert(
        "text_length".to_string(),
        serde_json::Value::Number(serde_json::Number::from(request.payload.text.len())),
    );

    log_voice_audit_event(
        db.inner().clone(),
        &user_id,
        VOICE_EVENT_SYNTHESIS_REQUEST,
        &format!(
            "Voice synthesis requested (voice: {}, text: {} chars)",
            voice_id,
            request.payload.text.len()
        ),
        details,
    )
    .await;

    let output = Command::new(python_exe)
        .arg(script_path.to_string_lossy().to_string())
        .arg("synthesize")
        .arg("--text")
        .arg(&request.payload.text)
        .arg("--voice")
        .arg(&voice_id)
        .output()
        .map_err(|e| format!("Failed to run voice synthesis: {}", e))?;

    if !output.status.success() {
        let stderr = String::from_utf8_lossy(&output.stderr);
        return Err(format!("Voice synthesis failed: {}", stderr.trim()));
    }

    let stdout = String::from_utf8_lossy(&output.stdout);
    for line in stdout.lines() {
        if let Ok(payload) = serde_json::from_str::<VoiceSynthesisResponse>(line) {
            return Ok(payload);
        }
    }

    Err("Voice synthesis did not return a response".to_string())
}

