/**
 * Approval Queue Component
 * 
 * Implements the HITL (Human-in-the-Loop) Protocol for Alden.
 * All AI-generated actions from external inputs (Email, Calendar, Web)
 * must be queued here for user approval before execution.
 * 
 * This is a SECURITY COMPONENT - it prevents prompt injection attacks
 * by ensuring no action executes without explicit user consent.
 * 
 * @version 1.0.0
 * @date 2026-01-23
 */

import React, { useState } from 'react';
import { Check, X, Mail, Calendar, CheckSquare, AlertTriangle, Clock, ChevronDown, ChevronUp } from '../ui/HearthlinkIcons';
import { Button } from '../ui/button';
import { cn } from '../../lib/utils';
import { sanitizeInput } from '../../utils/security';

// Types for pending actions
export type PendingActionType = 'email_reply' | 'email_archive' | 'email_delete' | 'task_create' | 'calendar_event' | 'file_operation';

export interface PendingAction {
  id: string;
  type: PendingActionType;
  title: string;
  description: string;
  sourceContext: string; // Where this action originated (e.g., "Email from john@example.com")
  createdAt: Date;
  payload: Record<string, unknown>; // The actual action data
  riskLevel: 'low' | 'medium' | 'high';
  aiConfidence: number; // 0-100
}

interface ApprovalQueueProps {
  pendingActions: PendingAction[];
  onApprove: (actionId: string) => Promise<void>;
  onReject: (actionId: string) => Promise<void>;
  onEdit?: (actionId: string) => void;
  className?: string;
}

const ACTION_ICONS: Record<PendingActionType, React.ComponentType<{ className?: string }>> = {
  email_reply: Mail,
  email_archive: Mail,
  email_delete: AlertTriangle,
  task_create: CheckSquare,
  calendar_event: Calendar,
  file_operation: AlertTriangle,
};

const RISK_COLORS = {
  low: 'text-green-400 bg-green-500/10 border-green-500/30',
  medium: 'text-amber-400 bg-amber-500/10 border-amber-500/30',
  high: 'text-red-400 bg-red-500/10 border-red-500/30',
};

export const ApprovalQueue: React.FC<ApprovalQueueProps> = ({
  pendingActions,
  onApprove,
  onReject,
  onEdit,
  className,
}) => {
  const [expandedId, setExpandedId] = useState<string | null>(null);
  const [processingIds, setProcessingIds] = useState<Set<string>>(new Set());

  const handleApprove = async (actionId: string) => {
    setProcessingIds(prev => new Set(prev).add(actionId));
    try {
      await onApprove(actionId);
    } finally {
      setProcessingIds(prev => {
        const next = new Set(prev);
        next.delete(actionId);
        return next;
      });
    }
  };

  const handleReject = async (actionId: string) => {
    setProcessingIds(prev => new Set(prev).add(actionId));
    try {
      await onReject(actionId);
    } finally {
      setProcessingIds(prev => {
        const next = new Set(prev);
        next.delete(actionId);
        return next;
      });
    }
  };

  const toggleExpand = (actionId: string) => {
    setExpandedId(prev => prev === actionId ? null : actionId);
  };

  if (pendingActions.length === 0) {
    return (
      <div className={cn(
        "flex flex-col items-center justify-center p-8 rounded-xl border border-slate-700/50 bg-slate-900/30",
        className
      )}>
        <div className="w-12 h-12 rounded-full bg-green-500/10 flex items-center justify-center mb-4">
          <Check className="w-6 h-6 text-green-400" />
        </div>
        <p className="text-slate-400 text-sm">No pending actions</p>
        <p className="text-slate-500 text-xs mt-1">All AI-generated actions have been reviewed</p>
      </div>
    );
  }

  return (
    <div className={cn("space-y-3", className)}>
      <div className="flex items-center justify-between mb-4">
        <h3 className="text-lg font-semibold text-slate-200 flex items-center gap-2">
          <Clock className="w-5 h-5 text-amber-400" />
          Pending Approval
          <span className="ml-2 px-2 py-0.5 bg-amber-500/20 text-amber-300 text-xs rounded-full">
            {pendingActions.length}
          </span>
        </h3>
      </div>

      <div className="space-y-2">
        {pendingActions.map((action) => {
          const Icon = ACTION_ICONS[action.type];
          const isExpanded = expandedId === action.id;
          const isProcessing = processingIds.has(action.id);

          return (
            <div
              key={action.id}
              className={cn(
                "rounded-lg border bg-slate-900/60 backdrop-blur-sm transition-all duration-200",
                RISK_COLORS[action.riskLevel],
                isProcessing && "opacity-50 pointer-events-none"
              )}
            >
              {/* Header Row */}
              <div
                className="flex items-center gap-3 p-4 cursor-pointer"
                onClick={() => toggleExpand(action.id)}
              >
                <div className={cn(
                  "w-10 h-10 rounded-lg flex items-center justify-center",
                  action.riskLevel === 'high' ? 'bg-red-500/20' : 'bg-slate-800'
                )}>
                  <Icon className="w-5 h-5" />
                </div>

                <div className="flex-1 min-w-0">
                  <p className="text-sm font-medium text-slate-200 truncate">{sanitizeInput(action.title, { maxLength: 200 })}</p>
                  <p className="text-xs text-slate-400 truncate">{sanitizeInput(action.sourceContext, { maxLength: 200 })}</p>
                </div>

                <div className="flex items-center gap-2">
                  {/* Risk Badge */}
                  <span className={cn(
                    "px-2 py-0.5 text-xs rounded-full capitalize",
                    RISK_COLORS[action.riskLevel]
                  )}>
                    {action.riskLevel}
                  </span>

                  {/* Confidence */}
                  <span className="text-xs text-slate-500">
                    {action.aiConfidence}% confident
                  </span>

                  {/* Expand Toggle */}
                  {isExpanded ? (
                    <ChevronUp className="w-4 h-4 text-slate-400" />
                  ) : (
                    <ChevronDown className="w-4 h-4 text-slate-400" />
                  )}
                </div>
              </div>

              {/* Expanded Details */}
              {isExpanded && (
                <div className="px-4 pb-4 space-y-4 border-t border-slate-700/50">
                  <div className="pt-4">
                    <p className="text-sm text-slate-300 whitespace-pre-wrap">{sanitizeInput(action.description, { maxLength: 5000 })}</p>
                  </div>

                  {/* Time Info */}
                  <div className="flex items-center gap-2 text-xs text-slate-500">
                    <Clock className="w-3 h-3" />
                    Created {action.createdAt.toLocaleString()}
                  </div>

                  {/* Action Buttons */}
                  <div className="flex items-center gap-2 pt-2">
                    <Button
                      onClick={(e) => {
                        e.stopPropagation();
                        handleApprove(action.id);
                      }}
                      disabled={isProcessing}
                      className="flex-1 bg-green-600 hover:bg-green-500 text-white gap-2"
                      size="sm"
                    >
                      <Check className="w-4 h-4" />
                      Approve & Execute
                    </Button>

                    {onEdit && (
                      <Button
                        onClick={(e) => {
                          e.stopPropagation();
                          onEdit(action.id);
                        }}
                        disabled={isProcessing}
                        variant="outline"
                        size="sm"
                        className="border-slate-600 text-slate-300 hover:bg-slate-800"
                      >
                        Edit
                      </Button>
                    )}

                    <Button
                      onClick={(e) => {
                        e.stopPropagation();
                        handleReject(action.id);
                      }}
                      disabled={isProcessing}
                      variant="ghost"
                      size="sm"
                      className="text-red-400 hover:text-red-300 hover:bg-red-500/10"
                    >
                      <X className="w-4 h-4" />
                      Reject
                    </Button>
                  </div>

                  {/* Security Warning for High Risk */}
                  {action.riskLevel === 'high' && (
                    <div className="flex items-start gap-2 p-3 bg-red-500/10 rounded-lg border border-red-500/30">
                      <AlertTriangle className="w-4 h-4 text-red-400 mt-0.5 flex-shrink-0" />
                      <p className="text-xs text-red-300">
                        <strong>Security Notice:</strong> This action was generated from external content.
                        Please review carefully before approving.
                      </p>
                    </div>
                  )}
                </div>
              )}
            </div>
          );
        })}
      </div>
    </div>
  );
};

