import { AsyncBaseBridge } from '../AsyncBaseBridge';
import { BridgeConfig } from '../BaseBridge';
import {
  AldenPersonaProps,
  AldenPersonaMemory,
  AldenTraits,
  CorrectionEvent,
  Task,
  CreateTaskRequest,
  TaskUpdates,
  CalendarEvent,
  CreateEventRequest,
  EventUpdates,
  ChatMessage,
