export class AldenBridge extends AsyncBaseBridge<AldenPersonaProps> {
  private personaMemory: AldenPersonaMemory | null = null;
  private tasks: Task[] = [];
  private tasksLoaded: boolean = false;

  constructor(config?: Partial<BridgeConfig>) {
    super({
      agentId: 'alden',
      ...config,
    });
  }

  /**
   * Override getMockResponse to provide Alden-specific mock data for browser mode
   */
  protected getMockResponse<T>(command: AgentCommand, params?: any): T {
    const mock = getAldenMockResponse<T>(command, params, this.userId);
    if (mock !== null) return mock;
    return super.getMockResponse<T>(command, params);
  }

  /**
   * Initialize Alden bridge - sets up persona memory only (no IPC calls)
   */
  protected async onInitialize(): Promise<void> {
    this.personaMemory = {
      persona_id: 'alden',
      user_id: this.userId || 'default_user',
      schema_version: '1.0.0',
      timestamp: new Date().toISOString(),
      traits: {
        openness: 75,
        conscientiousness: 80,
        extraversion: 65,
        agreeableness: 85,
        emotional_stability: 70,
      },
      motivation_style: 'supportive',
      trust_level: 1.0,
      feedback_score: 0,
      learning_agility: 0,
      reflective_capacity: 0,
      habit_consistency: 0,
      engagement: 0,
      correction_events: [],
      session_mood: [],
      relationship_log: [],
      user_tags: [],
      provenance: {
        origin: 'system',
        action: 'initialization',
      },
      last_modified_by: 'system',
      editable_fields: ['traits', 'motivation_style', 'user_tags'],
      audit_log: [],
    };

    this.tasks = [];
    this.tasksLoaded = false;
  }

  /**
   * Cleanup resources
   */
  protected async onDestroy(): Promise<void> {
    this.personaMemory = null;
    this.tasks = [];
    this.tasksLoaded = false;
  }

  /**
   * Get props for Alden React component
   */
  async getProps(): Promise<AldenPersonaProps> {
    if (!this.personaMemory) {
      throw new Error('Persona memory not loaded');
    }

    if (!this.tasksLoaded) {
      try {
        await this.loadTasks();
        this.tasksLoaded = true;
      } catch (error) {
        console.warn('[AldenBridge] Failed to load tasks, continuing with empty list:', error);
        this.tasks = [];
      }
    }

    return {
      personaId: this.personaMemory.persona_id,
      traits: this.personaMemory.traits,
      motivationStyle: this.personaMemory.motivation_style,
      trustLevel: this.personaMemory.trust_level,
      feedbackScore: this.personaMemory.feedback_score,
      habitConsistency: this.personaMemory.habit_consistency,
      onTraitUpdate: this.updateTraits.bind(this),
      onExport: this.exportPersonaMemory.bind(this),
      onCorrection: this.recordCorrection.bind(this),
      auditLog: this.personaMemory.audit_log,
    };
  }

  // ============================================================================
  // Persona Management
  // ============================================================================

  async updateTraits(traits: Partial<AldenTraits>): Promise<BridgeResponse<AldenPersonaMemory>> {
    try {
      await this.invokeCommand('alden_update_trait', { traits });
      if (this.personaMemory) {
        this.personaMemory.traits = { ...this.personaMemory.traits, ...traits };
      }
      this.notifySubscribers({ type: 'traits_updated', data: traits });
      return { success: true, data: this.personaMemory! };
    } catch (error: any) {
      return { success: false, error: error.message || 'Failed to update traits', code: 'TRAIT_UPDATE_ERROR' };
    }
  }

  async recordCorrection(correction: CorrectionEvent): Promise<BridgeResponse<void>> {
    try {
      await this.invokeCommand('alden_record_correction', { correction });
      if (this.personaMemory) {
        this.personaMemory.correction_events.push(correction);
      }
      this.notifySubscribers({ type: 'correction_recorded', data: correction });
      return { success: true, data: undefined };
    } catch (error: any) {
      if (this.personaMemory) {
        this.personaMemory.correction_events.push(correction);
      }
      return { success: true, data: undefined };
    }
  }

  async exportPersonaMemory(): Promise<BridgeResponse<AldenPersonaMemory>> {
    try {
      const exported = await this.invokeCommand<string>('alden_export_memory', {});
      this.notifySubscribers({ type: 'persona_exported', data: exported });
      return { success: true, data: this.personaMemory! };
    } catch (error: any) {
      return { success: false, error: error.message || 'Failed to export persona memory', code: 'EXPORT_ERROR' };
    }
  }

  // ============================================================================
  // Task Management
  // ============================================================================

  private async loadTasks(): Promise<void> {
    try {
      const request = { userId: this.userId || 'default_user' };
      this.tasks = await this.invokeCommand<Task[]>('alden_tasks_list', { request });
    } catch (error) {
      console.error('[AldenBridge.loadTasks] Failed to load tasks:', error);
      this.tasks = [];
    }
  }

  async getTasks(): Promise<BridgeResponse<Task[]>> {
    try {
      await this.loadTasks();
      return { success: true, data: this.tasks };
    } catch (error: any) {
      return { success: false, error: error.message || 'Failed to fetch tasks', code: 'TASK_FETCH_ERROR' };
    }
  }

  async createTask(task: CreateTaskRequest): Promise<BridgeResponse<Task>> {
    try {
      const request = { ...task, userId: task.userId || this.userId || 'default_user' };
      const created = await this.invokeCommand<Task>('alden_tasks_create', { request });
      this.tasks.push(created);
      this.notifySubscribers({ type: 'task_created', data: created });
      return { success: true, data: created };
    } catch (error: any) {
      return { success: false, error: error.message || 'Failed to create task', code: 'TASK_CREATE_ERROR' };
    }
  }

  async updateTask(taskId: string, updates: TaskUpdates): Promise<BridgeResponse<Task>> {
    try {
      const request = { userId: this.userId || 'default_user', taskId, ...updates };
      const updated = await this.invokeCommand<Task>('alden_tasks_update', { request });
      const index = this.tasks.findIndex(t => t.id === taskId);
      if (index !== -1) this.tasks[index] = updated;
      this.notifySubscribers({ type: 'task_updated', data: updated });
      return { success: true, data: updated };
    } catch (error: any) {
      return { success: false, error: error.message || 'Failed to update task', code: 'TASK_UPDATE_ERROR' };
    }
  }

  async deleteTask(taskId: string): Promise<BridgeResponse<void>> {
    try {
      const request = { userId: this.userId || 'default_user', taskId };
