/**
 * Async Base Bridge Implementation
 *
 * Extends BaseBridge with:
 * - Parallel command execution
 * - Command deduplication
 * - Request queuing
 * - Timeout handling
 *
 * @version 1.0.0
 * @date 2025-12-11
 */

import { invoke } from '@tauri-apps/api/tauri';
import { BaseBridge, BridgeConfig } from './BaseBridge';
import { AgentCommand } from '../types/agents';

type JsonObject = Record<string, unknown>;

export abstract class AsyncBaseBridge<TProps = unknown> extends BaseBridge<TProps> {
  private commandQueue: Map<string, Promise<any>> = new Map();

  constructor(config: BridgeConfig) {
    super(config);
  }

  /**
   * Invoke a command with parallel execution support and deduplication
   * @param command - The command to execute
   * @param params - Parameters for the command
   * @param priority - Priority level (currently reserved for future scheduler)
   */
  protected async invokeCommandParallel<T = unknown>(
    command: AgentCommand,
    params?: JsonObject,
    priority: 'low' | 'normal' | 'high' = 'normal'
  ): Promise<T> {
    const commandKey = this.generateCommandKey(command, params);

    // Check if already in progress (Deduplication)
    if (this.commandQueue.has(commandKey)) {
      // console.debug(`[AsyncBridge] Deduplicating command: ${commandKey}`);
      return this.commandQueue.get(commandKey);
    }

    // Create execution promise
    const promise = this.executeCommandWithTimeout<T>(command, params || {}, 30000);
    
    // Store in queue
    this.commandQueue.set(commandKey, promise);

    // Cleanup after completion (success or failure)
    promise.finally(() => {
      this.commandQueue.delete(commandKey);
    });

    return promise;
  }

  /**
   * Execute command with timeout wrapper
   */
  private async executeCommandWithTimeout<T>(
    command: AgentCommand,
    params: JsonObject,
    timeoutMs: number
  ): Promise<T> {
    
    // We reuse the parent's invokeCommand logic because it handles:
    // - Browser Mode limitations (Mock data)
    // - Logging
    // - Error parsing
    // But we wrap it in a race with a timeout
    
    const execution = super.invokeCommand<T>(command, params);

    const timeout = new Promise<never>((_, reject) => {
      setTimeout(() => reject(new Error(`Command timed out after ${timeoutMs}ms: ${command}`)), timeoutMs);
    });

    return Promise.race([execution, timeout]);
  }

  /**
   * Generate a unique key for the command + params combination
   */
  private generateCommandKey(command: string, params?: JsonObject): string {
    if (!params || Object.keys(params).length === 0) {
      return command;
    }
    // Simple deterministic serialization for basic params
    // For complex objects, this might need a more robust stable-stringify, 
    // but JSON.stringify is sufficient for 99% of IPC calls.
    return `${command}:${JSON.stringify(params)}`;
  }
}

