/**
 * Alice Agent Bridge Implementation - Tauri IPC Version
 *
 * Provides React/TypeScript bridge for Alice agent system using pure Tauri IPC.
 * This version uses invoke() calls instead of HTTP requests.
 *
 * Features:
 * - Behavioral profile analysis and management
 * - Insight generation and tracking
 * - Pattern recognition (communication, attention, emotional, learning)
 * - Context-aware recommendations
 *
 * @version 2.0.0
 * @date 2025-10-26
 */

import { invoke } from '@tauri-apps/api/tauri';
import { AsyncBaseBridge } from './AsyncBaseBridge';
import { BridgeConfig } from './BaseBridge';
import {
  AliceProps,
  AliceBehavioralProfile,
  AliceInsight,
  BridgeResponse,
  CommandRequest,
  JsonObject,
} from '../types/agents';

export type BehavioralObservation = {
  transcript: string;
  completed_tasks: number;
  total_tasks: number;
  interruptions: number;
  context_switches: number;
  sentiment: number; // -1.0 to 1.0
  self_reported_stress?: number; // 0-100
  self_reported_energy?: number; // 0-100
  focus_rating?: number; // 0-100
  task_complexity?: number; // 0-100
  completion_speed?: number; // tasks per hour or normalized speed
  notes?: string;
};

export type BehavioralSignal = {
  signal_id: string;
  user_id: string;
  focus: number;
  stress: number;
  engagement: number;
  cognitive_load: number;
  tags: string; // JSON array
  evidence: string; // JSON object
  created_at: string;
};

export type DeepWorkSession = {
  session_id: string;
  user_id: string;
  start_time: string;
  end_time?: string | null;
  duration_seconds?: number | null;
  goal?: string | null;
  status: 'active' | 'paused' | 'completed' | 'aborted';
  paused_at?: string | null;
  paused_total_seconds?: number | null;
  created_at: string;
};

type DistractionBlockState = {
  enabled: boolean;
  blocked_items: string[];
  applied_at: string;
  note: string;
};

type DeepWorkSessionResult = {
  session: DeepWorkSession;
  distraction_blocking: DistractionBlockState;
};

type DeepWorkStatus = {
  active: boolean;
  current_session?: DeepWorkSession | null;
  last_session?: DeepWorkSession | null;
  distraction_blocking: DistractionBlockState;
};

type DeepWorkAnalysis = {
  total_sessions: number;
  completed_sessions: number;
  aborted_sessions: number;
  active_sessions: number;
  average_session_duration_seconds: number;
  completion_rate: number;
  focus_score: number;
  stress_score: number;
  engagement_score: number;
};

type DeepWorkSessionStats = {
  status: DeepWorkStatus;
  analysis: DeepWorkAnalysis;
};

export class AliceBridge extends AsyncBaseBridge<AliceProps> {
  private profile: AliceBehavioralProfile | null = null;
  private insights: AliceInsight[] = [];

  constructor(config: Partial<BridgeConfig> & { userId: string }) {
    super({
      agentId: 'alice',
      ...config,
    });
    // Initialize with default profile immediately to prevent null errors
    this.profile = {
      user_id: this.userId || 'default_user',
      communication_style: 'analytical',
      preferred_interaction_times: ['09:00-12:00', '14:00-17:00'],
      attention_patterns: {
        focus_duration_avg: 45.0,
        distraction_triggers: ['notifications', 'background noise'],
        optimal_work_periods: ['morning', 'late afternoon'],
      },
      emotional_patterns: {
        stress_indicators: ['increased typing speed', 'shorter responses'],
        motivation_factors: ['achievement', 'recognition'],
        mood_trends: [],
      },
      learning_preferences: {
        preferred_formats: ['visual', 'interactive'],
        comprehension_pace: 'moderate',
        feedback_style: 'constructive',
      },
    };
  }

  /**
   * Get userId from auth context (if available)
   * Falls back to 'default_user' for backwards compatibility
   */
  private getUserId(): string {
    return this.userId || (window as unknown as { __hearthlink_userId?: string }).__hearthlink_userId || 'default_user';
  }

  /**
   * Helper to wrap requests with authentication token
   */
  private createAuthenticatedRequest<T>(payload: T): { auth_token: string; payload: T } {
    return {
      auth_token: `demo_token_${this.getUserId()}`,
      payload,
    };
  }

  /**
   * Initialize Alice bridge and load behavioral profile
   */
  protected async onInitialize(): Promise<void> {
    try {
      // Initialize with default profile
      this.profile = {
        user_id: this.userId || 'default_user',
        communication_style: 'analytical',
        preferred_interaction_times: ['09:00-12:00', '14:00-17:00'],
        attention_patterns: {
          focus_duration_avg: 45.0,
          distraction_triggers: ['notifications', 'background noise'],
          optimal_work_periods: ['morning', 'late afternoon'],
        },
        emotional_patterns: {
          stress_indicators: ['increased typing speed', 'shorter responses'],
          motivation_factors: ['achievement', 'recognition'],
          mood_trends: [
            { date: new Date('2024-01-01').toISOString(), mood: 'positive', score: 8.5 },
            { date: new Date('2024-01-02').toISOString(), mood: 'positive', score: 7.8 },
          ],
        },
        learning_preferences: {
          preferred_formats: ['visual', 'interactive'],
          comprehension_pace: 'moderate',
          feedback_style: 'constructive',
        },
      };

      // Initialize with empty insights
      this.insights = [];
    } catch (error) {
      console.error('[AliceBridge] Failed to initialize:', error);
      // Don't throw - allow degraded mode
      this.profile = {
        user_id: this.userId || 'default_user',
        communication_style: 'analytical',
        preferred_interaction_times: [],
        attention_patterns: {
          focus_duration_avg: 0,
          distraction_triggers: [],
          optimal_work_periods: [],
        },
        emotional_patterns: {
          stress_indicators: [],
          motivation_factors: [],
          mood_trends: [],
        },
        learning_preferences: {
          preferred_formats: [],
          comprehension_pace: 'moderate',
          feedback_style: 'constructive',
        },
      };
    }
  }

  /**
   * Cleanup resources
   */
  protected async onDestroy(): Promise<void> {
    this.profile = null;
    this.insights = [];
  }

  /**
   * Get props for Alice React component
   */
  async getProps(): Promise<AliceProps> {
    if (!this.profile) {
      console.warn('[AliceBridge.getProps] WARNING - Behavioral profile not loaded, using fallback');
      // Fallback to default profile to prevent UI crash
      this.profile = {
        user_id: this.userId || 'default_user',
        communication_style: 'analytical',
        preferred_interaction_times: [],
        attention_patterns: {
          focus_duration_avg: 45.0,
          distraction_triggers: [],
          optimal_work_periods: [],
        },
        emotional_patterns: {
          stress_indicators: [],
          motivation_factors: [],
          mood_trends: [],
        },
        learning_preferences: {
          preferred_formats: [],
          comprehension_pace: 'moderate',
          feedback_style: 'constructive',
        },
      };
    }

    const props = {
      profile: this.profile,
      insights: this.insights,
      onProfileUpdate: this.updateProfile.bind(this),
      onInsightDismiss: this.dismissInsight.bind(this),
    };

    console.log(`[AliceBridge:${this.agentId}] getProps returning props`);
    return props;
  }

  // ============================================================================
  // Profile Management
  // ============================================================================

  /**
   * Update behavioral profile
   */
  async updateProfile(
    updates: Partial<AliceBehavioralProfile>
  ): Promise<BridgeResponse<AliceBehavioralProfile>> {
    try {
      const request: CommandRequest = {
        user_id: this.userId, // Use this.userId
        communication_style: updates.communication_style,
        attention_patterns: updates.attention_patterns,
        emotional_patterns: updates.emotional_patterns,
        learning_preferences: updates.learning_preferences,
        // feedback_insights: updates.feedback_insights, // Removed this line
      };

      const updated = await invoke<AliceBehavioralProfile>('alice_update_profile', { request: this.createAuthenticatedRequest(request) });

      if (this.profile) {
        this.profile = { ...this.profile, ...updated };
      }

      this.notifySubscribers({ type: 'profile_updated', data: updated });

      if (!this.profile) {
        return { success: true, data: updated };
      }
      return { success: true, data: this.profile };
    } catch (error) {
      return this.createErrorResponse<AliceBehavioralProfile>(
        error,
        'Failed to update profile',
        'PROFILE_UPDATE_ERROR'
      );
    }
  }

  /**
   * Get behavioral profile
   */
  async getProfile(): Promise<BridgeResponse<AliceBehavioralProfile>> {
    try {
      const request = { userId: this.userId };
      const profile = await invoke<AliceBehavioralProfile>('alice_get_profile', {
        request: this.createAuthenticatedRequest(request),
      });

      this.profile = profile;
      return { success: true, data: this.profile };
    } catch (error) {
      return this.createErrorResponse<AliceBehavioralProfile>(
        error,
        'Failed to fetch profile',
        'PROFILE_FETCH_ERROR'
      );
    }
  }

  // ============================================================================
  // Insight Management
  // ============================================================================

  /**
   * Get all insights
   */
  async getInsights(
    category?: AliceInsight['category']
  ): Promise<BridgeResponse<AliceInsight[]>> {
    try {
      const request = { userId: this.userId, category };
      const insights = await invoke<AliceInsight[]>('alice_get_insights', {
        request: this.createAuthenticatedRequest(request),
      });

      this.insights = insights;
      return { success: true, data: this.insights };
    } catch (error) {
      return this.createErrorResponse<AliceInsight[]>(
        error,
        'Failed to fetch insights',
        'INSIGHT_FETCH_ERROR'
      );
    }
  }

  /**
   * Dismiss an insight
   */
  async dismissInsight(insightId: string): Promise<BridgeResponse<void>> {
    try {
      const request = { insightId };
      await invoke('alice_dismiss_insight', { request: this.createAuthenticatedRequest(request) });

      this.insights = this.insights.filter(i => i.insight_id !== insightId);
      this.notifySubscribers({ type: 'insight_dismissed', data: { insightId } });

      return { success: true, data: undefined };
    } catch (error) {
      return this.createErrorResponse<void>(
        error,
        'Failed to dismiss insight',
        'INSIGHT_DISMISS_ERROR'
      );
    }
  }

  /**
   * Request new insight generation
   */
  async generateInsights(context?: JsonObject): Promise<BridgeResponse<AliceInsight[]>> {
    try {
      const request: CommandRequest = {
        user_id: this.userId, // Use this.userId
        category: 'behavioral',
        title: 'Generated Insight',
        content: JSON.stringify(context || {}),
        confidence_score: 0.8,
      };

      const newInsights = await invoke<AliceInsight[]>('alice_create_insight', { request: this.createAuthenticatedRequest(request) });

      this.insights.push(...newInsights);
      this.notifySubscribers({ type: 'insights_generated', data: newInsights });

      return { success: true, data: newInsights };
    } catch (error) {
      return this.createErrorResponse<AliceInsight[]>(
        error,
        'Failed to generate insights',
        'INSIGHT_GENERATE_ERROR'
      );
    }
  }

  // ============================================================================
  // Pattern Analysis
  // ============================================================================

  /**
   * Analyze communication patterns
   */
  async analyzeCommunicationPatterns(): Promise<BridgeResponse<{
    style: string;
    preferred_times: string[];
    suggestions: string[];
  }>> {
    try {
      const request: CommandRequest = {
        user_id: this.userId, // Use this.userId
        analysis_type: 'communication',
        time_range: 'last_30_days',
      };

      const analysis = await invoke<{
        style: string;
        preferred_times: string[];
        suggestions: string[];
      }>('alice_analyze_patterns', { request: this.createAuthenticatedRequest(request) });

      this.notifySubscribers({ type: 'communication_analyzed', data: analysis });

      return { success: true, data: analysis };
    } catch (error) {
      return this.createErrorResponse(
        error,
        'Failed to analyze communication patterns',
        'COMMUNICATION_ANALYSIS_ERROR'
      );
    }
  }

  /**
   * Analyze attention patterns
   */
  async analyzeAttentionPatterns(): Promise<BridgeResponse<{
    focus_duration_avg: number;
    distraction_triggers: string[];
    optimal_work_periods: string[];
    recommendations: string[];
  }>> {
    try {
      const request: CommandRequest = {
        user_id: this.userId, // Use this.userId
        analysis_type: 'attention',
        time_range: 'last_30_days',
      };

      const analysis = await invoke<{
        focus_duration_avg: number;
        distraction_triggers: string[];
        optimal_work_periods: string[];
        recommendations: string[];
      }>('alice_analyze_patterns', { request: this.createAuthenticatedRequest(request) });

      this.notifySubscribers({ type: 'attention_analyzed', data: analysis });

      return { success: true, data: analysis };
    } catch (error) {
      return this.createErrorResponse(
        error,
        'Failed to analyze attention patterns',
        'ATTENTION_ANALYSIS_ERROR'
      );
    }
  }

  /**
   * Analyze emotional patterns
   */
  async analyzeEmotionalPatterns(): Promise<BridgeResponse<{
    stress_indicators: string[];
    motivation_factors: string[];
    mood_trends: Array<{ date: Date; mood: string; score: number }>;
    recommendations: string[];
  }>> {
    try {
      const request: CommandRequest = {
        user_id: this.userId, // Use this.userId
        analysis_type: 'emotional',
        time_range: 'last_30_days',
      };

      const analysis = await invoke<{
        stress_indicators: string[];
        motivation_factors: string[];
        mood_trends: Array<{ date: Date; mood: string; score: number }>;
        recommendations: string[];
      }>('alice_analyze_patterns', { request: this.createAuthenticatedRequest(request) });

      this.notifySubscribers({ type: 'emotional_analyzed', data: analysis });

      return { success: true, data: analysis };
    } catch (error) {
      return this.createErrorResponse(
        error,
        'Failed to analyze emotional patterns',
        'EMOTIONAL_ANALYSIS_ERROR'
      );
    }
  }

  /**
   * Get learning preferences analysis
   */
  async analyzeLearningPreferences(): Promise<BridgeResponse<{
    preferred_formats: string[];
    comprehension_pace: 'slow' | 'moderate' | 'fast';
    feedback_style: string;
    recommendations: string[];
  }>> {
    try {
      const request: CommandRequest = {
        user_id: this.userId, // Use this.userId
        analysis_type: 'learning',
        time_range: 'last_30_days',
      };

      const analysis = await invoke<{
        preferred_formats: string[];
        comprehension_pace: 'slow' | 'moderate' | 'fast';
        feedback_style: string;
        recommendations: string[];
      }>('alice_analyze_patterns', { request: this.createAuthenticatedRequest(request) });

      this.notifySubscribers({ type: 'learning_analyzed', data: analysis });

      return { success: true, data: analysis };
    } catch (error) {
      return this.createErrorResponse(
        error,
        'Failed to analyze learning preferences',
        'LEARNING_ANALYSIS_ERROR'
      );
    }
  }

  // ============================================================================
  // Context-Aware Recommendations
  // ============================================================================

  /**
   * Get context-aware recommendations based on current state
   */
  async getRecommendations(context: {
    current_activity?: string;
    time_of_day?: string;
    recent_mood?: string;
    energy_level?: number;
  }): Promise<BridgeResponse<{
    recommendations: Array<{
      category: string;
      suggestion: string;
      priority: 'low' | 'medium' | 'high';
      reasoning: string;
    }>;
  }>> {
    try {
      const request: CommandRequest = {
        user_id: this.userId, // Use this.userId
        current_activity: context.current_activity,
        time_of_day: context.time_of_day,
        recent_mood: context.recent_mood,
        energy_level: context.energy_level,
      };

      const recommendations = await invoke<{
        recommendations: Array<{
          category: string;
          suggestion: string;
          priority: 'low' | 'medium' | 'high';
          reasoning: string;
        }>;
      }>('alice_get_recommendations', { request: this.createAuthenticatedRequest(request) });

      this.notifySubscribers({ type: 'recommendations_generated', data: recommendations });

      return { success: true, data: recommendations };
    } catch (error) {
      return this.createErrorResponse(
        error,
        'Failed to get recommendations',
        'RECOMMENDATIONS_ERROR'
      );
    }
  }

  // ============================================================================
  // Deep Work Session Management
  // ============================================================================

  /**
   * Start a new Deep Work session
   */
  async startDeepWork(goal?: string): Promise<BridgeResponse<DeepWorkSessionResult>> {
    try {
      const request: CommandRequest = {
        user_id: this.userId,
        userId: this.userId,
        goal,
      };

      const session = await invoke<DeepWorkSessionResult>('alice_start_deep_work_session', {
        request: this.createAuthenticatedRequest(request),
      });
      this.notifySubscribers({ type: 'deep_work_started', data: session });
      return { success: true, data: session };
    } catch (error) {
      return this.createErrorResponse(
        error,
        'Failed to start Deep Work session',
        'DEEP_WORK_START_ERROR'
      );
    }
  }

  /**
   * Stop the current Deep Work session
   */
  async stopDeepWork(
    status: 'completed' | 'aborted' = 'completed'
  ): Promise<BridgeResponse<DeepWorkSessionResult>> {
    try {
      const request: CommandRequest = {
        user_id: this.userId,
        userId: this.userId,
        status,
      };

      const session = await invoke<DeepWorkSessionResult>('alice_stop_deep_work_session', {
        request: this.createAuthenticatedRequest(request),
      });
      this.notifySubscribers({ type: 'deep_work_stopped', data: session });
      return { success: true, data: session };
    } catch (error) {
      return this.createErrorResponse(
        error,
        'Failed to stop Deep Work session',
        'DEEP_WORK_STOP_ERROR'
      );
    }
  }

  /**
   * Get Deep Work session history
   */
  async getDeepWorkHistory(limit: number = 20): Promise<BridgeResponse<DeepWorkSession[]>> {
    try {
      const request: CommandRequest = {
        user_id: this.userId,
        userId: this.userId,
        limit,
      };

      const history = await invoke<DeepWorkSession[]>('alice_get_deep_work_history', { request: this.createAuthenticatedRequest(request) });
      return { success: true, data: history };
    } catch (error) {
      return this.createErrorResponse(
        error,
        'Failed to get Deep Work history',
        'DEEP_WORK_HISTORY_ERROR'
      );
    }
  }
  /**
   * Get full Deep Work analysis
   */
  async getDeepWorkAnalysis(): Promise<BridgeResponse<any>> {
    try {
      const request: CommandRequest = {
        user_id: this.userId,
        analysis_type: 'general',
        time_range: 'last_30_days',
