/**
 * Mimic Agent Bridge Implementation - Tauri IPC Version
 *
 * Provides React/TypeScript bridge for Mimic agent system using pure Tauri IPC.
 * This version uses invoke() calls instead of HTTP requests.
 *
 * Features:
 * - Dynamic persona management
 * - Persona switching
 * - Adaptive behavior
 * - Context-based adaptations
 *
 * @version 2.0.0
 * @date 2025-10-26
 */

import { AsyncBaseBridge } from './AsyncBaseBridge';
import { BridgeConfig } from './BaseBridge';
import {
  MimicProps,
  MimicPersona,
  MimicAdaptation,
  BridgeResponse,
  CommandRequest,
  MimicChatMessage,
} from '../types/agents';

export class MimicBridge extends AsyncBaseBridge<MimicProps> {
  private activePersona: MimicPersona | null = null;
  private availablePersonas: MimicPersona[] = [];
  private adaptations: MimicAdaptation[] = [];
  private dataLoaded = false;

  constructor(config: Partial<BridgeConfig> & { userId: string }) {
    super({
      agentId: 'mimic',
      ...config,
    });
  }

  /**
   * Initialize Mimic bridge - sets up initial state only (no IPC calls)
   *
   * IMPORTANT: No Tauri IPC calls should be made here to avoid deadlock.
   * IPC calls are deferred until after component mount via lazy loading in getProps().
   */
  protected async onInitialize(): Promise<void> {
    console.log('[MimicBridge.onInitialize] START - Initializing Mimic bridge');

    // Initialize with empty state - data will be loaded lazily
    this.activePersona = null;
    this.availablePersonas = [];
    this.adaptations = [];
    this.dataLoaded = false;

    console.log('[MimicBridge.onInitialize] END - Initialized successfully (data will load lazily)');
  }

  /**
   * Cleanup resources
   */
  protected async onDestroy(): Promise<void> {
    this.activePersona = null;
    this.availablePersonas = [];
    this.adaptations = [];
    this.dataLoaded = false;
  }

  /**
   * Get props for Mimic React component
   *
   * Performs lazy loading of persona data on first call after component mount.
   * This ensures Tauri IPC is ready and avoids initialization deadlock.
   */
  async getProps(): Promise<MimicProps> {
    console.log('[MimicBridge.getProps] START - Getting props for React component');

    // Lazy load persona data on first call (safe to use IPC after mount)
    if (!this.dataLoaded) {
      console.log('[MimicBridge.getProps] Lazy loading persona data (first call after mount)');
      try {
        const request = { userId: this.userId, user_id: this.userId };

        // Load all available personas
        this.availablePersonas = await this.invokeCommand<MimicPersona[]>('mimic_get_personas', { request });

        // Load active persona
        const activePersonaData = await this.invokeCommand<{ persona: MimicPersona }>('mimic_get_active_persona', { request });
        this.activePersona = activePersonaData.persona || null;

        // Load recent adaptations
        if (this.activePersona) {
          const adaptationRequest = {
            userId: this.userId, user_id: this.userId,
            persona_id: this.activePersona.persona_id,
          };
          this.adaptations = await this.invokeCommand<MimicAdaptation[]>('mimic_get_adaptations', { request: adaptationRequest });
        }

        this.dataLoaded = true;
        console.log('[MimicBridge.getProps] Persona data loaded successfully');
      } catch (error) {
        console.warn('[MimicBridge.getProps] Failed to load persona data, continuing with empty state:', error);
        // Don't throw - allow degraded mode
        this.activePersona = null;
        this.availablePersonas = [];
        this.adaptations = [];
      }
    }

    console.log('[MimicBridge.getProps] activePersona exists:', !!this.activePersona);

    if (!this.activePersona) {
      console.error('[MimicBridge.getProps] ERROR - Active persona not loaded');
      throw new Error('Active persona not loaded');
    }

    const props = {
      activePersona: this.activePersona,
      availablePersonas: this.availablePersonas,
      adaptations: this.adaptations,
      onPersonaSwitch: this.switchPersona.bind(this),
      onPersonaCreate: this.createPersona.bind(this),
      onAdaptationApply: this.applyAdaptation.bind(this),
    };

    console.log('[MimicBridge.getProps] END - Returning props successfully');
    return props;
  }

  // ============================================================================
  // Persona Management
  // ============================================================================

  /**
   * Get all available personas
   */
  async getPersonas(): Promise<BridgeResponse<MimicPersona[]>> {
    try {
      const request: CommandRequest = { userId: this.userId, user_id: this.userId };
      this.availablePersonas = await this.invokeCommand<MimicPersona[]>('mimic_get_personas', { request });
      return { success: true, data: this.availablePersonas };
    } catch (error) {
      return this.createErrorResponse<MimicPersona[]>(
        error,
        'Failed to fetch personas',
        'PERSONA_FETCH_ERROR'
      );
    }
  }

  /**
   * Get active persona
   */
  async getActivePersona(): Promise<BridgeResponse<MimicPersona>> {
    try {
      const request: CommandRequest = { userId: this.userId, user_id: this.userId };
      const response = await this.invokeCommand<{ persona: MimicPersona }>('mimic_get_active_persona', { request });
      this.activePersona = response.persona || null;
      return this.activePersona ? { success: true, data: this.activePersona } : this.createErrorResponse<MimicPersona>(
        new Error('Active persona missing'),
        'Failed to fetch active persona',
        'ACTIVE_PERSONA_ERROR'
      );
    } catch (error) {
      return this.createErrorResponse<MimicPersona>(
        error,
        'Failed to fetch active persona',
        'ACTIVE_PERSONA_ERROR'
      );
    }
  }

  /**
   * Switch to a different persona
   */
  async switchPersona(personaId: string): Promise<BridgeResponse<MimicPersona>> {
    try {
      const request: CommandRequest = {
        userId: this.userId, user_id: this.userId,
        personaId,
      };
      const response = await this.invokeCommand<{ persona: MimicPersona }>('mimic_switch_persona', { request });

      this.activePersona = response.persona;
      this.notifySubscribers({ type: 'persona_switched', data: response.persona });

      return { success: true, data: response.persona };
    } catch (error) {
      return this.createErrorResponse<MimicPersona>(
        error,
        'Failed to switch persona',
        'PERSONA_SWITCH_ERROR'
      );
    }
  }

  /**
   * Create a new persona
   */
  async createPersona(
    persona: Omit<MimicPersona, 'persona_id' | 'created_at'>
  ): Promise<BridgeResponse<MimicPersona>> {
    try {
      const request: CommandRequest = {
        userId: this.userId, user_id: this.userId,
        ...persona,
      };

      const created = await this.invokeCommand<MimicPersona>('mimic_create_persona', { request });

      this.availablePersonas.push(created);
      this.notifySubscribers({ type: 'persona_created', data: created });

      return { success: true, data: created };
    } catch (error) {
      return this.createErrorResponse<MimicPersona>(
        error,
        'Failed to create persona',
        'PERSONA_CREATE_ERROR'
      );
    }
  }

  /**
   * Update a persona
   */
  async updatePersona(
    personaId: string,
    updates: Partial<Omit<MimicPersona, 'persona_id' | 'created_at'>>
  ): Promise<BridgeResponse<MimicPersona>> {
    try {
      const request: CommandRequest = {
        personaId,
        userId: this.userId, user_id: this.userId,
        ...updates,
      };

      const updated = await this.invokeCommand<MimicPersona>('mimic_update_persona', { request });

      const index = this.availablePersonas.findIndex(p => p.persona_id === personaId);
      if (index !== -1) {
        this.availablePersonas[index] = updated;
      }

      if (this.activePersona?.persona_id === personaId) {
        this.activePersona = updated;
      }

      this.notifySubscribers({ type: 'persona_updated', data: updated });

      return { success: true, data: updated };
    } catch (error) {
      return this.createErrorResponse<MimicPersona>(
        error,
        'Failed to update persona',
        'PERSONA_UPDATE_ERROR'
      );
    }
  }

  /**
   * Delete a persona
   */
  async deletePersona(personaId: string): Promise<BridgeResponse<void>> {
    try {
      const request: CommandRequest = {
        userId: this.userId, user_id: this.userId,
        personaId,
      };
      await this.invokeCommand('mimic_delete_persona', { request });

      this.availablePersonas = this.availablePersonas.filter(p => p.persona_id !== personaId);
      this.notifySubscribers({ type: 'persona_deleted', data: { personaId } });

      return { success: true, data: undefined };
    } catch (error) {
      return this.createErrorResponse<void>(
        error,
        'Failed to delete persona',
        'PERSONA_DELETE_ERROR'
      );
    }
  }

  // ============================================================================
  // Adaptation Management
  // ============================================================================

  /**
   * Get all adaptations
   */
  async getAdaptations(): Promise<BridgeResponse<MimicAdaptation[]>> {
    try {
      if (!this.activePersona) {
        return { success: true, data: [] };
      }

      const request: CommandRequest = {
        userId: this.userId, user_id: this.userId,
        persona_id: this.activePersona.persona_id,
      };

      this.adaptations = await this.invokeCommand<MimicAdaptation[]>('mimic_get_adaptations', { request });
      return { success: true, data: this.adaptations };
    } catch (error) {
      return this.createErrorResponse<MimicAdaptation[]>(
        error,
        'Failed to fetch adaptations',
        'ADAPTATION_FETCH_ERROR'
      );
    }
  }

  /**
   * Apply an adaptation to the current persona
   */
  async applyAdaptation(adaptation: MimicAdaptation): Promise<BridgeResponse<MimicPersona>> {
    try {
      if (!this.activePersona) {
        return {
          success: false,
          error: 'No active persona',
          code: 'NO_ACTIVE_PERSONA',
        };
      }

      const request: CommandRequest = {
        userId: this.userId, user_id: this.userId,
        persona_id: this.activePersona.persona_id,
        context: adaptation.context,
        changes: adaptation.changes,
      };

      const response = await this.invokeCommand<{ persona: MimicPersona }>('mimic_apply_adaptation', { request });

      this.activePersona = response.persona;
      this.adaptations.push(adaptation);
      this.notifySubscribers({ type: 'adaptation_applied', data: response.persona });

      return { success: true, data: response.persona };
    } catch (error) {
      return {
        success: false,
        error: error instanceof Error ? error.message : 'Failed to apply adaptation',
        code: 'ADAPTATION_APPLY_ERROR',
      };
    }
  }

  /**
   * Request context-based adaptation suggestions
   */
  async suggestAdaptations(context: string): Promise<BridgeResponse<MimicAdaptation[]>> {
    try {
      const request: CommandRequest = {
        userId: this.userId, user_id: this.userId,
        context,
      };

      const suggestions = await this.invokeCommand<MimicAdaptation[]>('mimic_suggest_adaptations', { request });

      this.notifySubscribers({ type: 'adaptations_suggested', data: suggestions });

      return { success: true, data: suggestions };
    } catch (error) {
      return {
        success: false,
        error: error instanceof Error ? error.message : 'Failed to get adaptation suggestions',
        code: 'ADAPTATION_SUGGEST_ERROR',
      };
    }
  }

  /**
   * Rate the effectiveness of an adaptation
   */
  async rateAdaptation(
    adaptationId: string,
    effectivenessScore: number
  ): Promise<BridgeResponse<MimicAdaptation>> {
    try {
      const request: CommandRequest = {
        userId: this.userId, user_id: this.userId,
        adaptationId,
        effectivenessScore,
      };

      const updated = await this.invokeCommand<MimicAdaptation>('mimic_rate_adaptation', { request });

      const index = this.adaptations.findIndex(a => a.adaptation_id === adaptationId);
      if (index !== -1) {
        this.adaptations[index] = updated;
      }

      this.notifySubscribers({ type: 'adaptation_rated', data: updated });

      return { success: true, data: updated };
    } catch (error) {
      return {
        success: false,
        error: error instanceof Error ? error.message : 'Failed to rate adaptation',
        code: 'ADAPTATION_RATE_ERROR',
      };
    }
  }

  // ============================================================================
  // Context Analysis
  // ============================================================================

  /**
   * Analyze context and recommend best persona
   */
  async analyzeContext(context: {
    task_type?: string;
    user_mood?: string;
    time_of_day?: string;
    complexity_level?: 'low' | 'medium' | 'high';
  }): Promise<BridgeResponse<{
    recommended_persona: MimicPersona;
    reasoning: string;
    confidence: number;
  }>> {
    try {
      const request: CommandRequest = {
        userId: this.userId, user_id: this.userId,
        ...context,
      };

      const analysis = await this.invokeCommand('mimic_analyze_context', { request }) as {
        recommended_persona: MimicPersona;
        reasoning: string;
        confidence: number;
      };

      this.notifySubscribers({ type: 'context_analyzed', data: analysis });

      return { success: true, data: analysis };
    } catch (error) {
      return {
        success: false,
        error: error instanceof Error ? error.message : 'Failed to analyze context',
        code: 'CONTEXT_ANALYSIS_ERROR',
      };
    }
  }

  // ============================================================================
  // Rich chat feed for UI (derived from adaptations)
  // ============================================================================

  async getChatFeed(): Promise<BridgeResponse<MimicChatMessage[]>> {
    try {
      const feed: MimicChatMessage[] = this.adaptations.map((adaptation) => ({
        id: adaptation.adaptation_id || `adapt-${adaptation.persona_id ?? 'unknown'}-${adaptation.created_at ?? adaptation.timestamp ?? ''}`,
        persona_id: adaptation.persona_id ?? 'mimic',
        role: 'mimic',
        content: adaptation.description || adaptation.adaptation || 'Adaptation update',
        created_at: adaptation.created_at || adaptation.timestamp || new Date().toISOString(),
        metadata: {
          strategy: adaptation.strategy ?? '',
          confidence: adaptation.confidence ?? 0,
          status: adaptation.status ?? '',
        },
      }));
      return { success: true, data: feed };
    } catch (error) {
      return this.createErrorResponse<MimicChatMessage[]>(
        error,
        'Failed to build Mimic chat feed',
        'MIMIC_CHAT_FEED_ERROR'
      );
    }
  }
}

