const slugify = (value: string): string =>
  value
    .toLowerCase()
    .trim()
    .replace(/[^a-z0-9]+/g, '-')
    .replace(/(^-|-$)/g, '');

const buildPluginManifest = (request: CreatePluginRequest): JsonObject => {
  const manifest = request.manifest ?? {};
  const pluginId =
    request.pluginId ||
    (typeof manifest.plugin_id === 'string' ? manifest.plugin_id : undefined) ||
    (typeof manifest.marketplaceId === 'string' ? manifest.marketplaceId : undefined) ||
    slugify(request.name);

  const permissions = Array.isArray(request.permissions) ? request.permissions : [];
  const permissionSet = {
    read: permissions,
    write: permissions,
    network: permissions,
  };

  const authentication = {
    auth_type: 'none',
    storage: 'none',
  };

  const capabilities = Array.isArray(manifest.capabilities)
    ? manifest.capabilities.filter((cap) => typeof cap === 'string')
    : [];

  const baseManifest: JsonObject = {
    plugin_id: pluginId,
    name: request.name,
    version: request.version,
    author: typeof manifest.author === 'string' ? manifest.author : undefined,
    description: request.description,
    authentication,
    permissions: permissionSet,
    capabilities,
  };

  if (manifest.webhook_config) {
    baseManifest.webhook_config = manifest.webhook_config;
  }
  if (manifest.api_endpoints) {
    baseManifest.api_endpoints = manifest.api_endpoints;
  }

  return baseManifest;
};

export class SynapseBridge extends AsyncBaseBridge<SynapseProps> {
  private plugins: SynapsePlugin[] = [];
  private connections: SynapseConnection[] = [];
  private securityLogs: SynapseSecurityLog[] = [];
  private dataLoaded = false;
  private errorCodePrefix = 'SYNAPSE';

  constructor(config?: Partial<BridgeConfig>) {
    super({
      agentId: 'synapse',
      ...config,
    });
  }

  /**
   * Initialize Synapse bridge - sets up initial state only (no IPC calls)
   *
   * IMPORTANT: No Tauri IPC calls should be made here to avoid deadlock.
   * IPC calls are deferred until after component mount via lazy loading in getProps().
   */
  protected async onInitialize(): Promise<void> {
    console.log('[SynapseBridge.onInitialize] START - Initializing Synapse bridge');

    // Initialize with empty arrays - data will be loaded lazily
    this.plugins = [];
    this.connections = [];
    this.securityLogs = [];
    this.dataLoaded = false;

    console.log('[SynapseBridge.onInitialize] END - Initialized successfully (data will load lazily)');
  }

  /**
   * Cleanup resources
   */
  protected async onDestroy(): Promise<void> {
    this.plugins = [];
    this.connections = [];
    this.securityLogs = [];
    this.dataLoaded = false;
  }

  /**
   * Get props for Synapse React component
   *
   * Performs lazy loading of plugin/connection data on first call after component mount.
   * This ensures Tauri IPC is ready and avoids initialization deadlock.
   */
  async getProps(): Promise<SynapseProps> {
    // Lazy load plugin/connection data on first call (safe to use IPC after mount)
    if (!this.dataLoaded) {
      console.log('[SynapseBridge.getProps] Lazy loading plugin and connection data (first call after mount)');
      try {
        // Load plugins
        const pluginsResponse = await this.getPlugins();
        if (pluginsResponse.success && pluginsResponse.data) {
          this.plugins = pluginsResponse.data;
        }

        // Load connections
        const connectionsResponse = await this.getConnections();
        if (connectionsResponse.success && connectionsResponse.data) {
          this.connections = connectionsResponse.data;
        }

        // Load recent security logs (last 100)
        const logsResponse = await this.getSecurityLogs({ limit: 100 });
        if (logsResponse.success && logsResponse.data) {
          this.securityLogs = logsResponse.data;
        }

        this.dataLoaded = true;
        console.log('[SynapseBridge.getProps] Plugin and connection data loaded successfully');
      } catch (error) {
        console.warn('[SynapseBridge.getProps] Failed to load plugin/connection data, continuing with empty state:', error);
        // Don't throw - allow degraded mode
      }
    }

    return {
      plugins: this.plugins,
      connections: this.connections,
      securityLogs: this.securityLogs,
      onPluginToggle: this.togglePlugin.bind(this),
      onConnectionCreate: (connection) =>
        this.createConnection({
          name: connection.service_name,
          endpoint: connection.endpoint,
          credentials: {},
        }),
      onConnectionRemove: this.removeConnection.bind(this),
    };
  }

  // ============================================================================
  // Plugin Management
  // ============================================================================

  /**
   * Get all plugins
   * Invokes: synapse_list_plugins
   */
  async getPlugins(): Promise<BridgeResponse<SynapsePlugin[]>> {
    try {
      this.plugins = await invoke<SynapsePlugin[]>('synapse_list_plugins');
      return { success: true, data: this.plugins };
    } catch (error) {
      return this.createErrorResponse<SynapsePlugin[]>(
        error,
        'Failed to fetch plugins',
        'PLUGIN_FETCH_ERROR'
      );
    }
  }

  /**
   * Get a specific plugin by ID
   * Invokes: synapse_get_plugin
   */
  async getPlugin(pluginId: string): Promise<BridgeResponse<SynapsePlugin>> {
    try {
      const plugin = await invoke<SynapsePlugin>('synapse_get_plugin', { plugin_id: pluginId });
      return { success: true, data: plugin };
    } catch (error) {
      return this.createErrorResponse<SynapsePlugin>(error, 'Failed to fetch plugin', 'PLUGIN_GET_ERROR');
    }
  }

  /**
   * Install a plugin
   * Invokes: synapse_install_plugin
   *
   * NOTE: Requires explicit permission consent from the user via PermissionConsentModal.
   * The backend will validate that all required permissions have been consented to.
   */
  async installPlugin(request: CreatePluginRequest): Promise<BridgeResponse<SynapsePlugin>> {
    try {
      const manifest = buildPluginManifest(request);

      // Pass consented permissions to backend for validation
      // The backend will reject installation if required permissions are not consented
      const consentedPermissions = request.consentedPermissions ?? [];

      const installed = await invoke<SynapsePlugin>('synapse_install_plugin', {
        request: {
          manifest,
          consented_permissions: consentedPermissions,
        },
      });

      this.plugins.push(installed);
      this.notifySubscribers({ type: 'plugin_installed', data: installed });

      return { success: true, data: installed };
    } catch (error) {
      return this.createErrorResponse<SynapsePlugin>(
        error,
        'Failed to install plugin',
        'PLUGIN_INSTALL_ERROR'
      );
    }
  }

  /**
   * Uninstall a plugin
   * Invokes: synapse_uninstall_plugin
   */
  async uninstallPlugin(pluginId: string): Promise<BridgeResponse<void>> {
    try {
      await invoke<void>('synapse_uninstall_plugin', { plugin_id: pluginId });

      this.plugins = this.plugins.filter(p => p.plugin_id !== pluginId);
      this.notifySubscribers({ type: 'plugin_uninstalled', data: { pluginId } });

      return { success: true, data: undefined };
    } catch (error) {
      return this.createErrorResponse<void>(error, 'Failed to uninstall plugin', 'PLUGIN_UNINSTALL_ERROR');
    }
  }

  /**
   * Toggle plugin enabled status
   * Invokes: synapse_enable_plugin / synapse_disable_plugin
   */
  async togglePlugin(pluginId: string, enabled: boolean): Promise<BridgeResponse<SynapsePlugin>> {
    try {
      const command = enabled ? 'synapse_enable_plugin' : 'synapse_disable_plugin';
      const updated = await invoke<SynapsePlugin>(command, { plugin_id: pluginId });

      const index = this.plugins.findIndex(p => p.plugin_id === pluginId);
      if (index !== -1) {
        this.plugins[index] = updated;
      }

      this.notifySubscribers({ type: 'plugin_toggled', data: updated });

      return { success: true, data: updated };
    } catch (error) {
      return this.createErrorResponse<SynapsePlugin>(
        error,
        'Failed to toggle plugin',
        'PLUGIN_TOGGLE_ERROR'
      );
    }
  }

  /**
   * Update plugin permissions
   * Invokes: synapse_update_plugin_permissions
   */
  async updatePluginPermissions(
    pluginId: string,
    permissions: string[]
  ): Promise<BridgeResponse<SynapsePlugin>> {
    try {
      const updated = await invoke<SynapsePlugin>('synapse_update_plugin_permissions', {
        plugin_id: pluginId,
        permissions,
      });

      const index = this.plugins.findIndex(p => p.plugin_id === pluginId);
      if (index !== -1) {
        this.plugins[index] = updated;
      }

      this.notifySubscribers({ type: 'plugin_permissions_updated', data: updated });

      return { success: true, data: updated };
    } catch (error) {
      return this.createErrorResponse<SynapsePlugin>(
        error,
        'Failed to update plugin permissions',
        'PLUGIN_PERMISSIONS_ERROR'
      );
    }
  }

  /**
   * Get plugin metrics
   * Invokes: synapse_get_plugin_metrics
   */
  async getPluginMetrics(pluginId: string): Promise<BridgeResponse<PluginMetrics>> {
    try {
      const metrics = await invoke<PluginMetrics>('synapse_get_plugin_metrics', { plugin_id: pluginId });
      return { success: true, data: metrics };
    } catch (error) {
      return this.createErrorResponse<PluginMetrics>(
        error,
        'Failed to fetch plugin metrics',
        'PLUGIN_METRICS_ERROR'
      );
    }
  }

  // ============================================================================
  // Connection Management
  // ============================================================================

  /**
   * Get all connections
   * Invokes: synapse_list_connections
   */
  async getConnections(): Promise<BridgeResponse<SynapseConnection[]>> {
    try {
      this.connections = await invoke<SynapseConnection[]>('synapse_list_connections');
      return { success: true, data: this.connections };
    } catch (error) {
      return this.createErrorResponse<SynapseConnection[]>(
        error,
        'Failed to fetch connections',
        'CONNECTION_FETCH_ERROR'
      );
    }
  }

  /**
   * Get a specific connection by ID
   * Invokes: synapse_get_connection
   */
  async getConnection(connectionId: string): Promise<BridgeResponse<SynapseConnection>> {
    try {
      const connection = await invoke<SynapseConnection>('synapse_get_connection', {
        connection_id: connectionId,
      });
      return { success: true, data: connection };
    } catch (error) {
      return this.createErrorResponse<SynapseConnection>(
        error,
        'Failed to fetch connection',
        'CONNECTION_GET_ERROR'
      );
    }
  }

  /**
   * Create a new connection
   * Invokes: synapse_create_connection
   */
  async createConnection(
    request: CreateConnectionRequest
  ): Promise<BridgeResponse<SynapseConnection>> {
    try {
      const protocol =
        request.protocol ??
        ({
          rest_api: { auth: 'none' },
        } as JsonObject);

      const created = await invoke<SynapseConnection>('synapse_create_connection', {
        request: {
          plugin_id: request.pluginId ?? 'system',
          service_name: request.name,
          endpoint: request.endpoint,
          protocol,
          credentials: request.credentials ?? {},
        },
      });

      this.connections.push(created);
      this.notifySubscribers({ type: 'connection_created', data: created });

      return { success: true, data: created };
    } catch (error) {
      return this.createErrorResponse<SynapseConnection>(
        error,
