/// Ollama Client for local inference
pub struct OllamaClient {
    base_url: String,
    model: String,
    client: reqwest::Client,
}

impl OllamaClient {
    /// Validates that the Ollama endpoint is localhost-only
    ///
    /// SECURITY: Enforces Sovereign Privacy Covenant by blocking external AI endpoints.
    /// Allowed: localhost, 127.0.0.1, 192.168.x.x (local network)
    fn validate_endpoint(url: &str) -> Result<(), String> {
        let parsed = reqwest::Url::parse(url).map_err(|e| format!("Invalid URL: {}", e))?;
        let host = parsed.host_str().unwrap_or("");

        // ALLOW: localhost, 127.0.0.1, and local network (192.168.x.x) for local home lab setups
        if host != "localhost" && host != "127.0.0.1" && !host.starts_with("192.168.") {
            eprintln!("[SECURITY] BLOCKED external Ollama request to: {}", host);
            return Err(format!(
                "Ollama endpoint must be localhost (Privacy Covenant violation). Allowed: [localhost, 127.0.0.1, 192.168.*]. Got: {}",
                host
            ));
        }

        Ok(())
    }
    pub fn new(base_url: String, model: String) -> Self {
        Self {
            base_url,
            model,
            client: reqwest::Client::new(),
        }
    }

    pub async fn generate_embedding(&self, prompt: &str) -> Result<Vec<f64>, String> {
        let url = format!("{}/api/embeddings", self.base_url);

        // SECURITY: Enforce Sovereign Privacy Covenant
        // Ensure we are ONLY sending data to a local instance
        Self::validate_endpoint(&url)?;
        let body = serde_json::json!({
            "model": self.model,
            "prompt": prompt
        });

        let res = self
            .client
            .post(&url)
            .json(&body)
            .send()
            .await
            .map_err(|e| format!("Ollama request failed: {e}"))?;

        if !res.status().is_success() {
            return Err(format!("Ollama API error: {}", res.status()));
        }

        let response_json: serde_json::Value = res
            .json()
            .await
            .map_err(|e| format!("Failed to parse Ollama response: {e}"))?;

        let embedding = response_json["embedding"]
            .as_array()
            .ok_or("Missing 'embedding' field in response")?
            .iter()
            .map(|v| v.as_f64().unwrap_or(0.0))
            .collect();

        Ok(embedding)
    }
}

/// RAG Engine manager
pub struct RagEngine {
