    /// Perform semantic search using cosine similarity (Persona-Isolated)
    ///
    /// SECURITY: This method ALWAYS filters by persona_id to ensure agents
    /// only see their own memories. Use search_cross_persona for public memories.
    pub async fn search(&self, query: RagQuery) -> Result<Vec<SearchResult>, String> {
        let db_guard = self.db.read().await;
        let db = db_guard.as_ref().ok_or("Database not initialized")?;
        let max_results = query.max_results.max(1) as usize;
        let enforced_min_similarity = Self::enforce_min_similarity_floor(&query);

        // Log persona-isolated search for security audit
        println!(
            "[SECURITY] Persona-isolated RAG search: user_id={}, persona_id={}, query_text={:?}",
            query.user_id,
            query.persona_id,
            query.query_text.as_deref().unwrap_or("<embedding>")
        );

        // Auto-generate embedding if missing but text is provided
        let mut query_embedding = query.query_embedding.clone();
        if query_embedding.is_empty() {
            if let Some(text) = &query.query_text {
                // Default to local Ollama instance
                let ollama = OllamaClient::new(
                    "http://localhost:11434".to_string(),
                    "nomic-embed-text".to_string(),
                );
                match ollama.generate_embedding(text).await {
                    Ok(emb) => query_embedding = emb,
                    Err(e) => println!("Warning: Failed to auto-generate embedding: {e}"), // Fallback will happen naturally as query_embedding stays empty
                }
            }
        }

        let query_norm = Self::vector_magnitude(&query_embedding);

        if query_norm == 0.0 {
            return Ok(Vec::new());
        }

        // SECURITY: persona_id filtering is MANDATORY in WHERE clause
        // Both vector_embeddings AND memory_slices must match the persona_id
        let mut sql = String::from(
            r#"
            SELECT
                ve.memory_slice_id,
                ve.embedding,
                ve.chunk_index,
                ve.total_chunks,
                ve.metadata,
                ms.encrypted_content,
                ms.nonce,
                ms.memory_type AS content_type,
                ms.tags,
                ms.created_at
            FROM vector_embeddings ve
            JOIN memory_slices ms ON ve.memory_slice_id = ms.memory_id
            WHERE ve.user_id = ?
                AND ve.persona_id = ?
                AND ms.persona_id = ?
                AND ms.status = 'active'
        "#,
        );

        // MANDATORY: persona_id is always included (no longer optional)
        let mut params: Vec<String> = vec![
            query.user_id.clone(),
            query.persona_id.clone(),
            query.persona_id.clone(),
        ];

        if let Some(content_types) = &query.content_types {
            if !content_types.is_empty() {
                sql.push_str(" AND ms.memory_type IN (");
                for (i, _) in content_types.iter().enumerate() {
                    if i > 0 {
                        sql.push_str(", ");
                    }
                    sql.push('?');
                }
                sql.push(')');
                params.extend(content_types.clone());
            }
        }

        let mut query_builder = sqlx::query(&sql);
        for param in &params {
            query_builder = query_builder.bind(param);
        }

        let mut rows = query_builder.fetch(&db.read_pool);
        let mut results: Vec<SearchResult> = Vec::new();
        let now_ts = chrono::Utc::now().timestamp();

        while let Some(row) = rows
            .try_next()
            .await
            .map_err(|e| format!("Failed to fetch candidates: {e}"))?
        {
            let tags_value: Option<String> = row.try_get("tags").ok();
            if let Some(filter_tags) = &query.tags {
                if !Self::tags_match(filter_tags, tags_value.as_ref()) {
                    continue;
                }
            }

            let embedding_json: String = row
                .try_get("embedding")
                .map_err(|e| format!("Failed to get embedding: {e}"))?;

            let embedding: Vec<f64> = serde_json::from_str(&embedding_json)
                .map_err(|e| format!("Failed to parse embedding: {e}"))?;

            if embedding.len() != query_embedding.len() {
                continue;
            }

            let created_at: i64 = row
                .try_get("created_at")
                .map_err(|e| format!("Failed to get created_at: {e}"))?;

            let similarity =
                Self::cosine_similarity_precomputed(&query_embedding, query_norm, &embedding);

            let decayed_similarity = Self::apply_time_decay(similarity, created_at, now_ts);

            if decayed_similarity < enforced_min_similarity {
                continue;
            }

            let encrypted_content: String = row
                .try_get("encrypted_content")
                .map_err(|e| format!("Failed to get encrypted_content: {e}"))?;
            let nonce: String = row
                .try_get("nonce")
                .map_err(|e| format!("Failed to get nonce: {e}"))?;
            let plaintext_content = self
                .decrypt_memory_content(&encrypted_content, &nonce)
                .await?;

            let result = SearchResult {
                memory_slice_id: row
                    .try_get("memory_slice_id")
                    .map_err(|e| format!("Failed to get memory_slice_id: {e}"))?,
                content: plaintext_content,
                content_type: row
                    .try_get("content_type")
                    .map_err(|e| format!("Failed to get content_type: {e}"))?,
                similarity_score: decayed_similarity,
                chunk_index: row
                    .try_get("chunk_index")
                    .map_err(|e| format!("Failed to get chunk_index: {e}"))?,
                total_chunks: row
                    .try_get("total_chunks")
                    .map_err(|e| format!("Failed to get total_chunks: {e}"))?,
                metadata: row.try_get("metadata").ok(),
                tags: tags_value,
                created_at,
            };

            results.push(result);
        }

        results.sort_by(|a, b| {
            b.similarity_score
                .partial_cmp(&a.similarity_score)
                .unwrap_or(std::cmp::Ordering::Equal)
        });

        let mut gated_results = Self::gate_context(&results);

        if gated_results.len() > max_results {
            gated_results.truncate(max_results);
        }

        Ok(gated_results)
    }

