#[tauri::command]
pub async fn vault_rag_search(
    auth: tauri::State<'_, Arc<SecureAuthenticator>>,
    request: AuthenticatedRequest<SearchRequest>,
    state: tauri::State<'_, Arc<RwLock<Option<DatabaseManager>>>>,
    key_manager: tauri::State<'_, Arc<SecureKeyManager>>,
) -> Result<Vec<SearchResult>, String> {
    let authenticated_user = require_auth!(auth, &request.auth_token);
    require_ownership!(authenticated_user, &request.payload.query.user_id);
    let engine = RagEngine::new(state.inner().clone(), key_manager.inner().clone());
    engine.search(request.payload.query).await
}

