/// Robust script locator that checks resource_dir first, then falls back to project root
fn locate_rag_script_path(app: &tauri::AppHandle, relative_path: &str) -> Result<String, String> {
    // Try resource_dir first (for production builds)
    if let Some(resource_dir) = app.path_resolver().resource_dir() {
        let resource_path = resource_dir.join(relative_path);
        if resource_path.exists() {
            return Ok(resource_path.to_string_lossy().to_string());
        }
    }

    // Fallback to project root relative path (for development)
    let project_path = std::path::Path::new(relative_path);
    if project_path.exists() {
        return Ok(relative_path.to_string());
    }

    Err(format!(
        "Script not found at {relative_path} (checked resource_dir and project root)"
    ))
}

fn run_relevance_script(
    app: &tauri::AppHandle,
    input_json: &serde_json::Value,
) -> Result<serde_json::Value, String> {
    // Robust script locator with resource_dir fallback
    let script_path = locate_rag_script_path(app, "src/vault/rag_relevance.py")?;

    let mut child = Command::new("python")
        .arg(script_path)
        .stdin(Stdio::piped())
        .stdout(Stdio::piped())
        .stderr(Stdio::piped())
        .spawn()
        .map_err(|e| format!("Failed to spawn python script: {e}"))?;

    if let Some(mut stdin) = child.stdin.take() {
        let input_str = serde_json::to_string(input_json).map_err(|e| e.to_string())?;
        stdin
            .write_all(input_str.as_bytes())
            .map_err(|e| format!("Failed to write to stdin: {e}"))?;
    }

    let output = child
        .wait_with_output()
        .map_err(|e| format!("Failed to read output: {e}"))?;

    if !output.status.success() {
        let stderr = String::from_utf8_lossy(&output.stderr);
        return Err(format!("Python script failed: {stderr}"));
    }

    let stdout = String::from_utf8_lossy(&output.stdout);
    let result: serde_json::Value =
        serde_json::from_str(&stdout).map_err(|e| format!("Failed to parse JSON output: {e}"))?;

    Ok(result)
}

