pub async fn download_ollama() -> Result<(), String> {
    #[cfg(target_os = "windows")]
    {
        // Download Ollama installer for Windows
        let download_url = "https://ollama.com/download/OllamaSetup.exe";
        let temp_dir = std::env::temp_dir();
        let installer_path = temp_dir.join("OllamaSetup.exe");

        // Download installer
        let response = reqwest::get(download_url)
            .await
            .map_err(|e| format!("Failed to download Ollama: {e}"))?;

        let bytes = response
            .bytes()
            .await
            .map_err(|e| format!("Failed to read download: {e}"))?;

        std::fs::write(&installer_path, bytes)
            .map_err(|e| format!("Failed to save installer: {e}"))?;

        // Run installer silently
        let install_result = Command::new(&installer_path)
            .arg("/S") // Silent install
            .spawn()
            .map_err(|e| format!("Failed to run installer: {e}"))?
            .wait()
            .map_err(|e| format!("Failed to wait for installer: {e}"))?;

        if !install_result.success() {
            return Err("Installation failed".to_string());
        }

        // Clean up installer
        let _ = std::fs::remove_file(&installer_path);

        Ok(())
    }

    #[cfg(target_os = "macos")]
    {
        // On macOS, open browser to download page
        let _ = Command::new("open")
            .arg("https://ollama.com/download")
            .spawn();

        Err("Please download and install Ollama from the browser".to_string())
    }

    #[cfg(target_os = "linux")]
    {
        // Use curl to install Ollama on Linux
        let install_result = Command::new("curl")
            .arg("-fsSL")
            .arg("https://ollama.com/install.sh")
            .stdout(std::process::Stdio::piped())
            .spawn()
            .and_then(|child| {
                let output = child.stdout.expect("Failed to get stdout");
                Command::new("sh")
                    .stdin(output)
                    .spawn()
                    .and_then(|mut sh_child| sh_child.wait())
            });

        match install_result {
            Ok(status) if status.success() => Ok(()),
            Ok(_) => Err("Installation script failed".to_string()),
            Err(e) => Err(format!("Failed to install Ollama: {}", e)),
        }
    }
}

