#[derive(Clone)]
pub struct SettingsManager {
    config_path: PathBuf,
}

impl SettingsManager {
    pub fn new(app_handle: &tauri::AppHandle) -> Self {
        let config_dir = app_handle
            .path_resolver()
            .app_config_dir()
            .unwrap_or_else(|| PathBuf::from("config"));

        if !config_dir.exists() {
            let _ = fs::create_dir_all(&config_dir);
        }

        let config_path = config_dir.join("settings.json");

        Self { config_path }
    }

    /// Create a new SettingsManager with a specific path (useful for testing)
    #[allow(dead_code)]
    pub fn new_with_path(config_path: PathBuf) -> Self {
        Self { config_path }
    }

    pub fn load(&self) -> SettingsConfig {
        if self.config_path.exists() {
            if let Ok(content) = fs::read_to_string(&self.config_path) {
                if let Ok(config) = serde_json::from_str(&content) {
                    return config;
                }
            }
        }
        SettingsConfig::default()
    }

    pub fn save(&self, config: &SettingsConfig) -> Result<(), String> {
        let content = serde_json::to_string_pretty(config).map_err(|e| e.to_string())?;
        fs::write(&self.config_path, content).map_err(|e| e.to_string())?;
        Ok(())
    }
}

// Wrapper for state management
pub struct SettingsState {
    pub manager: Mutex<SettingsManager>,
}

#[derive(Serialize, Deserialize, Clone, Debug, Default)]
pub struct EmptySettingsRequest {}

#[derive(Serialize, Deserialize, Clone, Debug)]
pub struct SettingsSaveRequest {
    pub settings: SettingsConfig,
}

#[tauri::command]
pub async fn system_get_settings(
    auth: State<'_, Arc<SecureAuthenticator>>,
    request: AuthenticatedRequest<EmptySettingsRequest>,
    state: State<'_, SettingsState>,
) -> Result<SettingsConfig, String> {
    let _authenticated_user = require_auth!(auth, &request.auth_token);
    let manager = state
        .manager
        .lock()
        .map_err(|_| "Failed to lock settings manager".to_string())?;
    Ok(manager.load())
}

#[tauri::command]
pub async fn system_save_settings(
    auth: State<'_, Arc<SecureAuthenticator>>,
    request: AuthenticatedRequest<SettingsSaveRequest>,
    state: State<'_, SettingsState>,
) -> Result<(), String> {
    let _authenticated_user = require_auth!(auth, &request.auth_token);
    let manager = state
        .manager
        .lock()
        .map_err(|_| "Failed to lock settings manager".to_string())?;
    manager.save(&request.payload.settings)
}

