use crate::synapse::models::{AuthType, AuthenticationConfig, PermissionSet, PluginManifest};

pub const QWEN3_PLUGIN_ID: &str = "qwen3_tts";
pub const QWEN3_VOICE_NEUTRAL: &str = "qwen3-neutral";
pub const QWEN3_VOICE_WARM: &str = "qwen3-warm";
pub const QWEN3_VOICE_PROFESSIONAL: &str = "qwen3-professional";

pub fn create_default_manifest() -> PluginManifest {
    PluginManifest {
        plugin_id: QWEN3_PLUGIN_ID.to_string(),
        name: "Qwen3 TTS".to_string(),
        version: "1.0.0".to_string(),
        author: Some("Alibaba Cloud / Hearthlink".to_string()),
        description:
            "Local, low-latency speech synthesis using Qwen3. Supports voice cloning and design."
                .to_string(),
        authentication: AuthenticationConfig {
            auth_type: AuthType::None,
            storage: "none".to_string(),
        },
        permissions: PermissionSet {
            read: vec!["local_audio_storage".to_string()],
            write: vec!["local_audio_storage".to_string()],
            network: vec![], // Strictly local
        },
        capabilities: vec![
            "tts".to_string(),
            "voice-cloning".to_string(),
            "voice-design".to_string(),
        ],
        webhook_config: None,
        api_endpoints: None,
    }
}

pub fn get_available_voices() -> Vec<(String, String)> {
    vec![
        (QWEN3_VOICE_NEUTRAL.to_string(), "Qwen3 Neutral".to_string()),
        (QWEN3_VOICE_WARM.to_string(), "Qwen3 Warm".to_string()),
        (
            QWEN3_VOICE_PROFESSIONAL.to_string(),
            "Qwen3 Professional".to_string(),
        ),
    ]
}

