/**
 * AvatarStudio - Avatar display tab for Mimic Module
 * Shows persona avatars with their default images
 */
import React, { memo, useCallback, useState, useMemo, useRef } from "react";
import {
  User,
  Check,
  Download,
  Upload,
  Mic,
  Square,
} from "@/components/ui/HearthlinkIcons";
import { Avatar3D } from "../avatar/Avatar3D";
import { useAvatarStore } from "../avatar/useAvatarStore";
import { DEFAULT_PERSONA_AVATARS } from "../avatar/types";

import { AgentAvatar } from "../shared/chat/AgentAvatar";
import { SYSTEM_PERSONAS as SHARED_SYSTEM_PERSONAS } from "../shared/chat/types";
import type { Persona } from "../shared/chat/types";

// Map shared personas to the format needed for the studio
const STUDIO_PERSONAS = Object.values(SHARED_SYSTEM_PERSONAS).map((p) => ({
  ...p,
  color:
    p.id === "alden"
      ? "#d6b369"
      : p.id === "alice"
        ? "#00e6ff"
        : p.id === "sentry"
          ? "#ef4444"
          : "#a855f7",
  status: "idle" as const,
}));

interface PersonaSelectorProps {
  personas: typeof STUDIO_PERSONAS;
  selectedId: string;
  onSelect: (id: string) => void;
  avatarStore: ReturnType<typeof useAvatarStore>;
}

const PersonaSelector = memo<PersonaSelectorProps>(
  ({ personas, selectedId, onSelect, avatarStore }) => (
    <div className="space-y-2">
      <h3 className="text-xs font-bold text-[#a855f7] uppercase tracking-widest mb-3">
        Select Persona
      </h3>
      <div className="space-y-1">
        {personas.map((persona) => {
          const hasAvatar = !!DEFAULT_PERSONA_AVATARS[persona.id];
          const isSelected = selectedId === persona.id;

          return (
            <button
              key={persona.id}
              onClick={() => onSelect(persona.id)}
              className={`w-full flex items-center gap-3 px-3 py-2.5 rounded-lg transition-all ${
                isSelected
                  ? "bg-[#a855f7]/20 border border-[#a855f7]/50"
                  : "bg-slate-800/50 border border-transparent hover:border-[#a855f7]/30 hover:bg-slate-800"
              }`}
            >
              <div className="relative">
                <AgentAvatar
                  persona={persona as Persona}
                  size="sm"
                  showStatus={false}
                  showBorder={isSelected}
                  className={isSelected ? "ring-2 ring-[#a855f7]" : ""}
                />
                {hasAvatar && (
                  <div className="absolute -bottom-1 -right-1 bg-green-500 rounded-full p-0.5 border border-[#0a0f1d]">
                    <Check size={8} className="text-white" />
                  </div>
                )}
              </div>

              <div className="flex-1 text-left">
                <div className="text-sm font-semibold text-white">
                  {persona.name}
                </div>
                <div className="text-[10px] text-slate-400 uppercase tracking-wider">
                  {persona.subtitle}
                </div>
              </div>
            </button>
          );
        })}
      </div>
    </div>
  ),
);

PersonaSelector.displayName = "PersonaSelector";

export interface AvatarStudioProps {
  className?: string;
}

export const AvatarStudio = memo<AvatarStudioProps>(({ className = "" }) => {
  const [selectedPersonaId, setSelectedPersonaId] = useState<string>("alden");
  const avatarStore = useAvatarStore();

  const selectedPersona = useMemo(
    () =>
      STUDIO_PERSONAS.find((p) => p.id === selectedPersonaId) ||
      STUDIO_PERSONAS[0],
    [selectedPersonaId],
  );

  // Get avatar URL from defaults
  const avatarUrl = DEFAULT_PERSONA_AVATARS[selectedPersonaId] || "";

  const [activeTab, setActiveTab] = useState<"visuals" | "voice">("visuals");
  const [voiceDesignPrompt, setVoiceDesignPrompt] = useState("");
  const [cloningAudio, setCloningAudio] = useState<File | null>(null);
  const [generatedVoiceId, setGeneratedVoiceId] = useState<string | null>(null);
  const [isGenerating, setIsGenerating] = useState(false);
  const [isRecording, setIsRecording] = useState(false);
  const [recordingTime, setRecordingTime] = useState(0);

  const fileInputRef = useRef<HTMLInputElement>(null);
  const mediaRecorderRef = useRef<MediaRecorder | null>(null);
  const chunksRef = useRef<Blob[]>([]);
  const timerRef = useRef<NodeJS.Timeout | null>(null);

  const handleStartRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const mediaRecorder = new MediaRecorder(stream);
      mediaRecorderRef.current = mediaRecorder;
      chunksRef.current = [];

      mediaRecorder.ondataavailable = (e) => {
        if (e.data.size > 0) {
          chunksRef.current.push(e.data);
        }
      };

      mediaRecorder.onstop = () => {
        const blob = new Blob(chunksRef.current, { type: "audio/webm" });
        const file = new File([blob], "recording.webm", { type: "audio/webm" });
        setCloningAudio(file);
        stream.getTracks().forEach((track) => track.stop());
      };

      mediaRecorder.start();
      setIsRecording(true);
      setRecordingTime(0);

      timerRef.current = setInterval(() => {
        setRecordingTime((prev) => prev + 1);
      }, 1000);
    } catch (err) {
      console.error("Error accessing microphone:", err);
      alert("Could not access microphone.");
    }
  };

  const handleStopRecording = () => {
    if (mediaRecorderRef.current && isRecording) {
      mediaRecorderRef.current.stop();
      setIsRecording(false);
      if (timerRef.current) {
        clearInterval(timerRef.current);
        timerRef.current = null;
      }
    }
  };

  const handleVoiceCloning = async () => {
    if (!cloningAudio) return;
    setIsGenerating(true);
    // Simulate cloning process (upload -> embedding extraction -> model registration)
    setTimeout(() => {
      setGeneratedVoiceId("qwen3-cloned-v1");
      setIsGenerating(false);
      // Optional: clear audio to reset
      // setCloningAudio(null);
    }, 3000);
  };

  const handleVoiceDesign = async () => {
    setIsGenerating(true);
    // Simulate generation delay
    setTimeout(() => {
      setGeneratedVoiceId("custom-voice-1");
      setIsGenerating(false);
    }, 2000);
  };

  const handleExport = useCallback(() => {
    const data = avatarStore.exportAvatars();
    const blob = new Blob([data], { type: "application/json" });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = `hearthlink-avatars-${new Date().toISOString().split("T")[0]}.json`;
    a.click();
    URL.revokeObjectURL(url);
  }, [avatarStore]);

  const handleImport = useCallback(() => {
    const input = document.createElement("input");
    input.type = "file";
    input.accept = ".json";
    input.onchange = (e) => {
      const file = (e.target as HTMLInputElement).files?.[0];
      if (file) {
        const reader = new FileReader();
        reader.onload = (ev) => {
          const content = ev.target?.result as string;
          avatarStore.importAvatars(content);
        };
        reader.readAsText(file);
      }
    };
    input.click();
  }, [avatarStore]);

  return (
    <div className={`flex flex-col h-full relative ${className}`}>
      {/* Tab Navigation */}
      <div className="flex items-center gap-4 px-6 py-4 border-b border-white/10 bg-black/20">
        <button
          onClick={() => setActiveTab("visuals")}
          className={`px-4 py-2 rounded-lg text-sm font-bold uppercase tracking-wider transition-all ${
            activeTab === "visuals"
              ? "bg-[#a855f7]/20 text-[#a855f7] border border-[#a855f7]/50"
              : "text-slate-400 hover:text-white hover:bg-white/5"
          }`}
        >
          Visuals (Avatar)
        </button>
        <button
          onClick={() => setActiveTab("voice")}
          className={`px-4 py-2 rounded-lg text-sm font-bold uppercase tracking-wider transition-all ${
            activeTab === "voice"
              ? "bg-[#00e5ff]/20 text-[#00e5ff] border border-[#00e5ff]/50"
              : "text-slate-400 hover:text-white hover:bg-white/5"
          }`}
        >
          Voice (Qwen3)
        </button>
      </div>

      {activeTab === "visuals" ? (
        /* Studio View - Visuals */
        <div className="flex h-full gap-6 overflow-hidden pt-4">
          {/* Left Sidebar - Persona Selector & Tools */}
          <div className="w-80 flex flex-col gap-6 shrink-0 overflow-y-auto pr-2 pl-4 scrollbar-thin scrollbar-thumb-purple-900/50">
            {/* Persona Selector */}
            <PersonaSelector
              personas={STUDIO_PERSONAS}
              selectedId={selectedPersonaId}
              onSelect={setSelectedPersonaId}
              avatarStore={avatarStore}
            />

            {/* Avatar Statistics */}
            <div className="bg-slate-900/50 backdrop-blur-md rounded-xl p-5 border border-white/10">
              <h3 className="text-xs font-bold text-[#a855f7] uppercase tracking-widest mb-4">
                Avatar Statistics
              </h3>
              <div className="space-y-3">
                <div className="flex items-center justify-between">
                  <span className="text-xs text-slate-400 uppercase tracking-wider">
                    Available
                  </span>
                  <span className="text-lg font-bold text-[#a855f7]">
                    {Object.keys(DEFAULT_PERSONA_AVATARS).length}
                  </span>
                </div>
                <div className="w-full h-px bg-white/10" />
                <div className="flex items-center justify-between">
                  <span className="text-xs text-slate-400 uppercase tracking-wider">
                    Total Personas
                  </span>
                  <span className="text-lg font-bold text-white">
                    {STUDIO_PERSONAS.length}
                  </span>
                </div>
              </div>
            </div>

            {/* Import/Export Tools */}
            <div className="bg-slate-900/50 backdrop-blur-md rounded-xl p-5 border border-white/10">
              <h3 className="text-xs font-bold text-[#a855f7] uppercase tracking-widest mb-4">
                Avatar Tools
              </h3>
              <div className="space-y-2">
                <button
                  onClick={handleImport}
                  className="w-full px-4 py-2.5 rounded-lg bg-slate-800 text-slate-300 border border-slate-700 hover:border-[#a855f7]/50 hover:bg-slate-700 transition-all flex items-center justify-center gap-2 text-sm font-medium"
                >
                  <Upload size={14} />
                  Import Avatars
                </button>
                <button
                  onClick={handleExport}
                  className="w-full px-4 py-2.5 rounded-lg bg-slate-800 text-slate-300 border border-slate-700 hover:border-[#a855f7]/50 hover:bg-slate-700 transition-all flex items-center justify-center gap-2 text-sm font-medium"
                >
                  <Download size={14} />
                  Export Avatars
                </button>
              </div>
            </div>
          </div>

          {/* Right Content - Avatar Display */}
          <div className="flex-1 flex flex-col min-w-0 overflow-hidden">
            <div className="flex-1 flex items-center justify-center px-12 py-8">
              <div className="flex flex-col items-center gap-8 w-full max-w-2xl">
                {/* Persona Info Header */}
                <div className="text-center space-y-2">
                  <h2 className="text-4xl font-bold text-white">
                    {selectedPersona.name}
                  </h2>
                  <p className="text-sm text-[#a855f7] uppercase tracking-[0.2em] font-semibold">
                    {selectedPersona.subtitle}
                  </p>
                </div>

                {/* Avatar Display */}
                {avatarUrl ? (
                  <div className="relative w-full max-w-lg aspect-[3/4]">
                    {/* Studio frame background */}
                    <div className="absolute inset-0 rounded-3xl bg-gradient-to-b from-[#a855f7]/5 to-[#0a0f1d] border-4 border-[#a855f7]/20 shadow-[0_0_30px_rgba(168,85,247,0.1)]" />

                    {/* Inner Bevel */}
                    <div className="absolute inset-1 rounded-[1.8rem] border border-white/5 pointer-events-none" />

                    {/* Glow effect */}
                    <div className="absolute inset-0 bg-[radial-gradient(circle_at_center,_var(--tw-gradient-stops))] from-[#a855f7]/20 via-transparent to-transparent opacity-60" />

                    {/* Avatar viewport */}
                    <div className="absolute inset-4 flex items-center justify-center">
                      <img
                        src={avatarUrl}
                        alt={`${selectedPersona.name} Avatar`}
                        className="max-w-full max-h-full object-contain drop-shadow-2xl rounded-2xl"
                      />
                    </div>
                  </div>
                ) : (
                  <div className="w-full max-w-lg aspect-[3/4] rounded-3xl bg-slate-900/50 backdrop-blur-sm border-[8px] border-dashed border-slate-700/50 flex flex-col items-center justify-center gap-8 p-12">
                    <div className="w-32 h-32 rounded-full bg-slate-800/50 flex items-center justify-center border-4 border-slate-700/50 shadow-inner">
                      <User size={64} className="text-slate-600" />
                    </div>
                    <div className="text-center space-y-3">
                      <p className="text-2xl font-bold text-slate-300">
                        No Avatar Available
                      </p>
                      <p className="text-sm text-slate-500 max-w-sm leading-relaxed">
                        Avatar image not found for {selectedPersona.name}
                      </p>
                    </div>
                  </div>
                )}

                {/* Status Badge */}
                {avatarUrl && (
                  <div className="px-5 py-1.5 rounded-full bg-green-500/10 border-2 border-green-500/50 shadow-[0_0_15px_rgba(34,197,94,0.1)]">
                    <span className="text-[10px] font-bold text-green-400 uppercase tracking-[0.15em] flex items-center gap-2">
                      <Check size={12} className="flex-shrink-0" />
                      Avatar Active
                    </span>
                  </div>
                )}

                {/* Info */}
                <div className="text-center">
                  <p className="text-xs text-slate-500 uppercase tracking-wider">
                    Static Avatar Image
                  </p>
                </div>
              </div>
            </div>
          </div>
        </div>
      ) : (
        /* Studio View - Voice (Qwen3) */
        <div className="flex h-full gap-6 overflow-hidden p-6">
          <div className="w-1/2 flex flex-col gap-6">
            <div className="bg-slate-900/50 backdrop-blur-md rounded-xl p-6 border border-[#00e5ff]/20 flex flex-col gap-4">
              <h2 className="text-lg font-bold text-[#00e5ff] uppercase tracking-widest flex items-center gap-2">
                <Upload size={18} /> Voice Cloning
              </h2>
              <p className="text-sm text-slate-400">
                Upload or record a crisp 3-10s audio sample to clone a voice.
              </p>
              <div className="border-2 border-dashed border-slate-700 rounded-lg p-6 flex flex-col items-center justify-center gap-4 bg-black/20">
                {!isRecording ? (
                  <div className="flex gap-4">
                    <div
                      onClick={() => fileInputRef.current?.click()}
                      className="flex flex-col items-center gap-2 cursor-pointer hover:text-[#00e5ff] transition-colors p-4 rounded-lg hover:bg-white/5"
                    >
                      <Upload
                        size={24}
                        className="text-slate-500 hover:text-[#00e5ff]"
                      />
                      <span className="text-sm text-slate-300">
                        Upload File
                      </span>
                    </div>
                    <div className="w-px bg-slate-700 h-16 self-center" />
                    <div
                      onClick={handleStartRecording}
                      className="flex flex-col items-center gap-2 cursor-pointer hover:text-red-500 transition-colors p-4 rounded-lg hover:bg-white/5"
                    >
                      <Mic
                        size={24}
                        className="text-slate-500 hover:text-red-500"
                      />
                      <span className="text-sm text-slate-300">Record Mic</span>
                    </div>
                  </div>
                ) : (
                  <div className="flex flex-col items-center gap-3 animate-pulse">
                    <div className="w-12 h-12 rounded-full bg-red-500/20 flex items-center justify-center border border-red-500">
                      <div className="w-3 h-3 bg-red-500 rounded-full" />
                    </div>
                    <span className="text-red-500 font-bold tracking-widest">
                      RECORDING {recordingTime}s
                    </span>
                    <button
                      onClick={handleStopRecording}
                      className="px-4 py-1.5 bg-slate-800 border border-slate-600 rounded text-xs uppercase hover:bg-slate-700 mt-2 flex items-center gap-2"
                    >
                      <Square size={10} /> Stop Recording
                    </button>
                  </div>
                )}

                {cloningAudio && !isRecording && (
                  <div className="mt-2 px-3 py-1 bg-[#00e5ff]/10 border border-[#00e5ff]/30 rounded text-xs text-[#00e5ff] flex items-center gap-2">
                    <Check size={10} />
                    Selected: {cloningAudio.name}
                  </div>
                )}

                <input
                  ref={fileInputRef}
                  type="file"
                  className="hidden"
                  accept="audio/*"
                  onChange={(e) => {
                    const file = (e.target as HTMLInputElement).files?.[0];
                    if (file) setCloningAudio(file);
                  }}
                />
              </div>

              <button
                onClick={handleVoiceCloning}
                disabled={isGenerating || !cloningAudio || isRecording}
                className="w-full py-3 bg-[#00e5ff]/10 border border-[#00e5ff]/50 rounded-lg text-[#00e5ff] font-bold uppercase tracking-widest hover:bg-[#00e5ff]/20 disabled:opacity-50 disabled:cursor-not-allowed transition-all flex items-center justify-center gap-2"
              >
                {isGenerating ? "Cloning..." : "Clone Voice"}
              </button>
            </div>

            <div className="bg-slate-900/50 backdrop-blur-md rounded-xl p-6 border border-[#00e5ff]/20 flex flex-col gap-4 flex-1">
              <h2 className="text-lg font-bold text-[#00e5ff] uppercase tracking-widest flex items-center gap-2">
                <Check size={18} /> Voice Design
              </h2>
              <p className="text-sm text-slate-400">
                Describe the voice you want to generate (e.g., "A raspy, elderly
                wizard with a slight British accent").
              </p>
              <textarea
                className="w-full h-32 bg-black/40 border border-slate-700 rounded-lg p-3 text-white focus:border-[#00e5ff] focus:outline-none resize-none"
                placeholder="Enter voice description..."
                value={voiceDesignPrompt}
                onChange={(e) => setVoiceDesignPrompt(e.target.value)}
              />
              <button
                onClick={handleVoiceDesign}
                disabled={isGenerating || !voiceDesignPrompt}
                className="mt-auto w-full py-3 bg-[#00e5ff]/10 border border-[#00e5ff]/50 rounded-lg text-[#00e5ff] font-bold uppercase tracking-widest hover:bg-[#00e5ff]/20 disabled:opacity-50 disabled:cursor-not-allowed transition-all"
              >
                {isGenerating ? "Generating..." : "Generate Voice"}
              </button>
            </div>
          </div>

          <div className="w-1/2 bg-black/40 rounded-xl border border-white/5 p-6 flex flex-col items-center justify-center">
            {generatedVoiceId ? (
              <div className="text-center space-y-4">
                <div className="w-24 h-24 rounded-full bg-[#00e5ff]/10 border-2 border-[#00e5ff] flex items-center justify-center shadow-[0_0_30px_rgba(0,229,255,0.3)] mx-auto animate-pulse">
                  <Check size={40} className="text-[#00e5ff]" />
                </div>
                <h3 className="text-2xl font-bold text-white">
                  Voice Generated!
                </h3>
                <p className="text-slate-400">ID: {generatedVoiceId}</p>
                <div className="flex gap-3 justify-center mt-6">
                  <button className="px-6 py-2 bg-[#00e5ff] text-black font-bold rounded-lg hover:bg-[#00e5ff]/90">
                    Apply to Persona
                  </button>
                  <button className="px-6 py-2 border border-[#00e5ff] text-[#00e5ff] font-bold rounded-lg hover:bg-[#00e5ff]/10">
                    Preview
                  </button>
                </div>
              </div>
            ) : (
              <div className="text-center space-y-4 opacity-50">
                <div className="w-24 h-24 rounded-full bg-slate-800 border-2 border-slate-700 flex items-center justify-center mx-auto">
                  <Download size={32} className="text-slate-500" />
                </div>
                <p className="text-slate-400">
                  Generate a voice to preview it here
                </p>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
});

AvatarStudio.displayName = "AvatarStudio";

export default AvatarStudio;

