/**
 * Base Agent Bridge Implementation
 *
 * Provides common functionality for all agent bridges including:
 * - API communication
 * - Event subscription
 * - Error handling
 * - State management
 *
 * @version 1.3.0
 * @date 2025-10-09
 */

import { invoke } from '@tauri-apps/api/tauri';
import { AgentId, AgentBridge, BridgeResponse, AgentCommand, CommandRequest } from '../types/agents';
import { AuthClient } from '../lib/tauri-client';
import { isBrowserMode, warnBrowserMode } from '../utils/tauri';

type JsonObject = Record<string, unknown>;

export interface BridgeConfig {
  agentId: AgentId;
  baseUrl?: string;
  timeout?: number;
  retryAttempts?: number;
  userId?: string;
  authToken?: string;
}

export abstract class BaseBridge<TProps = unknown> implements AgentBridge<TProps> {
  public readonly agentId: AgentId;
  protected baseUrl: string;
  protected timeout: number;
  protected retryAttempts: number;
  protected subscribers: Set<(data: unknown) => void> = new Set();
  protected initialized = false;
  protected userId: string;
  protected authToken: string;

  constructor(config: BridgeConfig) {
    this.agentId = config.agentId;
    this.baseUrl = config.baseUrl || '/api';
    this.timeout = config.timeout || 30000;
    this.retryAttempts = config.retryAttempts || 3;
    this.userId = config.userId ?? AuthClient.getUserId() ?? '';
    this.authToken = config.authToken ?? AuthClient.getAuthToken() ?? '';

    if (!this.userId || !this.authToken) {
       console.warn(`[Bridge:${this.agentId}] Initialized without strict credentials. Operations may fail.`);
       // throw new Error(`[Bridge:${this.agentId}] Authentication required: userId and authToken must be provided.`);
    }
  }

  /**
   * Initialize the bridge connection
   */
  async initialize(): Promise<void> {
    if (this.initialized) {
      return;
    }

    try {
      // Perform agent-specific initialization
      await this.onInitialize();
      this.initialized = true;
      console.log(`[Bridge:${this.agentId}] Initialized successfully`);
    } catch (error) {
      console.error(`[Bridge:${this.agentId}] Initialization failed:`, error);
      throw error;
    }
  }

  /**
   * Get props for the agent component
   */
  abstract getProps(): Promise<TProps>;

  /**
   * Send a message to the agent
   */
  async sendMessage(message: unknown): Promise<BridgeResponse<unknown>> {
    this.assertAuthContext();
    if (!this.initialized) {
      return {
        success: false,
        error: 'Bridge not initialized',
        code: 'NOT_INITIALIZED',
      };
    }

    try {
      const command = `${this.agentId}_send_message`;
      const request: CommandRequest = {
        user_id: this.userId,
        message,
      };

      const response = await this.invokeCommand(command as AgentCommand, { request });
      this.notifySubscribers({ type: 'message_sent', data: message });
      return { success: true, data: response };
    } catch (error) {
      const messageText = error instanceof Error ? error.message : 'Failed to send message';
      const code = typeof error === 'object' && error !== null && 'code' in error ? (error as { code?: string }).code : undefined;
      return {
        success: false,
        error: messageText,
        code: code || 'SEND_ERROR',
      };
    }
  }

  /**
   * Subscribe to bridge events
   */
  subscribe(callback: (data: unknown) => void): () => void {
    this.subscribers.add(callback);
    return () => {
      this.subscribers.delete(callback);
    };
  }

  /**
   * Destroy the bridge and cleanup resources
   */
  async destroy(): Promise<void> {
    try {
      await this.onDestroy();
      this.subscribers.clear();
      this.initialized = false;
      console.log(`[Bridge:${this.agentId}] Destroyed successfully`);
    } catch (error) {
      console.error(`[Bridge:${this.agentId}] Destruction failed:`, error);
      throw error;
    }
  }

  // Protected methods for subclasses

  /**
   * Hook for agent-specific initialization
   */
  protected abstract onInitialize(): Promise<void>;

  /**
   * Hook for agent-specific cleanup
   */
  protected abstract onDestroy(): Promise<void>;

  /**
   * Invoke a Tauri IPC command
   * @param command - The Tauri command name (e.g., 'alden_tasks_list')
   * @param params - The parameters to pass to the command
   * @param attempt - Current retry attempt (for internal use)
   * @returns The command result
   */
  protected async invokeCommand<T = unknown>(
    command: AgentCommand,
    params?: JsonObject,
    attempt = 1
  ): Promise<T> {
    const callId = `${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;

    this.assertAuthContext();
    // Browser mode: return mock data instead of failing
    if (isBrowserMode()) {
      warnBrowserMode('BaseBridge');
      console.log(`[BaseBridge.invokeCommand][${callId}] BROWSER MODE - Returning mock data for command: ${command}`);
      return this.getMockResponse<T>(command, params);
    }

    console.log(`[BaseBridge.invokeCommand][${callId}] START command: ${command}`, JSON.stringify(params));

    // Prepare payload with authenticated context
    const payload = { ...(params || {}) } as JsonObject;
    if (!('auth_token' in payload)) {
      payload.auth_token = this.authToken;
    }

    try {
      console.log(`[BaseBridge.invokeCommand][${callId}] About to call Tauri invoke() with command: ${command}`);
      const startTime = performance.now();

      const result = await invoke<T>(command, payload);

      const elapsed = performance.now() - startTime;
      console.log(`[BaseBridge.invokeCommand][${callId}] SUCCESS after ${elapsed.toFixed(2)}ms for command: ${command}`);
      console.log(`[BaseBridge.invokeCommand][${callId}] Result type:`, typeof result);
      console.log(`[BaseBridge.invokeCommand][${callId}] Result is array:`, Array.isArray(result));

      return result;
    } catch (error) {
      const safeNow = () => {
        try {
          return (typeof performance !== 'undefined' && performance?.now?.()) || Date.now();
        } catch {
          return Date.now();
        }
      };
      const elapsed = safeNow();
      console.error(`[BaseBridge.invokeCommand][${callId}] ERROR after ${elapsed.toFixed(2)}ms for command: ${command}`);
      console.error(`[BaseBridge.invokeCommand][${callId}] Error details:`, error);
      console.error(`[BaseBridge.invokeCommand][${callId}] Error type:`, typeof error);
      if (error instanceof Error) {
        console.error(`[BaseBridge.invokeCommand][${callId}] Error message:`, error.message);
        console.error(`[BaseBridge.invokeCommand][${callId}] Error stack:`, error.stack);
      }

      // Retry on transient errors
      if (attempt < this.retryAttempts && this.isRetryableError(error)) {
        console.log(`[BaseBridge.invokeCommand][${callId}] Retrying command ${command} (attempt ${attempt + 1}/${this.retryAttempts})`);
        await this.delay(1000 * attempt); // Exponential backoff
        return this.invokeCommand(command, params, attempt + 1);
      }

      console.error(`[BaseBridge.invokeCommand][${callId}] Throwing error for command: ${command}`);
      throw error;
    }
  }

