    let user_id = request.payload.user_id.clone();
    let mut details = std::collections::HashMap::new();
    details.insert(
        "voice_name".to_string(),
        serde_json::Value::String(voice_name.clone()),
    );

    log_voice_audit_event(
        db.inner().clone(),
        &user_id,
        "VOICE_CLONE_REQUEST",
        &format!("Voice cloning requested (name: {})", voice_name),
        details,
    )
    .await;

    run_voice_cloning(
        &script_path,
        python_exe,
        &request.payload.audio_base64,
        &voice_name,
    )
}

