    }

    let config = {
        let manager = settings_state
            .manager
            .lock()
            .map_err(|_| "Failed to lock settings manager".to_string())?;
        manager.load()
    };

    if !config.voice.voice_output {
        return Err(
            "Voice output is disabled in settings. Enable it before sending announcements."
                .to_string(),
        );
    }

    Ok(())
}

