  voice_name: string;
};

export type VoiceCloneResponse = {
  success: boolean;
  voice_id?: string;
  path?: string;
  error?: string;
};

const buildAuth = <T extends { user_id: string }>(payload: T) => ({
  auth_token: `demo_token_${payload.user_id}`,
  ...payload,
});

export const voiceStartListening = async (
  request: VoiceStartListeningRequest,
): Promise<string> => {
  return invoke("voice_start_listening", { request: buildAuth(request) });
};

export const voiceStop = async (request: VoiceStopRequest): Promise<string> => {
  return invoke("voice_stop", { request: buildAuth(request) });
};

export const voiceSynthesize = async (
  request: VoiceSynthesisRequest,
): Promise<VoiceSynthesisResponse> => {
  return invoke("voice_synthesize", { request: buildAuth(request) });
};

export const voiceClone = async (
  request: VoiceCloneRequest,
): Promise<VoiceCloneResponse> => {
  return invoke("voice_clone", { request: buildAuth(request) });
};

// --- Voice Vault Interactions ---

export type VoiceBiometrics = {
  stress_level?: number;
  speech_rate?: number;
  volume_level?: number;
  pitch_variation?: number;
  confidence?: number;
  additional_metrics?: Record<string, any>;
};

export type VoiceInteractionType =
  | "transcript"
  | "command"
  | "query"
  | "response"
  | "error";
export type VoiceInteractionState = "processing" | "completed" | "failed";

export type VoiceInteraction = {
  id: string;
  user_id: string;
  persona_id?: string;
  interaction_type: VoiceInteractionType;
  state: VoiceInteractionState;
  transcript: string;
  audio_metadata?: VoiceBiometrics;
  engine?: string;
  session_id?: string;
  created_at: string;
  duration_ms?: number;
};

export type CommunalVoiceSignal = {
  timestamp: string;
  persona_id?: string;
  biometrics: VoiceBiometrics;
};

export type GetVoiceInteractionsRequest = {
  user_id: string;
  persona_id?: string;
  interaction_type?: VoiceInteractionType;
  session_id?: string;
  limit?: number;
};

export const getVoiceInteractions = async (
  request: GetVoiceInteractionsRequest,
): Promise<VoiceInteraction[]> => {
  // The strict-auth wrapper in main.rs expects { auth_token, payload: { ... } }
  // but the `buildAuth` helper above flattens it: { auth_token, ...payload }.
  // We need to match the backend expectation.
  // Rust: AuthenticatedRequest<GetVoiceInteractionsRequest>

  // Check if other bridges use a specific wrapper.
  // Usually AuthenticatedRequest on Rust side implies: invoke('cmd', { request: { auth_token, payload } })
  // OR invoke('cmd', { auth_token, payload }) if flat.
  // Based on `BaseBridge.ts` patterns in this project, it's likely wrapped.
  // However, existing voice commands use `buildAuth` which is flat.
  // Let's look at `voice_interaction_get` signature in `voice_interactions.rs`:
  // It takes `request: AuthenticatedRequest<GetVoiceInteractionsRequest>`.

  // Correct structure for AuthenticatedRequest macros in this project is usually:
  // { request: { auth_token: "...", payload: { ... } } }

  return invoke("voice_interaction_get", {
    request: {
      auth_token: `demo_token_${request.user_id}`,
      ...request,
    },
  });
};

export type GetVoiceHistoryRequest = {
  user_id: string;
  persona_id?: string;
  limit?: number;
  offset?: number;
};

export const getVoiceHistory = async (
  request: GetVoiceHistoryRequest,
): Promise<VoiceInteraction[]> => {
  return invoke("vault_get_voice_history", {
    request: {
      auth_token: `demo_token_${request.user_id}`,
      ...request,
    },
  });
};

export const getVoiceInteractionStats = async (
  userId: string,
): Promise<any> => {
  return invoke("voice_interaction_stats", {
    request: {
      auth_token: `demo_token_${userId}`,
      user_id: userId,
    },
  });
};

export const getCommunalVoiceSignals = async (
  request: GetVoiceHistoryRequest,
): Promise<CommunalVoiceSignal[]> => {
  return invoke("vault_get_communal_voice_signals", {
    request: {
      auth_token: `demo_token_${request.user_id}`,
      ...request,
    },
  });
};

