fn is_before_since(timestamp: &str, since: Option<&DateTime<Utc>>) -> bool {
    if let Some(since_dt) = since {
        if let Ok(ts) = DateTime::parse_from_rfc3339(timestamp) {
            return ts.with_timezone(&Utc) < *since_dt;
        }
    }
    false
}

fn summarize_content(value: &Value) -> String {
    match value {
        Value::String(s) => s.clone(),
        Value::Object(map) => map
            .get("text")
            .or_else(|| map.get("message"))
            .and_then(|v| v.as_str())
            .map(|s| s.to_string())
            .unwrap_or_else(|| value.to_string()),
        _ => value.to_string(),
    }
}

fn severity_to_priority(severity: &str) -> FeedPriority {
    match severity.to_lowercase().as_str() {
        "critical" => FeedPriority::Critical,
        "high" => FeedPriority::High,
        "medium" | "warn" | "warning" => FeedPriority::Medium,
        _ => FeedPriority::Low,
    }
}

