/**
 * UnifiedChatLayout - Main 4-column chat layout matching the mockup design
 *
 * Layout Structure:
 * â”Œâ”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”¬â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”¬â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”¬â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”
 * â”‚   Persona   â”‚     Agent Bridge      â”‚   Active    â”‚   Private   â”‚
 * â”‚   Roster    â”‚   (Multi-Agent Chat)  â”‚   Agent     â”‚   Channel   â”‚
 * â”‚  (col 1)    â”‚      (col 2)          â”‚  (col 3)    â”‚   (col 4)   â”‚
 * â””â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”´â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”´â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”´â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”˜
 *
 * - Column 1: AI Personas list (system + Mimic personas)
 * - Column 2: Agent Bridge - group chat with all active AI agents
 * - Column 3: Active Agent - avatar, status, and system metrics
 * - Column 4: Private Channel - 1-on-1 chat with module's resident AI
 */
import React, { memo, useState, useCallback, useMemo } from 'react';
import { PersonaRoster } from './PersonaRoster';
import { AgentBridge } from './AgentBridge';
import { ActiveAgentPanel } from './ActiveAgentPanel';
import { PrivateChannel } from './PrivateChannel';
import { GlobalCommsStream } from '../../mythtech/tasks/GlobalCommsStream';
import { ChevronLeft, ChevronsRight, Activity, MessageSquare } from '@/components/ui/HearthlinkIcons';

import type {
  Persona,
  ChatMessage,
  SystemMetrics,
  SystemPersonaId,
} from './types';

export interface UnifiedChatLayoutProps {
  // Module context
  moduleId: string;
  userId: string;

  // Personas
  personas: Persona[];
  activePersonaId: string;
  residentPersonaId: SystemPersonaId;
  onPersonaSelect: (personaId: string) => void;

  // Group chat (Agent Bridge)
  groupMessages: ChatMessage[];
  groupLoading: boolean;
  groupError: string | null;
  groupTypingPersonas: string[];
  onSendGroupMessage: (content: string) => void;

  // Private chat
  privateMessages: ChatMessage[];
  privateLoading: boolean;
  privateError: string | null;
  privateTyping: boolean;
  onSendPrivateMessage: (content: string) => void;

  // System metrics
  metrics: SystemMetrics;

  // Layout options
  showGroupChat?: boolean;
  initialCollapsed?: boolean;

  // Navigation
  // Navigation
  onClose?: () => void;
  className?: string;

  // Features
  onFocusAssist?: () => void;
}

export const UnifiedChatLayout = memo<UnifiedChatLayoutProps>(({
  moduleId,
  userId,
  personas,
  activePersonaId,
  residentPersonaId,
  onPersonaSelect,
  groupMessages,
  groupLoading,
  groupError,
  groupTypingPersonas,
  onSendGroupMessage,
  privateMessages,
  privateLoading,
  privateError,
  privateTyping,
  onSendPrivateMessage,
  metrics,
  showGroupChat = true,
  initialCollapsed = false,
  onClose,
  className = '',
  onFocusAssist,
}) => {
  const [isPrivateCollapsed, setIsPrivateCollapsed] = useState(initialCollapsed);
  const [showGlobalStream, setShowGlobalStream] = useState(false);

  // Get active persona object
  const activePersona = useMemo(() =>
    personas.find(p => p.id === activePersonaId) || personas[0],
    [personas, activePersonaId]
  );

  // Get resident persona object
  const residentPersona = useMemo(() =>
    personas.find(p => p.id === residentPersonaId) || personas[0],
    [personas, residentPersonaId]
  );

  // Handle persona selection
  const handlePersonaSelect = useCallback((personaId: string) => {
    onPersonaSelect(personaId);
  }, [onPersonaSelect]);

  // Toggle private channel collapse
  const togglePrivateCollapse = useCallback(() => {
    setIsPrivateCollapsed(prev => !prev);
  }, []);

  // Calculate grid columns based on collapsed state
  const gridCols = isPrivateCollapsed
    ? '260px 1fr 400px 96px'  // Collapsed private (matches w-24)
    : '260px 1fr 400px 360px'; // Expanded (matches w-[360px])

  const themeRGB = useMemo(() => {
    const themes: Record<string, string> = {
      alden: '214, 179, 105', // Gold
      mimic: '168, 85, 247',  // Purple
      alice: '45, 212, 191',  // Teal
      sentry: '239, 68, 68',  // Red
    };
    return themes[moduleId.toLowerCase()] || themes.alden;
  }, [moduleId]);

  // Define CSS variable for children to consume
  const containerStyle = {
    display: 'grid',
    gridTemplateColumns: gridCols,
    // alignItems: 'start', // REMOVED to restore stretch
    // gap: '4px', // REMOVED to match contiguous sidebar look
    '--chat-accent-rgb': themeRGB,
    '--chat-accent-color': `rgb(${themeRGB})`,
  } as React.CSSProperties;

  // Sidebar chrome style matching UnifiedCommandCenter.optimized.tsx
  const chatFrameChrome: React.CSSProperties = {
    // border: '1px solid rgba(0, 205, 255, 0.55)', // REMOVED to fix 1px alignment shift vs other columns
    borderLeft: '1px solid rgba(0, 205, 255, 0.55)',
    boxShadow: '0 0 28px rgba(0, 240, 255, 0.18), inset 0 0 18px rgba(197,160,89,0.16)', // Updated to match sidebar
    background: 'linear-gradient(135deg, rgba(11,17,36,0.92), rgba(0,240,255,0.06))',
  };

  return (
    <div
      className={`h-full w-full bg-[#020617] text-[#c5d2e8] font-sans overflow-hidden relative ${className}`}
      style={containerStyle}
    >
      {/* Global glassmorphism overlay */}
      <div className="absolute inset-0 bg-gradient-to-br from-[#0b1628]/10 via-transparent to-[#0b1628]/10 pointer-events-none z-0" />

      {/* Column 1: Persona Roster */}
      <div
        className="border-r-2 relative z-10 flex flex-col h-full overflow-hidden mt-0 pt-0"
        style={{ borderColor: `rgba(${themeRGB}, 0.3)` }}
      >
        <div className="flex-1 min-h-0 h-full">
          <PersonaRoster
            personas={personas}
            activePersonaId={activePersonaId}
            onPersonaSelect={handlePersonaSelect}
          />
        </div>
      </div>

      {/* Column 2: Agent Bridge (Multi-Agent Chat) */}
      <div
        className="border-r-2 relative z-10 min-w-0 flex flex-col h-full overflow-hidden mt-0 pt-0"
        style={{ borderColor: `rgba(${themeRGB}, 0.3)` }}
      >
        <div className="flex-1 min-h-0 h-full relative">
          {showGroupChat ? (
            <AgentBridge
              messages={groupMessages}
              personas={personas}
              isLoading={groupLoading}
              error={groupError}
              typingPersonas={groupTypingPersonas}
              onSendMessage={onSendGroupMessage}
            />
          ) : (
            <PrivateChannel
              residentPersona={residentPersona}
              messages={privateMessages}
              isLoading={privateLoading}
              error={privateError}
              isTyping={privateTyping}
              onSendMessage={onSendPrivateMessage}
              isCollapsed={false}
              hideCollapseToggle={true}

