import React from 'react';
import { UnifiedChatContainer } from './UnifiedChatContainer';
import type { SystemPersonaId } from './types';
import { MODULE_RESIDENT_AI } from './types';

export interface ExpandedChatProps {
  userId: string;
  moduleId?: string;
  onClose?: () => void;
  className?: string;
}

export const ExpandedChat: React.FC<ExpandedChatProps> = ({
  userId,
  moduleId = 'alden',
  onClose,
  className = '',
}) => {
  // Resolve resident persona based on module ID
  const normalizedModuleId = moduleId.toLowerCase();
  const residentPersonaId = (MODULE_RESIDENT_AI[normalizedModuleId] || 'alden') as SystemPersonaId;

  return (
    <UnifiedChatContainer
      userId={userId}
      moduleId={moduleId}
      residentPersonaId={residentPersonaId}
      onClose={onClose}
      className={className}
    />
  );
};

export default ExpandedChat;

