/**
 * UnifiedChatContainer
 *
 * Container component that wires UnifiedChatLayout to the Orchestration Backend.
 * Uses useChatStateWithOrchestration to manage multi-agent delegation and state.
 *
 * This component:
 * 1. Manages chat state via useChatStateWithOrchestration
 * 2. Handles message delegation to appropriate agents
 * 3. Updates active persona and system metrics
 * 4. Provides real-time typing indicators and status updates
 *
 * @example
 * ```tsx
 * <UnifiedChatContainer
 *   userId="user123"
 *   moduleId="alden"
 *   residentPersonaId="alden"
 * />
 * ```
 */

import React from 'react';
import { UnifiedChatLayout } from './UnifiedChatLayout';
import { useChatStateWithOrchestration } from './useChatStateWithOrchestration';
import type { SystemPersonaId } from './types';

export interface UnifiedChatContainerProps {
  userId: string;
  moduleId: string;
  residentPersonaId: SystemPersonaId;
  onClose?: () => void;
  className?: string;
}

/**
 * Container component for UnifiedChatLayout with Orchestration integration
 */
export function UnifiedChatContainer({
  userId,
  moduleId,
  residentPersonaId,
  onClose,
  className = '',
