      // Private Chat
      privateMessages={privateMessages}
      privateLoading={privateLoading}
      privateError={privateError}
      privateTyping={privateTyping}
      onSendPrivateMessage={sendPrivateMessage}

      // Metrics
      metrics={metrics}

      // Layout configuration
      showGroupChat={true}
      initialCollapsed={false}
      onClose={onClose}
      className={className}
    />
  );
}

