import React, { useState, useEffect, useCallback, Suspense } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { MessageSquare, Minimize2 } from "@/components/ui/HearthlinkIcons";
import { useZenMode } from "../context/ZenContext";

// Enhanced Components
import EnhancedLaunchPage from "./EnhancedLaunchPage";
import EnhancedTaskDashboard from "./EnhancedTaskDashboard";
import PersonaManager from "./PersonaManager";
import EmailManager from "./EmailManager";
import { HearthlinkLoadingSequence } from "./shared";

// Authentication
import { useAuth } from "../contexts/AuthContext";
import { LoginScreen } from "./auth/LoginScreen";

// ADHD UI System
import { ADHDThemeProvider } from "./ui/ADHDOptimizedInterface";

// Legacy Component Wrappers
// eslint-disable-next-line import/no-named-as-default
import LegacyComponentWrapper from "./LegacyComponentWrapper";

// Shared Chat Imports
import { PrivateChannel } from "./shared/chat/PrivateChannel";
import { useChatState } from "./shared/chat/useChatState";
import { MODULE_RESIDENT_AI, SystemPersonaId } from "./shared/chat/types";
import { SettingsPanelEnhanced } from "./settings/SettingsPanelEnhanced";
import { useSettingsBridge } from "../hooks/useBridge";
import { AliceDeepWorkSession } from "./alice/AliceDeepWorkSession";
import { setAppReadyState } from "../utils/appReady";

// Voice Components
import { VoiceOrb } from "./voice/VoiceOrb";
import { useVoiceState } from "../context/VoiceStateContext";

// Lazy Load Components
const HierarchicalLaunchPage = React.lazy(
  () => import("./HierarchicalLaunchPage"),
);
const AldenCommandCenter = React.lazy(
  () => import("./alden/AldenCommandCenter"),
);
const AliceCommandCenter = React.lazy(
  () => import("./alice/AliceCommandCenter"),
);
const MimicCommandCenter = React.lazy(
  () => import("./mimic/MimicCommandCenter"),
);
const SentryCommandCenter = React.lazy(
  () => import("./sentry/SentryCommandCenter"),
);
const SynapseCommandCenter = React.lazy(
  () => import("./synapse/SynapseCommandCenter"),
);
const ExpandedChat = React.lazy(() => import("./shared/chat/ExpandedChat"));

// Named Exports Lazy Load
const VaultDashboard = React.lazy(() =>
  import("./vault/VaultDashboard").then((module) => ({
    default: module.VaultDashboard,
  })),
);
const CoreDashboard = React.lazy(() =>
  import("./core/CoreDashboard").then((module) => ({
    default: module.CoreDashboard,
  })),
);

export interface UnifiedHearthlinkAppProps {
  userId?: string;
  authToken?: string;
  focusFormulaLicenseToken?: string;
  onError?: (error: string) => void;
  onBackToLauncher?: () => void;
}

interface AppState {
  currentView: string;
  userData: Record<string, unknown> | null;
  isAuthenticated: boolean;
  backendAvailable: boolean;
  activeModule: string | null;
  navigationHistory: string[];
}

const HEARTHLINK_ICON_SIZE = 192;

const UnifiedHearthlinkApp: React.FC<UnifiedHearthlinkAppProps> = ({
  userId: propUserId,
  authToken,
  focusFormulaLicenseToken: _focusFormulaLicenseToken,
  onError,
  onBackToLauncher,
}) => {
  // --- Zen Mode ---
  const { isZenMode, toggleZenMode } = useZenMode();

  // --- Auth Integration ---
  const {
    userId: authUserId,
    isAuthenticated: isAuthenticatedFromContext,
    isLoading: authLoading,
    isDemoMode,
    enableDemoMode,
  } = useAuth();

  // Use authenticated userId or fallback to prop
  const userId = authUserId || propUserId || "demo_user";

  const location = useLocation();

  // Sync State with URL (Deep Linking & Back/Forward Support)
  useEffect(() => {
    const path = location.pathname.replace(/^\//, "").toLowerCase();
    const validViews = [
      "alden",
      "alice",
      "mimic",
      "sentry",
      "vault",
      "synapse",
      "core",
      "launch",
    ];

    if (path && validViews.includes(path)) {
      setAppState((prev) => {
        if (prev.currentView === path) return prev;
        return {
          ...prev,
          currentView: path,
          activeModule: path === "launch" ? null : path,
          navigationHistory: [...prev.navigationHistory, path],
        };
      });
    }
  }, [location.pathname]);

  const [appState, setAppState] = useState<AppState>({
    currentView: "launch",
    userData: null,
    isAuthenticated: isAuthenticatedFromContext,
    backendAvailable: false,
    activeModule: null,
    navigationHistory: [],
  });

  const [loading, setLoading] = useState(true);
  const [showSettings, setShowSettings] = useState(false);
  const [isFocusAssistOpen, setIsFocusAssistOpen] = useState(false);
  const [isInitialBoot, setIsInitialBoot] = useState(true);
  const { isOrbVisible: showVoiceOrb, setOrbVisible: setShowVoiceOrb } =
    useVoiceState();
  const handleBootSequenceComplete = useCallback(
    () => setIsInitialBoot(false),
    [],
  );

  // --- ADHD Settings Integration ---
  const { props: settingsProps } = useSettingsBridge(userId);
  const accessibility = settingsProps?.settings?.accessibility;

  const adhdConfig = React.useMemo(
    () => ({
      reduceMotion: accessibility?.reduceMotion ?? false,
      useHighContrast: accessibility?.highContrast ?? false,
      enableFocusIndicators: accessibility?.focusIndicators ?? true,
      minimizeClutter: accessibility?.minimizeClutter ?? false,
    }),
    [
      accessibility?.reduceMotion,
      accessibility?.highContrast,
      accessibility?.focusIndicators,
      accessibility?.minimizeClutter,
    ],
  );

  // --- Global Chat State Management ---
  const [isChatCollapsed, setIsChatCollapsed] = useState(false);
  const [isExpandedChatOpen, setIsExpandedChatOpen] = useState(false);
  const currentModuleId = appState.activeModule || "alden";
  const residentPersonaId = (MODULE_RESIDENT_AI[
    currentModuleId.toLowerCase()
  ] || "alden") as SystemPersonaId;

  const {
    personas,
    privateMessages,
    privateLoading,
    privateError,
    privateTyping,
    sendPrivateMessage,
  } = useChatState({
    userId,
    moduleId: currentModuleId,
    residentPersonaId,
    onError: (err) => console.error("Global Chat Error:", err),
  });

  const residentPersona =
    personas.find((p) => p.id === residentPersonaId) || personas[0];
  const sidebarWidth = isChatCollapsed ? "80px" : "380px";

  // ... (existing code)

  const renderNavigationBar = () => (
    <nav
      aria-label="Main navigation"
      className="bg-card border-b border-border px-6 py-3 flex items-center justify-between sticky top-0 z-40"
    >
      <div className="flex items-center space-x-4">
        <ul className="flex items-center space-x-4" role="list">
          <li>
            <button
              onClick={() => navigateTo("launch")}
              className="text-primary hover:text-white transition-colors"
            >
              Hearthlink
            </button>
          </li>

          {appState.navigationHistory.length > 0 && (
            <li>
              <button
                onClick={navigateBack}
                className="text-gray-400 hover:text-white transition-colors"
              >
                Back
              </button>
            </li>
          )}
        </ul>

        {appState.activeModule && (
          <span className="text-gray-400">/ {appState.activeModule}</span>
        )}
      </div>

      <div className="flex items-center space-x-4">
        {/* Zen Mode Toggle */}
        <button
          onClick={toggleZenMode}
          data-testid="zen-mode-toggle"
          className={`p-1.5 rounded-full transition-colors flex items-center gap-2 ${
            isZenMode
              ? "bg-slate-700 text-slate-300"
              : "hover:bg-gray-700 text-gray-400 hover:text-white"
          }`}
          aria-label={
            isZenMode ? "Disable Visual Silence" : "Enable Visual Silence"
          }
          title={
            isZenMode
              ? "Disable Visual Silence"
              : "Enable Visual Silence (Ctrl+Shift+Z)"
          }
        >
          <Minimize2 size={16} />
          {isZenMode && <span className="text-xs font-medium">Zen</span>}
        </button>

        <span className="text-sm text-gray-400">
          {appState.isAuthenticated ? `User: ${userId}` : "Not authenticated"}
        </span>

        <div className="flex items-center space-x-2">
          <span
            className={`w-2 h-2 rounded-full ${
              appState.backendAvailable ? "bg-green-500" : "bg-yellow-500"
            }`}
          ></span>
          <span className="text-xs text-gray-400">
            {appState.backendAvailable ? "Backend Ready" : "Limited Mode"}
          </span>
        </div>
      </div>
    </nav>
  );

  // --- Local Error State ---
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    setAppReadyState(!loading && !error);
  }, [loading, error]);

  useEffect(() => {
    // Check backend connection
    const checkBackend = async () => {
      try {
        // Mock backend check for now or import SystemBridge if available
        // const isConnected = await SystemBridge.checkHealth();
        // setAppState(prev => ({ ...prev, backendAvailable: isConnected }));
        setLoading(false);
      } catch (err) {
        console.warn("Backend connection check failed:", err);
        setLoading(false);
      }
    };
    checkBackend();
  }, []);

  const navigateTo = (view: string) => {
    // Normalize view: handle '/alden', 'Alden', 'alden' -> 'alden'
    const normalizedView = view.toLowerCase().replace(/^\//, "");

    // Handle special cases if any (like Settings opening a modal instead of navigating)
    if (normalizedView === "settings") {
      setShowSettings(true);
      return;
    }

    setAppState((prev) => ({
      ...prev,
      currentView: normalizedView,
      activeModule: normalizedView === "launch" ? null : normalizedView,
      navigationHistory: [...prev.navigationHistory, prev.currentView],
    }));
  };

  const navigateBack = () => {
    setAppState((prev) => {
      const newHistory = [...prev.navigationHistory];
      const lastView = newHistory.pop() || "launch";
      return {
        ...prev,
        currentView: lastView,
        activeModule: lastView === "launch" ? null : lastView,
        navigationHistory: newHistory,
      };
    });
  };

  const renderMainContent = () => {
    switch (appState.currentView) {
      case "alden":
        return (
          <AldenCommandCenter
            userId={userId}
            onNavigateToModule={navigateTo}
            onBackToLauncher={() => navigateTo("launch")}
          />
        );
      case "alice":
        return (
          <AliceCommandCenter
            userId={userId}
            onNavigateToModule={navigateTo}
            onBackToLauncher={() => navigateTo("launch")}
          />
        );
      case "mimic":
        return (
          <MimicCommandCenter
            userId={userId}
            onNavigateToModule={navigateTo}
            onBackToLauncher={() => navigateTo("launch")}
          />
        );
      case "sentry":
        return (
          <SentryCommandCenter
            userId={userId}
            onNavigateToModule={navigateTo}
            onBackToLauncher={() => navigateTo("launch")}
          />
        );
      case "vault":
        return (
          <VaultDashboard
            userId={userId}
            onNavigateToModule={navigateTo}
            onBackToLauncher={() => navigateTo("launch")}
          />
        );
      case "synapse":
        return (
          <SynapseCommandCenter
            userId={userId}
            onNavigateToModule={navigateTo}
            onBackToLauncher={() => navigateTo("launch")}
          />
        );
      case "core":
        return (
          <CoreDashboard
            userId={userId}
            onNavigateToModule={navigateTo}
            onBackToLauncher={() => navigateTo("launch")}
          />
        );
      case "launch":
      default:
        return (
          <HierarchicalLaunchPage
            onModuleSelect={navigateTo}
            onOpenQuickCapture={() => console.log("Quick Capture")}
            onToggleVoiceOrb={() => setShowVoiceOrb(!showVoiceOrb)}
          />
        );
    }
  };

  // Show login screen if not authenticated and not loading
  if (!authLoading && !isAuthenticatedFromContext) {
    return (
      <ADHDThemeProvider>
        <LoginScreen
          onLoginSuccess={() => {
            setAppState((prev) => ({ ...prev, isAuthenticated: true }));
          }}
          onSkipToDemo={enableDemoMode}
        />
      </ADHDThemeProvider>
    );
  }

  return (
    <ADHDThemeProvider
      config={adhdConfig}
      key={`${adhdConfig.reduceMotion}-${adhdConfig.useHighContrast}-${adhdConfig.enableFocusIndicators}-${adhdConfig.minimizeClutter}`}
    >
      <div
        className="min-h-screen bg-background text-white relative font-sans flex flex-col overflow-hidden"
        data-testid={!loading ? "app-ready" : "app-loading"}
      >
        {!loading && !error && renderNavigationBar()}

        {/* Demo Mode Indicator */}
        {isDemoMode && (
          <div className="fixed top-2 left-1/2 -translate-x-1/2 z-50 px-4 py-1 bg-amber-500/20 border border-amber-500/50 rounded-full text-amber-300 text-xs font-medium">
            Demo Mode
          </div>
        )}

        {/* Main Content Area - Flexes to avoid Sidebar overlap */}
        <div
          className="relative flex-1 transition-all duration-300 ease-in-out h-[calc(100vh-64px)] overflow-auto"
          style={{ marginRight: sidebarWidth }}
        >
          <Suspense
            fallback={
              <div className="h-full w-full flex items-center justify-center">
                <HearthlinkLoadingSequence
                  variant={isInitialBoot ? "video" : "pulse"}
                  onComplete={handleBootSequenceComplete}
                />
              </div>
            }
          >
            {renderMainContent()}
          </Suspense>
        </div>

        {/* Global Persistent Chat Sidebar */}
        {!loading && !error && !isExpandedChatOpen && (
          <div
            className="fixed right-0 top-[0px] h-full z-[50] border-l border-border bg-card shadow-xl transition-all duration-300 ease-in-out pt-[64px]"
            style={{
              width: sidebarWidth,
              top: "64px",
              height: "calc(100vh - 64px)",
            }}
          >
            <PrivateChannel
              residentPersona={residentPersona}
              messages={privateMessages}
              isLoading={privateLoading}
              error={privateError}
              isTyping={privateTyping}
              onSendMessage={sendPrivateMessage}
              isCollapsed={isChatCollapsed}
              onToggleCollapse={() => setIsChatCollapsed(!isChatCollapsed)}
              onStandardView={() => setIsExpandedChatOpen(true)}
              className="h-full border-none"
              // Collapsed sidebar controls
              onFocusAssist={() => {
                setShowSettings(false);
                setIsExpandedChatOpen(false);
                setIsFocusAssistOpen(true);
              }}
            />
          </div>
        )}

        {/* Expanded 4-Column Chat View (Modal/Overlay) */}
        {!loading && !error && isExpandedChatOpen && (
          <div className="fixed inset-0 z-[100] bg-background">
            <Suspense
              fallback={
                <div className="h-full w-full flex items-center justify-center">
                  <HearthlinkLoadingSequence
                    variant={isInitialBoot ? "video" : "pulse"}
                    onComplete={handleBootSequenceComplete}
                  />
                </div>
              }
            >
              <ExpandedChat
                userId={userId}
                moduleId={currentModuleId}
                onClose={() => setIsExpandedChatOpen(false)}
                className="h-full w-full"
              />
            </Suspense>
          </div>
        )}

        {/* Global Settings Modal */}
        {showSettings && (
          <div className="fixed inset-0 z-[200] bg-background">
            <SettingsPanelEnhanced onClose={() => setShowSettings(false)} />
          </div>
        )}

        {/* Global Error Handler */}
        {error && (
          <div className="fixed bottom-4 right-4 bg-red-900/20 border border-red-400 text-red-400 px-4 py-2 rounded max-w-md z-[60]">
            <p className="text-sm">{error}</p>

