/// Helper function for OAuth2 token refresh
///
/// This function performs a token refresh using client credentials flow.
/// It can be called internally by the plugin or used by the credential manager.
///
/// # Arguments
/// * `client_id` - OAuth2 client ID
/// * `client_secret` - OAuth2 client secret
/// * `token_url` - OAuth2 token endpoint URL
///
/// # Returns
/// A tuple of (access_token, expires_in_seconds)
pub async fn refresh_oauth2_token(
    client_id: &str,
    client_secret: &str,
    token_url: &str,
) -> Result<(String, u64), String> {
    let client = reqwest::Client::new();

    let form_data = [
        ("client_id", client_id),
        ("client_secret", client_secret),
        ("grant_type", "client_credentials"),
    ];

    let response = client
        .post(token_url)
        .form(&form_data)
        .send()
        .await
        .map_err(|e| format!("Token refresh request failed: {e}"))?;

    let status = response.status();
    if !status.is_success() {
        return Err(format!("HTTP {}: Token refresh failed", status));
    }

    let json: serde_json::Value = response
        .json()
        .await
        .map_err(|e| format!("Failed to parse token response: {e}"))?;

    let access_token = json
        .get("access_token")
        .and_then(|v| v.as_str())
        .ok_or("Missing access_token in response")?
        .to_string();

    let expires_in = json
        .get("expires_in")
        .and_then(|v| v.as_u64())
        .unwrap_or(1800); // Default to 30 minutes if not specified

    Ok((access_token, expires_in))
}

/// Helper function to check if token needs refresh
///
/// Returns true if the token is within 20% of its expiry time.
pub fn token_needs_refresh(expires_at: i64) -> bool {
    let now = chrono::Utc::now().timestamp();
    let remaining = expires_at - now;
    let threshold = (expires_at - now) / 5; // 20% threshold
    remaining <= threshold
}

