use crate::key_manager::SecureKeyManager;
use crate::synapse::encryption::EncryptedCredentials;
use crate::synapse::models::{VendorAuthStrategy, VendorCredentialConfig};

const HKDF_INFO: &[u8] = "vendor_cred".as_bytes();
const NONCE_SIZE: usize = 12; // 96 bits for GCM

/// Unified gateway for all external vendor API calls.
/// Enforces credential isolation and request signing.
pub struct VendorGateway {
    client: Client,
    key_manager: Arc<SecureKeyManager>,
}

impl VendorGateway {
    pub fn new(key_manager: Arc<SecureKeyManager>) -> Result<Self, String> {
        let client = Client::builder()
            .timeout(Duration::from_secs(30))
            .build()
            .map_err(|e| format!("Failed to build HTTP client: {e}"))?;

        Ok(Self {
            client,
            key_manager,
        })
    }

    /// Execute an authenticated request to a vendor API.
    /// Credentials are decrypted at call-time and never persisted in memory.
    pub async fn execute(
        &self,
        plugin_id: &str,
        method: Method,
        url: &str,
        config: &VendorCredentialConfig,
        body: Option<serde_json::Value>,
    ) -> Result<Response, String> {
        if config.plugin_id != plugin_id {
            return Err("Cross-plugin credential access blocked".to_string());
        }

        let response = {
            let creds = self
                .decrypt_credentials(plugin_id, &config.encrypted_blob)
                .await?;

            let mut request = self.client.request(method, url);
            request = self.apply_auth(request, &config.auth_strategy, &creds)?;

            if let Some(payload) = body {
                request = request.json(&payload);
            }

            request
                .send()
                .await
                .map_err(|e| format!("Vendor request failed: {e}"))?
        };

        Ok(response)
    }

    fn apply_auth(
        &self,
        mut request: RequestBuilder,
        strategy: &VendorAuthStrategy,
        creds: &HashMap<String, SecretString>,
    ) -> Result<RequestBuilder, String> {
        match strategy {
            VendorAuthStrategy::ApiKeyHeader { header_name } => {
                let key = creds.get("api_key").ok_or("Missing api_key")?;
                request = request.header(header_name.as_str(), key.expose_secret());
            }
            VendorAuthStrategy::BearerToken => {
                let token = creds.get("access_token").ok_or("Missing access_token")?;
                request = request.bearer_auth(token.expose_secret());
            }
            VendorAuthStrategy::OAuth2ClientCredentials { .. } => {
                let token = creds.get("access_token").ok_or("Missing access_token")?;
                request = request.bearer_auth(token.expose_secret());
            }
            VendorAuthStrategy::BasicAuth => {
                let user = creds.get("username").ok_or("Missing username")?;
                let pass = creds.get("password").ok_or("Missing password")?;
                request = request.basic_auth(user.expose_secret(), Some(pass.expose_secret()));
            }
        }

        Ok(request)
    }

    async fn decrypt_credentials(
        &self,
        plugin_id: &str,
        encrypted_blob: &[u8],
    ) -> Result<HashMap<String, SecretString>, String> {
        let (encrypted, _): (EncryptedCredentials, usize) =
            bincode::decode_from_slice(encrypted_blob, standard())
                .map_err(|e| format!("Failed to deserialize encrypted data: {e}"))?;

        if encrypted.nonce.len() != NONCE_SIZE {
            return Err("Invalid nonce size".to_string());
        }

        let mut master_key = self
            .key_manager
            .get_vault_key()
            .await
            .map_err(|e| format!("Failed to get encryption key: {e}"))?;

        let mut derived_key = self.derive_plugin_key(&master_key, plugin_id)?;
        master_key.zeroize();

        let cipher = Aes256Gcm::new_from_slice(&derived_key)
            .map_err(|e| format!("Failed to create cipher: {e}"))?;
        derived_key.zeroize();

        let nonce = Nonce::from_slice(&encrypted.nonce);

        let plaintext = cipher
            .decrypt(nonce, encrypted.ciphertext.as_ref())
            .map_err(|e| format!("Decryption failed: {e}"))?;

        let mut json_str =
            String::from_utf8(plaintext).map_err(|e| format!("Invalid UTF-8: {e}"))?;
        let parsed: HashMap<String, String> = serde_json::from_str(&json_str)
            .map_err(|e| format!("Failed to deserialize credentials: {e}"))?;
        json_str.zeroize();

        let mut secrets = HashMap::new();
        for (key, value) in parsed {
            secrets.insert(key, SecretString::new(value));
        }

        Ok(secrets)
    }

    fn derive_plugin_key(&self, master_key: &[u8], plugin_id: &str) -> Result<Vec<u8>, String> {
        let hkdf = Hkdf::<Sha256>::new(Some(plugin_id.as_bytes()), master_key);
        let mut derived_key = vec![0u8; 32];
        hkdf.expand(HKDF_INFO, &mut derived_key)
            .map_err(|_| "HKDF expansion failed".to_string())?;
        Ok(derived_key)
    }
}

