+fn parse_search_status(status_json: &serde_json::Value) -> (bool, Option<String>) {
+    let entry = status_json
+        .get("entry")
+        .and_then(|v| v.as_array())
+        .and_then(|entries| entries.first());
+
+    let content = entry.and_then(|v| v.get("content"));
+    let is_done = parse_splunk_bool(content.and_then(|v| v.get("isDone"))).unwrap_or(false);
+    let dispatch_state = content
+        .and_then(|v| v.get("dispatchState"))
+        .and_then(|v| v.as_str())
+        .map(|value| value.to_string());
+
+    (is_done, dispatch_state)
+}
