+fn parse_splunk_bool(value: Option<&serde_json::Value>) -> Option<bool> {
+    match value {
+        Some(serde_json::Value::Bool(val)) => Some(*val),
+        Some(serde_json::Value::Number(val)) => val.as_i64().map(|num| num != 0),
+        Some(serde_json::Value::String(val)) => {
+            let normalized = val.trim().to_ascii_lowercase();
+            match normalized.as_str() {
+                "true" | "1" | "yes" => Some(true),
+                "false" | "0" | "no" => Some(false),
+                _ => None,
+            }
+        }
+        _ => None,
+    }
