import { AldenTraits } from "./agents";

// ============================================================================
// Agent Builder Type Definitions
// based on "Agent Builder System â€“ Full Expanded Specification"
// ============================================================================

export type BuilderPhase =
  | "role"
  | "thinking"
  | "communication"
  | "traits"
  | "behavior"
  | "identity"
  | "review";

export const BUILDER_PHASE_ORDER: BuilderPhase[] = [
  "role",
  "thinking",
  "communication",
  "traits",
  "behavior",
  "identity",
  "review",
];

// Phase 1: Role
export type AgentRoleId =
  | "coordinator"
  | "analyst"
  | "creator"
  | "diplomat"
  | "engineer"
  | "director";

export interface AgentRoleDef {
  id: AgentRoleId;
  label: string;
  subLabel: string;
  description: string;
  iconName: string; // Map to icon component
  colorTheme: string; // Tailwind class prefix, e.g. "blue"
  defaultTraits: AldenTraits;
}

// Phase 2: Thinking Style
export type ThinkingStyleId =
  | "analytical"
  | "creative"
  | "empathetic"
  | "strategic";

export interface ThinkingStyleDef {
  id: ThinkingStyleId;
  label: string;
  description: string;
  promptFragment: string; // System prompt injection
}

// Phase 3: Communication Style
export type CommunicationStyleId = "formal" | "casual" | "socratic" | "direct";

export interface CommunicationStyleDef {
  id: CommunicationStyleId;
  label: string;
  description: string;
  promptFragment: string;
}

// Phase 4: Special Traits
export type SpecialTraitId =
  | "clarifier"
  | "devi_advocate"
  | "translator"
  | "archivist";

export interface SpecialTraitDef {
  id: SpecialTraitId;
  label: string;
  description: string;
  promptFragment: string;
  excludes?: SpecialTraitId[]; // Mutually exclusive traits
}

// Phase 5: Behavior Profile (Traits)
// Re-uses AldenTraits, but we need metadata for the UI sliders
export interface TraitMetadata {
  key: keyof AldenTraits;
  label: string;
  lowLabel: string;
  highLabel: string;
}

// Phase 6: Identity & Relationship
export type RelationshipStance =
  | "tool"
  | "collaborator"
  | "mentor"
  | "subordinate";
export type ConfidencePosture = "humble" | "balanced" | "assertive";

export interface IdentityConfig {
  name: string;
  avatar: string; // Path
  relationshipStance: RelationshipStance;
  confidencePosture: ConfidencePosture;
}

// Complete Builder State
export interface AgentBuilderState {
  currentPhase: BuilderPhase;
  // Selections
  selectedRole: AgentRoleId | null;
  selectedThinking: ThinkingStyleId | null;
  selectedCommunication: CommunicationStyleId | null;
  selectedTraits: SpecialTraitId[];
  behavioralProfile: AldenTraits; // Defaults seeded by Role
  identity: IdentityConfig;

  // Meta
  isManualMode: boolean; // For overrides
  manualSystemPrompt?: string; // If overwritten
}

// Final Agent Profile (Stored in DB/Vault)
export interface AgentProfile {
  id: string;
  version: number;
  isDeployed: boolean;

  // Configuration
  roleId: AgentRoleId;
  thinkingStyleId: ThinkingStyleId;
  communicationStyleId: CommunicationStyleId;
  specialTraitIds: SpecialTraitId[];
  behavior: AldenTraits;
  identity: IdentityConfig;

  // Derived
  systemPrompt: string;
  createdAt: string;
  updatedAt: string;
}

