          border: "border-cyan-500/50",
          bg: "bg-cyan-500/10",
        };
      case "creative":
        return {
          icon: <Zap className="w-8 h-8" />,
          color: "text-purple-400",
          border: "border-purple-500/50",
          bg: "bg-purple-500/10",
        };
      case "empathetic":
        return {
          icon: <Brain className="w-8 h-8" />,
          color: "text-rose-400",
          border: "border-rose-500/50",
          bg: "bg-rose-500/10",
        };
      case "strategic":
        return {
          icon: <Network className="w-8 h-8" />,
          color: "text-amber-400",
          border: "border-amber-500/50",
          bg: "bg-amber-500/10",
        };
      default:
        return {
          icon: <Brain />,
          color: "text-slate-400",
          border: "border-slate-500",
          bg: "bg-slate-500/10",
        };
    }
  };

  return (
    <div className="h-full flex flex-col animate-in fade-in slide-in-from-right-4 duration-300">
      <div className="text-center mb-10">
        <h3 className="text-3xl font-black text-white font-orbitron-force uppercase tracking-widest mb-3">
          Cognitive Architecture
        </h3>
        <p className="text-slate-400 max-w-lg mx-auto leading-relaxed">
          How should this agent decompose problems? This choice fundamentally
          alters the reasoning chain and error handling posture.
        </p>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6 flex-1 max-w-4xl mx-auto w-full">
        {THINKING_STYLES.map((style) => {
          const isSelected = selected === style.id;
          const vis = getVisuals(style.id);

          return (
            <button
              key={style.id}
              onClick={() => onSelect(style.id)}
              className={`relative p-8 rounded-2xl border-2 text-left transition-all duration-300 flex flex-col justify-between
                ${
                  isSelected
                    ? `${vis.bg} ${vis.border} shadow-[0_0_30px_rgba(0,0,0,0.2)] scale-[1.02]`
                    : "bg-slate-900/40 border-white/5 hover:bg-slate-900/80 hover:border-white/10"
                }
              `}
            >
              <div>
                <div
                  className={`mb-6 p-4 rounded-xl bg-slate-950/50 w-min ${vis.color} shadow-inner`}
                >
                  {vis.icon}
                </div>

                <h4
                  className={`text-xl font-bold uppercase font-orbitron-force mb-2 ${isSelected ? "text-white" : "text-slate-300"}`}
                >
                  {style.label}
                </h4>

                <p
                  className={`text-sm leading-relaxed ${isSelected ? "text-slate-200" : "text-slate-400"}`}
                >
                  {style.description}
                </p>
              </div>

              <div
                className={`mt-6 pt-6 border-t ${isSelected ? "border-white/10" : "border-white/5"}`}
              >
                <span
                  className={`text-[10px] font-mono uppercase tracking-widest ${vis.color}`}
                >
                  {isSelected ? "Archtecture Selected" : "Click to Initiate"}
                </span>
              </div>
            </button>
          );
        })}
      </div>
    </div>
  );
};

