import React, { useCallback, useRef } from "react";
import { TRAIT_METADATA } from "../AgentBuilderData";
import { AldenTraits } from "@/types/agents";

interface PhaseBehaviorProps {
  profile: AldenTraits;
  onChange: (key: keyof AldenTraits, value: number) => void;
}

const PhaseBehavior: React.FC<PhaseBehaviorProps> = ({ profile, onChange }) => {
  const padRef = useRef<HTMLDivElement | null>(null);

  const updateFromPointer = useCallback(
    (clientX: number, clientY: number) => {
      const rect = padRef.current?.getBoundingClientRect();
      if (!rect) return;

      const clampedX = Math.min(Math.max(0, clientX - rect.left), rect.width);
      const clampedY = Math.min(Math.max(0, clientY - rect.top), rect.height);

      const verbosity =
        Math.min(
          100,
          Math.max(0, Math.round((clampedX / rect.width) * 10) * 10),
        ); // rounded to tens
      const warmth = Math.min(
        100,
        Math.max(
          0,
          Math.round((100 - (clampedY / rect.height) * 100) / 10) * 10,
        ),
      );

      onChange("extraversion", verbosity);
      onChange("agreeableness", warmth);
    },
    [onChange],
  );

  const handlePadPointer = useCallback(
    (event: React.PointerEvent<HTMLDivElement>) => {
      event.preventDefault();
      updateFromPointer(event.clientX, event.clientY);

      const handleMove = (moveEvent: PointerEvent) => {
        updateFromPointer(moveEvent.clientX, moveEvent.clientY);
      };
      const handleUp = () => {
        window.removeEventListener("pointermove", handleMove);
        window.removeEventListener("pointerup", handleUp);
      };

      window.addEventListener("pointermove", handleMove);
      window.addEventListener("pointerup", handleUp);
    },
    [updateFromPointer],
  );

  const getMeta = (key: keyof AldenTraits) =>
    TRAIT_METADATA.find((m) => m.key === key);

  const sliderKeys: Array<keyof AldenTraits> = [
    "openness",
    "conscientiousness",
    "emotional_stability",
  ];

  return (
    <div className="h-full flex flex-col animate-in fade-in slide-in-from-right-4 duration-300">
      <div className="text-center mb-6">
        <h3 className="text-2xl md:text-3xl font-black text-white font-orbitron-force uppercase tracking-widest mb-2">
          Behavioral Tuning
        </h3>
        <p className="text-slate-400 max-w-2xl mx-auto leading-relaxed text-sm md:text-base">
          Fine-tune expression parameters. Note: Extreme values may reduce model stability.
        </p>
      </div>

      <div className="max-w-5xl mx-auto w-full bg-slate-900/30 p-6 rounded-2xl border border-white/5">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          {/* XY Pad for Verbosity (X) vs Tone Warmth (Y) */}
          <div className="space-y-3">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-xs font-bold uppercase tracking-wider text-slate-300">
                  Expressiveness Map
                </p>
                <p className="text-[11px] text-slate-500">
                  Drag the orb to set Verbosity (X) and Tone Warmth (Y) together.
                </p>
              </div>
              <div className="text-[11px] font-mono text-purple-300 bg-purple-500/10 px-2 py-0.5 rounded">
                V:{profile.extraversion}% / W:{profile.agreeableness}%
              </div>
            </div>

            <div
              ref={padRef}
              onPointerDown={handlePadPointer}
              className="relative h-60 w-full rounded-xl bg-slate-950/70 border border-white/10 overflow-hidden select-none touch-none"
            >
              {/* Grid lines */}
              <div className="absolute inset-0 pointer-events-none">
                <div className="absolute left-1/2 top-0 bottom-0 w-px bg-white/5" />
                <div className="absolute top-1/2 left-0 right-0 h-px bg-white/5" />
              </div>

              {/* Labels */}
              <div className="absolute left-3 top-3 text-[10px] uppercase text-slate-500">
                Warm
              </div>
              <div className="absolute left-3 bottom-3 text-[10px] uppercase text-slate-500">
                Clinical
              </div>
              <div className="absolute right-3 bottom-3 text-[10px] uppercase text-slate-500">
                Talkative
              </div>
              <div className="absolute right-3 top-3 text-[10px] uppercase text-slate-500">
                Concise
              </div>

              {/* Draggable orb */}
              <div
                className="absolute h-5 w-5 rounded-full bg-purple-400 shadow-[0_0_12px_rgba(168,85,247,0.5)] border border-white/40 pointer-events-none"
                style={{
                  left: `${profile.extraversion}%`,
                  top: `${100 - profile.agreeableness}%`,
                  transform: "translate(-50%, -50%)",
                }}
              />
            </div>
          </div>

          {/* Remaining sliders */}
          <div className="space-y-5">
            {sliderKeys.map((key) => {
              const meta = getMeta(key);
              if (!meta) return null;
              return (
                <div key={meta.key} className="space-y-2">
                  <div className="flex justify-between items-end">
                    <label className="text-xs font-bold uppercase tracking-wider text-slate-300">
                      {meta.label}
                    </label>
                    <span className="text-[11px] font-mono text-purple-400 bg-purple-500/10 px-2 py-0.5 rounded">
                      {profile[meta.key]}%
                    </span>
                  </div>

                  <div className="relative h-10 flex items-center">
                    <input
                      type="range"
                      min="0"
                      max="100"
                      value={profile[meta.key]}
                      onChange={(e) =>
                        onChange(meta.key, parseInt(e.target.value, 10))
                      }
                      className="w-full h-2 bg-slate-800 rounded-full appearance-none cursor-pointer accent-purple-500 z-10 relative"
                    />

                    <div className="absolute -bottom-1 left-0 text-[10px] uppercase text-slate-600 font-bold">
                      {meta.lowLabel}
                    </div>
                    <div className="absolute -bottom-1 right-0 text-[10px] uppercase text-slate-600 font-bold">
                      {meta.highLabel}
                    </div>

                    <div className="absolute left-1/2 top-1/2 -translate-x-1/2 -translate-y-1/2 w-0.5 h-3 bg-white/10" />
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      </div>
    </div>
  );
};

