import React, { useState } from "react";
import {
  AgentBuilderState,
  BUILDER_PHASE_ORDER,
  AgentProfile,
  IdentityConfig,
  SpecialTraitId,
  ThinkingStyleId,
  CommunicationStyleId,
} from "@/types/agentBuilder";
import { AGENT_ROLES } from "./AgentBuilderData";
import { User } from "@/components/ui/HearthlinkIcons";
import { compileSystemPrompt } from "./shared/promptCompiler";

import PhaseRole from "./phases/PhaseRole";
import PhaseThinking from "./phases/PhaseThinking";
import PhaseCommunication from "./phases/PhaseCommunication";
import PhaseTraits from "./phases/PhaseTraits";
import PhaseBehavior from "./phases/PhaseBehavior";
import PhaseIdentity from "./phases/PhaseIdentity";
import PhaseReview from "./phases/PhaseReview";
import { AldenTraits } from "@/types/agents";

interface AgentBuilderProps {
  initialState?: Partial<AgentBuilderState>;
  onComplete: (profile: AgentProfile) => void;
  onCancel: () => void;
}

const DEFAULT_STATE: AgentBuilderState = {
  currentPhase: "role",
  selectedRole: null,
  selectedThinking: null,
  selectedCommunication: null,
  selectedTraits: [],
  behavioralProfile: {
    openness: 50,
    conscientiousness: 50,
    extraversion: 50,
    agreeableness: 50,
    emotional_stability: 50,
  },
  identity: {
    name: "",
    avatar: "/assets/Mimic.png",
    relationshipStance: "collaborator",
    confidencePosture: "balanced",
  },
  isManualMode: false,
};

export const AgentBuilder: React.FC<AgentBuilderProps> = ({
  initialState,
  onComplete,
  onCancel,
}) => {
  const [state, setState] = useState<AgentBuilderState>({
    ...DEFAULT_STATE,
    ...initialState,
  });

  // Helper to update state
  const updateState = (updates: Partial<AgentBuilderState>) => {
    setState((prev) => ({ ...prev, ...updates }));
  };

  // Phase Navigation
  const handleNext = () => {
    const currentIndex = BUILDER_PHASE_ORDER.indexOf(state.currentPhase);
    if (currentIndex < BUILDER_PHASE_ORDER.length - 1) {
      updateState({ currentPhase: BUILDER_PHASE_ORDER[currentIndex + 1] });
    }
  };

  const handleBack = () => {
    const currentIndex = BUILDER_PHASE_ORDER.indexOf(state.currentPhase);
    if (currentIndex > 0) {
      updateState({ currentPhase: BUILDER_PHASE_ORDER[currentIndex - 1] });
    } else {
      onCancel();
    }
  };

  // Phase Selection Logic
  const selectRole = (roleId: string) => {
    const roleDef = AGENT_ROLES.find((r) => r.id === roleId);
    if (roleDef) {
      // Seed defaults
      updateState({
        selectedRole: roleDef.id,
        behavioralProfile: roleDef.defaultTraits,
      });
    }
  };

  const handleCommit = () => {
    // Compile final profile
    const compiledPrompt = compileSystemPrompt(state);

    const profile: AgentProfile = {
      id: crypto.randomUUID(),
      version: 1,
      isDeployed: true,
      roleId: state.selectedRole!,
      thinkingStyleId: state.selectedThinking!,
      communicationStyleId: state.selectedCommunication!,
      specialTraitIds: state.selectedTraits,
      behavior: state.behavioralProfile,
      identity: state.identity,
      systemPrompt: state.isManualMode
        ? state.manualSystemPrompt || ""
        : compiledPrompt,
      createdAt: new Date().toISOString(),
      updatedAt: new Date().toISOString(),
    };
    onComplete(profile);
  };

  // Render Phase Content
  const renderPhase = () => {
    switch (state.currentPhase) {
      case "role":
        return (
          <PhaseRole state={state} onSelect={(id: string) => selectRole(id)} />
        );
      case "thinking":
        return (
          <PhaseThinking
            selected={state.selectedThinking}
            onSelect={(id: ThinkingStyleId) =>
              updateState({ selectedThinking: id })
            }
          />
        );
      case "communication":
        return (
          <PhaseCommunication
            selected={state.selectedCommunication}
            onSelect={(id: CommunicationStyleId) =>
              updateState({ selectedCommunication: id })
            }
          />
        );
      case "traits":
        return (
          <PhaseTraits
            selected={state.selectedTraits}
            onToggle={(id: SpecialTraitId) => {
              const current = state.selectedTraits;
              const next = current.includes(id)
                ? current.filter((t: SpecialTraitId) => t !== id)
                : [...current, id];
              // Cap at 3
              if (next.length <= 3) {
                updateState({ selectedTraits: next });
              }
            }}
          />
        );
      case "behavior":
        return (
          <PhaseBehavior
            profile={state.behavioralProfile}
            onChange={(key: keyof AldenTraits, val: number) =>
              updateState({
                behavioralProfile: { ...state.behavioralProfile, [key]: val },
              })
            }
          />
        );
      case "identity":
        return (
          <PhaseIdentity
            identity={state.identity}
            onChange={(updates: Partial<IdentityConfig>) =>
              updateState({ identity: { ...state.identity, ...updates } })
            }
          />
        );
      case "review":
        return <PhaseReview state={state} onCommit={handleCommit} />;
      default:
        return <div>Unknown Phase</div>;
    }
  };

  const currentIndex = BUILDER_PHASE_ORDER.indexOf(state.currentPhase);
  const totalPhases = BUILDER_PHASE_ORDER.length;

  return (
    <div className="w-full h-full flex flex-col bg-slate-950 text-white overflow-hidden">
      {/* Header Protocol Bar */}
      <div className="h-16 flex items-center justify-between px-8 border-b border-white/5 bg-white/5 shrink-0">
        <div className="flex items-center gap-4">
          <div className="p-2 bg-purple-500/20 rounded-lg text-purple-400">
            <User size={20} />
          </div>
          <div>
            <h2 className="text-lg font-bold font-orbitron-force tracking-widest uppercase text-white">
              Create New Persona
            </h2>
            <p className="text-[10px] text-slate-500 uppercase tracking-widest font-mono">
              Step {currentIndex + 1} of {totalPhases}:{" "}
              {state.currentPhase.toUpperCase()}
            </p>
          </div>
        </div>

        {/* Progress Steps */}
        <div className="flex gap-1.5">
          {BUILDER_PHASE_ORDER.map((phase, idx) => (
            <div
              key={phase}
              className={`h-1.5 w-12 rounded-full transition-all duration-300 ${
                idx <= currentIndex
                  ? "bg-purple-500 shadow-[0_0_8px_rgba(168,85,247,0.5)]"
                  : "bg-white/10"
              }`}
            />
          ))}
        </div>
      </div>

      {/* Phase Content */}
      <div className="flex-1 min-h-0 overflow-y-auto p-8 relative custom-scrollbar">
        <div className="max-w-6xl mx-auto h-full flex flex-col">
          {renderPhase()}
        </div>
      </div>

      {/* Navigation Footer */}
      <div className="h-20 border-t border-white/5 bg-slate-900/50 flex items-center justify-between px-8 shrink-0">
        <button
          onClick={handleBack}
          className="px-6 py-2 rounded border border-white/10 text-slate-400 hover:text-white hover:border-white/20 transition-all font-bold uppercase text-xs tracking-wider"
        >
          {currentIndex === 0 ? "Cancel" : "Back"}
        </button>

        {state.currentPhase !== "review" && (
          <button
            onClick={handleNext}
            disabled={!canProceed(state)}
            className={`px-8 py-2 rounded clip-path-slant bg-purple-600 text-white font-bold uppercase text-xs tracking-wider transition-all
                   ${!canProceed(state) ? "opacity-50 cursor-not-allowed grayscale" : "hover:bg-purple-500 hover:shadow-[0_0_20px_rgba(168,85,247,0.4)]"}`}
          >
            Continue
          </button>
        )}
      </div>
    </div>
  );
};

// Validation Helper
function canProceed(state: AgentBuilderState): boolean {
  switch (state.currentPhase) {
    case "role":
      return !!state.selectedRole;
    case "thinking":
      return !!state.selectedThinking;
    case "communication":
      return !!state.selectedCommunication;
    case "identity":
      return (
        !!state.identity.relationshipStance &&
        !!state.identity.confidencePosture
      );
    default:
      return true;
  }
}

