import React, { useState, useEffect, useMemo } from "react";
import { SettingsConfig } from "@/bridges/SettingsBridge";
import { PremiumToggle } from "@/components/shared/premium/PremiumToggle";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import {
  voiceStartListening,
  voiceStop,
  voiceSynthesize,
  VoiceStartListeningRequest,
  VoiceStopRequest,
} from "@/bridges/VoiceBridge";
import {
  SettingSection,
  SettingRow,
  selectClass,
  SYSTEM_VOICES,
  MIMIC_VOICES,
  ELEVEN_VOICES,
  QWEN3_VOICES,
} from "./Common";

export const VoiceSettingsContent: React.FC<{
  settings: SettingsConfig["voice"];
  onUpdate: (data: Partial<SettingsConfig["voice"]>) => void;
}> = ({ settings, onUpdate }) => {
  const [inputDevices, setInputDevices] = useState<MediaDeviceInfo[]>([]);
  const [outputDevices, setOutputDevices] = useState<MediaDeviceInfo[]>([]);
  const [playingSample, setPlayingSample] = useState<string | null>(null);

  const handlePlaySample = async (voiceId: string) => {
    if (playingSample) return;
    setPlayingSample(voiceId);
    try {
      await voiceSynthesize({
        user_id: "default_user",
        text: "System voice test complete.",
        voice_id: voiceId,
      });
    } catch (err) {
      console.error("Failed to play sample:", err);
    } finally {
      setPlayingSample(null);
    }
  };

  // Derived voices list based on provider
  const availableVoices = useMemo(() => {
    switch (settings.ttsProvider) {
      case "mimic3":
        return MIMIC_VOICES;
      case "elevenlabs":
        return ELEVEN_VOICES;
      case "qwen3":
        return QWEN3_VOICES;
      default:
        return SYSTEM_VOICES;
    }
  }, [settings.ttsProvider]);

  useEffect(() => {
    const getDevices = async () => {
      try {
        const devices = await navigator.mediaDevices.enumerateDevices();
        setInputDevices(devices.filter((d) => d.kind === "audioinput"));
        setOutputDevices(devices.filter((d) => d.kind === "audiooutput"));
      } catch (err) {
        console.error("Failed to enumerate devices:", err);
      }
    };
    getDevices();
  }, []);

  const handleVoiceInputToggle = async (enabled: boolean) => {
    onUpdate({ enabled });

    if (enabled) {
      try {
        const request: VoiceStartListeningRequest = {
          user_id: "default_user",
          engine: "whisper",
          mode: "continuous",
        };
        await voiceStartListening(request);
        console.log("[Voice] Voice input enabled and listening started");
      } catch (error) {
        console.error("[Voice] Failed to start voice listening:", error);
        onUpdate({ enabled: false });
      }
    } else {
      try {
        const request: VoiceStopRequest = {
          user_id: "default_user",
        };
        await voiceStop(request);
        console.log("[Voice] Voice input disabled and listening stopped");
      } catch (error) {
        console.error("[Voice] Failed to stop voice listening:", error);
      }
    }
  };

  const handleVoiceOutputToggle = async (enabled: boolean) => {
    onUpdate({ voiceOutput: enabled });
    console.log(`[Voice] Voice output ${enabled ? "enabled" : "disabled"}`);
  };

  return (
    <div className="space-y-5">
      <SettingSection title="Voice Input">
        <SettingRow
          id="enable-voice-input-checkbox"
          label="Enable Voice Input (STT)"
          description="Use voice commands to interact with Hearthlink (Speech-to-Text)"
        >
          <PremiumToggle
            id="enable-voice-input-checkbox"
            checked={settings.enabled}
            onChange={(e: any) => handleVoiceInputToggle(e.target.checked)}
          />
        </SettingRow>

        <SettingRow
          id="input-device-select"
          label="Microphone"
          description="Select audio input device"
        >
          <select
            id="input-device-select"
            value={settings.inputDeviceId || "default"}
            onChange={(e: any) => onUpdate({ inputDeviceId: e.target.value })}
            className={selectClass}
            style={{ colorScheme: "dark" }}
          >
            <option value="default" className="bg-[#0b1124]">
              Default
            </option>
            {inputDevices.map((device) => (
              <option
                key={device.deviceId}
                value={device.deviceId}
                className="bg-[#0b1124]"
              >
                {device.label || `Microphone ${device.deviceId.slice(0, 5)}...`}
              </option>
            ))}
          </select>
        </SettingRow>

        <SettingRow
          id="stt-engine-select"
          label="STT Engine"
          description="Speech-to-Text recognition provider"
        >
          <select
            id="stt-engine-select"
            value={settings.sttEngine || "whisper"}
            onChange={(e: any) => onUpdate({ sttEngine: e.target.value })}
            className={selectClass}
            style={{ colorScheme: "dark" }}
          >
            <option value="whisper" className="bg-[#0b1124]">
              Whisper (Local)
            </option>
            <option value="system" className="bg-[#0b1124]">
              System Native
            </option>
          </select>
        </SettingRow>

        <SettingRow
          id="wake-word-mode-select"
          label="Wake Word Mode"
          description="How to activate voice input"
        >
          <div className="flex flex-col gap-3 w-full">
            <select
              id="wake-word-mode-select"
              value={
                settings.wakeWord === "Hey Hearthlink"
                  ? "strict"
                  : settings.wakeWord === "Agent Name"
                    ? "adaptive"
                    : "custom"
              }
              onChange={(e: any) => {
                const val = e.target.value;
                if (val === "strict") onUpdate({ wakeWord: "Hey Hearthlink" });
                else if (val === "adaptive")
                  onUpdate({ wakeWord: "Agent Name" });
              }}
              className={selectClass}
              style={{ colorScheme: "dark" }}
            >
              <option value="strict" className="bg-[#0b1124]">
                Strict ("Hey Hearthlink")
              </option>
              <option value="adaptive" className="bg-[#0b1124]">
                Persona Names (Global)
              </option>
              <option value="custom" className="bg-[#0b1124]">
                Custom Phrase
              </option>
            </select>

            {settings.wakeWord !== "Hey Hearthlink" &&
              settings.wakeWord !== "Agent Name" && (
                <Input
                  id="wake-word-input"
                  type="text"
                  placeholder="Enter custom wake phrase..."
                  value={settings.wakeWord}
                  onChange={(e: any) => onUpdate({ wakeWord: e.target.value })}
                  className="w-full"
                />
              )}
          </div>
        </SettingRow>
      </SettingSection>

      <SettingSection title="Voice Output">
        <SettingRow
          id="enable-voice-responses-checkbox"
          label="Enable Voice Responses (TTS)"
          description="Personas can respond with synthesized speech (Text-to-Speech)"
        >
          <PremiumToggle
            id="enable-voice-responses-checkbox"
            checked={settings.voiceOutput}
            onChange={(e: any) => handleVoiceOutputToggle(e.target.checked)}
          />
        </SettingRow>

        <SettingRow
          id="output-device-select"
          label="Speaker"
          description="Select audio output device"
        >
          <select
            id="output-device-select"
            value={settings.outputDeviceId || "default"}
            onChange={(e: any) => onUpdate({ outputDeviceId: e.target.value })}
            className={selectClass}
            style={{ colorScheme: "dark" }}
          >
            <option value="default" className="bg-[#0b1124]">
              Default
            </option>
            {outputDevices.map((device) => (
              <option
                key={device.deviceId}
                value={device.deviceId}
                className="bg-[#0b1124]"
              >
                {device.label || `Speaker ${device.deviceId.slice(0, 5)}...`}
              </option>
            ))}
          </select>
        </SettingRow>

        <SettingRow
          id="tts-provider-select"
          label="TTS Provider"
          description="Select text-to-speech engine (Qwen3 recommended for local)"
        >
          <select
            id="tts-provider-select"
            value={settings.ttsProvider || "system"}
            onChange={(e: any) => onUpdate({ ttsProvider: e.target.value })}
            className={selectClass}
            style={{ colorScheme: "dark" }}
          >
            <option value="qwen3" className="bg-[#0b1124]">
              Qwen3 (Local, Recommended)
            </option>
            <option value="mimic3" className="bg-[#0b1124]">
              Mimic3 (Local)
            </option>
            <option value="system" className="bg-[#0b1124]">
              System Native
            </option>
            <option value="elevenlabs" className="bg-[#0b1124]">
              ElevenLabs (Cloud)
            </option>
          </select>
        </SettingRow>

        <SettingRow
          id="voice-selection"
          label="TTS Voice"
          description="Select the voice model for system responses"
        >
          <div className="flex gap-2 w-full">
            <select
              id="voice-selection"
              value={settings.voiceId || "alden-default"}
              onChange={(e: any) => onUpdate({ voiceId: e.target.value })}
              className={selectClass}
              style={{ colorScheme: "dark" }}
            >
              {availableVoices.map((voice) => (
                <option
                  key={voice.id}
                  value={voice.id}
                  className="bg-[#0b1124]"
                >
                  {voice.name}
                </option>
              ))}
            </select>
            <Button
              variant="ghost"
              size="sm"
              onClick={() =>
                handlePlaySample(settings.voiceId || "alden-default")
              }
              disabled={!!playingSample}
              title="Play Sample"
              className="px-2"
            >
              {playingSample === (settings.voiceId || "alden-default") ? (
                <div className="w-4 h-4 rounded-full border-2 border-t-transparent border-[#00e5ff] animate-spin" />
              ) : (
                "Ã¢â€“Â¶"
              )}
            </Button>
          </div>
        </SettingRow>

        <SettingRow
          id="speech-rate-input"
          label="Speech Rate"
          description="How fast personas speak"
        >
          <div className="flex items-center gap-4">
            <input
              id="speech-rate-input"
              type="range"
              min="0.5"
              max="2.0"
              step="0.1"
              value={settings.rate}
              onChange={(e: any) => onUpdate({ rate: parseFloat(e.target.value) })}
              className="h-2 w-[200px] cursor-pointer appearance-none rounded-full bg-[#1e293b] accent-[#00e5ff]"
            />
            <span className="text-sm font-medium text-[#8fa7c7] w-12">
              {settings.rate}x
            </span>
          </div>
        </SettingRow>
      </SettingSection>
    </div>
  );
};

