import React from "react";

interface SettingSectionProps {
  title: string;
  children: React.ReactNode;
}

export const SettingSection: React.FC<SettingSectionProps> = ({
  title,
  children,
}) => (
  <div className="space-y-4">
    <h3 className="text-sm font-semibold tracking-tight text-[#d6b369] border-b border-[rgba(0,205,255,0.2)] pb-2 flex items-center gap-2 font-orbitron-force">
      <div className="w-1.5 h-1.5 bg-[#00e5ff] rotate-45" />
      {title}
    </h3>
    <div className="space-y-3">{children}</div>
  </div>
);

interface SettingRowProps {
  id: string;
  label: string;
  description: string;
  children: React.ReactNode;
}

export const SettingRow: React.FC<SettingRowProps> = ({
  id,
  label,
  description,
  children,
}) => (
  <div className="flex flex-col sm:flex-row sm:items-center justify-between gap-3 p-3 rounded-lg hover:bg-[#00e5ff]/5 transition-colors border border-transparent hover:border-[rgba(0,205,255,0.15)] min-h-[60px]">
    <div className="flex-1 space-y-0.5 min-w-0 pr-4">
      <label
        htmlFor={id}
        className="block text-sm font-medium text-[#c5d2e8] truncate"
      >
        {label}
      </label>
      <p className="text-[11px] leading-relaxed text-[#8fa7c7] max-w-xl">
        {description}
      </p>
    </div>
    <div className="flex items-center min-w-[180px] justify-end flex-shrink-0">
      {children}
    </div>
  </div>
);

export const selectClass =
  "flex h-10 w-full items-center justify-between rounded-lg border border-[rgba(0,205,255,0.3)] bg-[#0b1124] px-3 py-2 text-sm text-[#c5d2e8] focus:outline-none focus:ring-2 focus:ring-[#00e5ff]/40 min-w-[200px]";

export const SYSTEM_VOICES = [
  { id: "default", name: "System Default", tier: "Standard" },
  { id: "win-david", name: "Microsoft David", tier: "Standard" },
  { id: "win-zira", name: "Microsoft Zira", tier: "Standard" },
];

export const MIMIC_VOICES = [
  { id: "alden-default", name: "Alden (Mimic3)", tier: "Local" },
