];

export const QWEN3_VOICES = [
  { id: "qwen3-neutral", name: "Qwen3 Neutral", tier: "Local" },
  { id: "qwen3-warm", name: "Qwen3 Warm", tier: "Local" },
  { id: "qwen3-professional", name: "Qwen3 Professional", tier: "Local" },
];

export const ALL_VOICES = [
  ...SYSTEM_VOICES,
  ...MIMIC_VOICES,
  ...ELEVEN_VOICES,
  ...QWEN3_VOICES,
];

